interface BulkTemplateFormProps {
  onClose: () => void;
  onSave: (templates: Omit<DutyTemplate, 'id' | 'scheduleId' | 'createdAt' | 'updatedAt'>[]) => void;
  existingTemplates?: DutyTemplate[];
}

const BulkTemplateForm: React.FC<BulkTemplateFormProps> = ({
  onClose,
  onSave,
  existingTemplates = []
}) => {
  const [formData, setFormData] = useState({
    startTime: '06:00',
    endTime: '18:00',
    interval: 15, // minutes
    duration: 30, // minutes per template
    breakDuration: 0 // minutes between templates
  });
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [preview, setPreview] = useState<Array<{startTime: string, endTime: string}>>([]);

  const generatePreview = () => {
    const templates: Array<{startTime: string, endTime: string}> = [];

    try {
      const startHour = parseInt(formData.startTime.split(':')[0]);
      const startMinute = parseInt(formData.startTime.split(':')[1]);
      const endHour = parseInt(formData.endTime.split(':')[0]);
      const endMinute = parseInt(formData.endTime.split(':')[1]);

      const startMinutes = startHour * 60 + startMinute;
      const endMinutes = endHour * 60 + endMinute;

      let currentMinutes = startMinutes;

      while (currentMinutes + formData.duration <= endMinutes) {
        const startTime = new Date(2000, 0, 1, Math.floor(currentMinutes / 60), currentMinutes % 60);
        const endTime = new Date(2000, 0, 1, Math.floor((currentMinutes + formData.duration) / 60), (currentMinutes + formData.duration) % 60);

        templates.push({
          startTime: startTime.toISOString(),
          endTime: endTime.toISOString()
        });

        currentMinutes += formData.interval + formData.breakDuration;
      }

      setPreview(templates);
    } catch (error) {
      console.error('Error generating preview:', error);
      setPreview([]);
    }
  };

  React.useEffect(() => {
    generatePreview();
  }, [formData]);

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!formData.startTime) {
      newErrors.startTime = 'Start time is required';
    }

    if (!formData.endTime) {
      newErrors.endTime = 'End time is required';
    }

    if (formData.startTime && formData.endTime) {
      const startMinutes = parseInt(formData.startTime.split(':')[0]) * 60 + parseInt(formData.startTime.split(':')[1]);
      const endMinutes = parseInt(formData.endTime.split(':')[0]) * 60 + parseInt(formData.endTime.split(':')[1]);

      if (startMinutes >= endMinutes) {
        newErrors.endTime = 'End time must be after start time';
      }
    }

    if (formData.interval < 5) {
      newErrors.interval = 'Interval must be at least 5 minutes';
    }

    if (formData.duration < 5) {
      newErrors.duration = 'Duration must be at least 5 minutes';
    }

    if (formData.duration > formData.interval) {
      newErrors.duration = 'Duration cannot be longer than interval';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    if (preview.length === 0) {
      setErrors({ general: 'No templates would be generated with these settings' });
      return;
    }

    onSave(preview.map(template => ({
      startTime: template.startTime,
      endTime: template.endTime,
      dutyType: 'TRIP' as const
    })));
    onClose();
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const formatTime = (timeString: string) => {
    try {
      const date = new Date(timeString);
      return date.toLocaleTimeString('en-US', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: false 
      });
    } catch {
      return timeString;
    }
  };

  return (
    <ModalOverlay onClick={onClose}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>Bulk Create Templates</ModalTitle>
          <CloseButton onClick={onClose}>
            <FiSettings size={20} />
          </CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <Grid>
            <FormGroup>
              <Label htmlFor="startTime">Start Time *</Label>
              <Input
                type="time"
                id="startTime"
                name="startTime"
                value={formData.startTime}
                onChange={handleInputChange}
                hasError={!!errors.startTime}
              />
              {errors.startTime && <ErrorMessage>{errors.startTime}</ErrorMessage>}
            </FormGroup>

            <FormGroup>
              <Label htmlFor="endTime">End Time *</Label>
              <Input
                type="time"
                id="endTime"
                name="endTime"
                value={formData.endTime}
                onChange={handleInputChange}
                hasError={!!errors.endTime}
              />
              {errors.endTime && <ErrorMessage>{errors.endTime}</ErrorMessage>}
            </FormGroup>
          </Grid>

          <Grid>
            <FormGroup>
              <Label htmlFor="interval">Interval (minutes) *</Label>
              <Input
                type="number"
                id="interval"
                name="interval"
                value={formData.interval}
                onChange={handleInputChange}
                min="5"
                max="1440"
                hasError={!!errors.interval}
              />
              {errors.interval && <ErrorMessage>{errors.interval}</ErrorMessage>}
            </FormGroup>

            <FormGroup>
              <Label htmlFor="duration">Duration per Template (minutes) *</Label>
              <Input
                type="number"
                id="duration"
                name="duration"
                value={formData.duration}
                onChange={handleInputChange}
                min="5"
                max="480"
                hasError={!!errors.duration}
              />
              {errors.duration && <ErrorMessage>{errors.duration}</ErrorMessage>}
            </FormGroup>
          </Grid>

          <FormGroup>
            <Label htmlFor="breakDuration">Break Between Templates (minutes)</Label>
            <Input
              type="number"
              id="breakDuration"
              name="breakDuration"
              value={formData.breakDuration}
              onChange={handleInputChange}
              min="0"
              max="60"
            />
          </FormGroup>

          {preview.length > 0 && (
            <PreviewSection>
              <PreviewTitle>Preview ({preview.length} templates will be created)</PreviewTitle>
              <PreviewList>
                {preview.slice(0, 10).map((template, index) => (
                  <PreviewItem key={index} dutyType={template.dutyType}>
                    <PreviewName>{template.name}</PreviewName>
                    <PreviewTime>
                      {formatTime(template.startTime)} - {formatTime(template.endTime)}
                    </PreviewTime>
                  </PreviewItem>
                ))}
                {preview.length > 10 && (
                  <InfoText>... and {preview.length - 10} more templates</InfoText>
                )}
              </PreviewList>
              <InfoText>
                Templates will be created every {formData.interval} minutes from {formData.startTime} to {formData.endTime}
              </InfoText>
            </PreviewSection>
          )}

          {errors.general && (
            <ErrorMessage>{errors.general}</ErrorMessage>
          )}

          <ButtonGroup>
            <Button type="button" onClick={onClose}>
              Cancel
            </Button>
            <Button type="submit" variant="primary" disabled={preview.length === 0}>
              <FiClock size={16} />
              Create {preview.length} Templates
            </Button>
          </ButtonGroup>
        </Form>
      </ModalContent>
    </ModalOverlay>
  );
};

export default BulkTemplateForm;
