// OpenRouteService configuration
// You can get a free API key from https://openrouteservice.org/dev/#/signup
const ORS_API_KEY = process.env.REACT_APP_ORS_API_KEY || '5b3ce3597851110001cf6248a1c7d8b8b4c94c5fb8b0b4e5e5b5b5b5'; // Demo key (limited usage)
const ORS_BASE_URL = 'https://api.openrouteservice.org/v2/directions';

export interface RouteCoordinate {
  lat: number;
  lng: number;
}

export interface RouteResponse {
  coordinates: RouteCoordinate[];
  distance: number; // in meters
  duration: number; // in seconds
}

export class RoutingService {
  /**
   * Calculate route between multiple waypoints using OpenRouteService
   * @param waypoints Array of coordinates in order
   * @param signal Optional AbortSignal for request cancellation
   * @returns Promise with route coordinates and metadata
   */
  static async calculateRoute(waypoints: RouteCoordinate[], signal?: AbortSignal): Promise<RouteResponse | null> {
    if (waypoints.length < 2) {
      return null;
    }

    // Create cache key from waypoints
    const cacheKey = waypoints.map(w => `${w.lat.toFixed(6)},${w.lng.toFixed(6)}`).join('|');

    // Check cache first
    const cached = routeCache.get(cacheKey);
    if (cached) {
      console.log('Using cached route');
      return cached;
    }

    try {
      // Convert coordinates to ORS format [lng, lat]
      const coordinates = waypoints.map(point => [point.lng, point.lat]);

      const requestBody = {
        coordinates: coordinates,
        format: 'json',
        geometry_format: 'geojson'
      };

      const response = await fetch(`${ORS_BASE_URL}/driving-car/json`, {
        method: 'POST',
        headers: {
          'Accept': 'application/json, application/geo+json, application/gpx+xml, img/png; charset=utf-8',
          'Authorization': ORS_API_KEY,
          'Content-Type': 'application/json; charset=utf-8'
        },
        body: JSON.stringify(requestBody),
        signal // Add AbortSignal to the fetch request
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.routes && data.routes.length > 0) {
        const route = data.routes[0];

        // Extract coordinates from the route geometry
        const routeCoordinates: RouteCoordinate[] = route.geometry.coordinates.map(
          (coord: [number, number]) => ({
            lng: coord[0],
            lat: coord[1]
          })
        );

        const result = {
          coordinates: routeCoordinates,
          distance: route.summary.distance,
          duration: route.summary.duration
        };

        // Cache the result
        routeCache.set(cacheKey, result);

        // Clean up old cache entries after some time
        setTimeout(() => {
          routeCache.delete(cacheKey);
        }, CACHE_DURATION);

        return result;
      }

      return null;
    } catch (error) {
      console.error('Error calculating route:', error);

      // Fallback to straight line if routing fails
      return {
        coordinates: waypoints,
        distance: 0,
        duration: 0
      };
    }
  }

  /**
   * Calculate distance between two points (Haversine formula)
   * Used as fallback when routing service is unavailable
   */
  static calculateStraightLineDistance(point1: RouteCoordinate, point2: RouteCoordinate): number {
    const R = 6371e3; // Earth's radius in meters
    const φ1 = point1.lat * Math.PI / 180;
    const φ2 = point2.lat * Math.PI / 180;
    const Δφ = (point2.lat - point1.lat) * Math.PI / 180;
    const Δλ = (point2.lng - point1.lng) * Math.PI / 180;

    const a = Math.sin(Δφ/2) * Math.sin(Δφ/2) +
              Math.cos(φ1) * Math.cos(φ2) *
              Math.sin(Δλ/2) * Math.sin(Δλ/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));

    return R * c; // Distance in meters
  }

  /**
   * Format distance for display
   */
  static formatDistance(meters: number): string {
    if (meters < 1000) {
      return `${Math.round(meters)} m`;
    }
    return `${(meters / 1000).toFixed(1)} km`;
  }

  /**
   * Format duration for display
   */
  static formatDuration(seconds: number): string {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  }
}
