import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiArrowLeft, FiArrowRight, FiCheck, FiCalendar, FiMap, FiClock, FiSettings, FiTrash2 } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  PageContainer,
  PageHeader,
  PageTitle,
  HeaderActions,
  FormCard,
  FormGroup,
  Label,
  Input,
  ErrorMessage
} from '../../components/Common/CommonStyles';
import styled from 'styled-components';
import { theme } from '../../styles/theme';
import { apiService } from '../../services/apiService';
import { CreateBulkTripDutiesRequest } from '../../types/api';
import MultiSelectSearchDropdown from '../../components/UI/DropDowns/MultiSelectSearchDropdown';
import { dataService } from '../../services/dataService';
import { ServiceSchedule, Route } from '../../types';

const StepContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
`;

const StepIndicator = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: ${theme.spacing.xl};
`;

const Step = styled.div<{ active: boolean; completed: boolean }>`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.lg};
  background: ${({ active, completed }) => 
    completed ? theme.colors.success : 
    active ? theme.colors.primary : 
    theme.colors.card};
  color: ${({ active, completed }) => 
    completed || active ? 'white' : 
    theme.colors.textSecondary};
  font-weight: ${theme.typography.fontWeight.medium};
  transition: all 0.2s;
  border: 1px solid ${({ active, completed }) => 
    completed ? theme.colors.success : 
    active ? theme.colors.primary : 
    theme.colors.border};
`;

const StepNumber = styled.div<{ active: boolean; completed: boolean }>`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.bold};
  background: ${({ active, completed }) => 
    completed ? 'white' : 
    active ? 'white' : 
    theme.colors.surface};
  color: ${({ active, completed }) => 
    completed ? theme.colors.success : 
    active ? theme.colors.primary : 
    theme.colors.textSecondary};
  border: 1px solid ${({ active, completed }) => 
    completed ? theme.colors.success : 
    active ? theme.colors.primary : 
    theme.colors.border};
`;

const StepContent = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${theme.spacing.lg};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const PreviewCard = styled.div`
  background: ${theme.colors.card};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.lg};
  margin-top: ${theme.spacing.lg};
`;

const TripItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: ${theme.spacing.sm} 0;
  border-bottom: 1px solid ${theme.colors.border};

  &:last-child {
    border-bottom: none;
  }
`;

const TripInfo = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
`;

const TripTime = styled.div`
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
`;

const TripNumber = styled.div`
  font-size: ${theme.typography.fontSize.sm};
  color: ${theme.colors.textSecondary};
`;

const DeleteButton = styled.button`
  background: none;
  border: none;
  color: ${theme.colors.error};
  cursor: pointer;
  padding: ${theme.spacing.xs};
  border-radius: ${theme.borderRadius.sm};
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;

  &:hover {
    background: ${theme.colors.error}20;
    color: ${theme.colors.error};
  }
`;

interface DutyFormData {
  scheduleId: string;
  routeId: string;
  date: string;
  startTime: string;
  endTime: string;
  frequency: number;
}

interface GeneratedTrip {
  tripNumber: number;
  scheduledStartTime: string;
  scheduledEndTime: string;
}

const CreateDuty: React.FC = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState<DutyFormData>({
    scheduleId: '',
    routeId: '',
    date: '',
    startTime: '',
    endTime: '',
    frequency: 30
  });
  const [errors, setErrors] = useState<Partial<DutyFormData>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [generatedTrips, setGeneratedTrips] = useState<GeneratedTrip[]>([]);

  // State for API data
  const [schedules, setSchedules] = useState<ServiceSchedule[]>([]);
  const [routes, setRoutes] = useState<Route[]>([]);
  const [schedulesLoading, setSchedulesLoading] = useState(false);
  const [routesLoading, setRoutesLoading] = useState(false);

  // Selected items for single-select dropdowns
  const [selectedSchedule, setSelectedSchedule] = useState<ServiceSchedule | null>(null);
  const [selectedRoute, setSelectedRoute] = useState<Route | null>(null);

  const steps = [
    { number: 1, title: 'Select Schedule', icon: FiCalendar },
    { number: 2, title: 'Select Route', icon: FiMap },
    { number: 3, title: 'Configure Time', icon: FiClock },
    { number: 4, title: 'Review & Generate', icon: FiCheck }
  ];

  // Load schedules and routes on component mount
  useEffect(() => {
    const loadSchedules = async () => {
      try {
        setSchedulesLoading(true);
        const schedulesData = await dataService.getServiceSchedules();
        setSchedules(schedulesData);
      } catch (error) {
        console.error('Error loading schedules:', error);
      } finally {
        setSchedulesLoading(false);
      }
    };

    const loadRoutes = async () => {
      try {
        setRoutesLoading(true);
        const routesData = await dataService.getRoutes();
        setRoutes(routesData);
      } catch (error) {
        console.error('Error loading routes:', error);
      } finally {
        setRoutesLoading(false);
      }
    };

    loadSchedules();
    loadRoutes();
  }, []);

  // Search functions for dropdowns
  const searchSchedules = async (searchTerm: string): Promise<ServiceSchedule[]> => {
    try {
      if (!searchTerm.trim()) {
        return schedules;
      }
      return schedules.filter(schedule => 
        schedule.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    } catch (error) {
      console.error('Error searching schedules:', error);
      return [];
    }
  };

  const searchRoutes = async (searchTerm: string): Promise<Route[]> => {
    try {
      if (!searchTerm.trim()) {
        return routes;
      }
      return routes.filter(route => 
        route.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        route.code.toLowerCase().includes(searchTerm.toLowerCase())
      );
    } catch (error) {
      console.error('Error searching routes:', error);
      return [];
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear error when user starts typing
    if (errors[name as keyof DutyFormData]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }
  };

  // Handle schedule selection
  const handleScheduleSelect = (schedule: ServiceSchedule) => {
    setSelectedSchedule(schedule);
    setFormData(prev => ({ ...prev, scheduleId: schedule.id }));

    // Clear error when user selects
    if (errors.scheduleId) {
      setErrors(prev => ({ ...prev, scheduleId: undefined }));
    }
  };

  // Handle route selection
  const handleRouteSelect = (route: Route) => {
    setSelectedRoute(route);
    setFormData(prev => ({ ...prev, routeId: route.id }));

    // Clear error when user selects
    if (errors.routeId) {
      setErrors(prev => ({ ...prev, routeId: undefined }));
    }
  };

  // Handle trip deletion
  const handleDeleteTrip = (tripNumber: number) => {
    setGeneratedTrips(prev => prev.filter(trip => trip.tripNumber !== tripNumber));
  };

  const validateStep = (step: number): boolean => {
    const newErrors: Partial<DutyFormData> = {};

    if (step === 1) {
      if (!formData.scheduleId) {
        newErrors.scheduleId = 'Please select a schedule';
      }
    }

    if (step === 2) {
      if (!formData.routeId) {
        newErrors.routeId = 'Please select a route';
      }
    }

    if (step === 3) {
      if (!formData.date) {
        newErrors.date = 'Please select a date';
      }
      if (!formData.startTime) {
        newErrors.startTime = 'Please select start time';
      }
      if (!formData.endTime) {
        newErrors.endTime = 'Please select end time';
      }
      if (formData.startTime && formData.endTime && formData.startTime >= formData.endTime) {
        newErrors.endTime = 'End time must be after start time';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const generateTrips = () => {
    if (!formData.startTime || !formData.endTime || !formData.frequency) return;

    const startTime = new Date(`2000-01-01T${formData.startTime}`);
    const endTime = new Date(`2000-01-01T${formData.endTime}`);
    const frequencyMs = formData.frequency * 60 * 1000;

    const trips: GeneratedTrip[] = [];
    let currentTime = new Date(startTime);
    let tripNumber = 1;

    while (currentTime < endTime) {
      const tripEndTime = new Date(currentTime.getTime() + (60 * 60 * 1000)); // Assume 1 hour duration

      trips.push({
        tripNumber,
        scheduledStartTime: currentTime.toTimeString().slice(0, 5),
        scheduledEndTime: tripEndTime.toTimeString().slice(0, 5)
      });

      currentTime = new Date(currentTime.getTime() + frequencyMs);
      tripNumber++;
    }

    setGeneratedTrips(trips);
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      if (currentStep === 3) {
        generateTrips();
      }
      setCurrentStep(prev => prev + 1);
    }
  };

  const handlePrevious = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);

    try {
      // Prepare the bulk trip duties request
      const bulkRequest: CreateBulkTripDutiesRequest = {
        scheduleId: formData.scheduleId,
        routeId: formData.routeId,
        date: formData.date,
        trips: generatedTrips.map(trip => ({
          scheduledStartTime: trip.scheduledStartTime,
          scheduledEndTime: trip.scheduledEndTime
        }))
      };

      console.log('Creating bulk trip duties:', bulkRequest);

      // Call the API
      const result = await apiService.createBulkTripDuties(bulkRequest);

      console.log('Successfully created trip duties:', result);

      // Navigate back to duties list
      navigate('/duties');
    } catch (error) {
      console.error('Error creating trip duties:', error);
      alert('Failed to create trip duties. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    navigate('/duties');
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <StepContent>
            <h3 style={{ color: theme.colors.textPrimary, margin: 0 }}>Select Service Schedule</h3>
            <FormGroup>
              <Label htmlFor="scheduleId">Service Schedule *</Label>
              <MultiSelectSearchDropdown
                values={selectedSchedule ? [selectedSchedule] : []}
                onChange={(schedules) => {
                  if (schedules.length > 0) {
                    handleScheduleSelect(schedules[0]);
                  } else {
                    setSelectedSchedule(null);
                    setFormData(prev => ({ ...prev, scheduleId: '' }));
                  }
                }}
                onSearch={searchSchedules}
                displayValue={(schedule) => schedule.name}
                renderItem={(schedule) => (
                  <div style={{ padding: '4px 0' }}>
                    <div style={{ fontWeight: '500', fontSize: '14px' }}>
                      {schedule.name}
                    </div>
                  </div>
                )}
                placeholder="Search schedules..."
                disabled={schedulesLoading}
              />
              {errors.scheduleId && <ErrorMessage>{errors.scheduleId}</ErrorMessage>}
            </FormGroup>
          </StepContent>
        );

      case 2:
        return (
          <StepContent>
            <h3 style={{ color: theme.colors.textPrimary, margin: 0 }}>Select Route</h3>
            <FormGroup>
              <Label htmlFor="routeId">Route *</Label>
              <MultiSelectSearchDropdown
                values={selectedRoute ? [selectedRoute] : []}
                onChange={(routes) => {
                  if (routes.length > 0) {
                    handleRouteSelect(routes[0]);
                  } else {
                    setSelectedRoute(null);
                    setFormData(prev => ({ ...prev, routeId: '' }));
                  }
                }}
                onSearch={searchRoutes}
                displayValue={(route) => route.name}
                renderItem={(route) => (
                  <div style={{ padding: '4px 0' }}>
                    <div style={{ fontWeight: '500', fontSize: '14px' }}>
                      {route.name}
                    </div>
                    <div style={{ fontSize: '12px', color: '#6b7280', marginTop: '2px' }}>
                      {route.code}
                    </div>
                  </div>
                )}
                placeholder="Search routes..."
                disabled={routesLoading}
              />
              {errors.routeId && <ErrorMessage>{errors.routeId}</ErrorMessage>}
            </FormGroup>
          </StepContent>
        );

      case 3:
        return (
          <StepContent>
            <h3 style={{ color: theme.colors.textPrimary, margin: 0 }}>Configure Date & Time</h3>
            <FormRow>
              <FormGroup>
                <Label htmlFor="date">Date *</Label>
                <Input
                  type="date"
                  id="date"
                  name="date"
                  value={formData.date}
                  onChange={handleInputChange}
                  hasError={!!errors.date}
                />
                {errors.date && <ErrorMessage>{errors.date}</ErrorMessage>}
              </FormGroup>
              <FormGroup>
                <Label htmlFor="frequency">Frequency (minutes) *</Label>
                <Input
                  type="number"
                  id="frequency"
                  name="frequency"
                  value={formData.frequency}
                  onChange={handleInputChange}
                  min="5"
                  max="480"
                  step="5"
                  hasError={!!errors.frequency}
                  placeholder="Enter frequency in minutes"
                />
                {errors.frequency && <ErrorMessage>{errors.frequency}</ErrorMessage>}
              </FormGroup>
            </FormRow>
            <FormRow>
              <FormGroup>
                <Label htmlFor="startTime">Start Time *</Label>
                <Input
                  type="time"
                  id="startTime"
                  name="startTime"
                  value={formData.startTime}
                  onChange={handleInputChange}
                  hasError={!!errors.startTime}
                />
                {errors.startTime && <ErrorMessage>{errors.startTime}</ErrorMessage>}
              </FormGroup>
              <FormGroup>
                <Label htmlFor="endTime">End Time *</Label>
                <Input
                  type="time"
                  id="endTime"
                  name="endTime"
                  value={formData.endTime}
                  onChange={handleInputChange}
                  hasError={!!errors.endTime}
                />
                {errors.endTime && <ErrorMessage>{errors.endTime}</ErrorMessage>}
              </FormGroup>
            </FormRow>
          </StepContent>
        );

      case 4:
        return (
          <StepContent>
            <h3 style={{ color: theme.colors.textPrimary, margin: 0 }}>Review Generated Trips</h3>
            <PreviewCard>
              <div style={{ marginBottom: theme.spacing.lg }}>
                <h4 style={{ color: theme.colors.textPrimary, margin: '0 0 16px 0' }}>Configuration Summary</h4>
                <p style={{ color: theme.colors.textSecondary, margin: '8px 0' }}><strong style={{ color: theme.colors.textPrimary }}>Schedule:</strong> {selectedSchedule?.name || 'Not selected'}</p>
                <p style={{ color: theme.colors.textSecondary, margin: '8px 0' }}><strong style={{ color: theme.colors.textPrimary }}>Route:</strong> {selectedRoute?.name || 'Not selected'}</p>
                <p style={{ color: theme.colors.textSecondary, margin: '8px 0' }}><strong style={{ color: theme.colors.textPrimary }}>Date:</strong> {new Date(formData.date).toLocaleDateString()}</p>
                <p style={{ color: theme.colors.textSecondary, margin: '8px 0' }}><strong style={{ color: theme.colors.textPrimary }}>Time Range:</strong> {formData.startTime} - {formData.endTime}</p>
                <p style={{ color: theme.colors.textSecondary, margin: '8px 0' }}><strong style={{ color: theme.colors.textPrimary }}>Frequency:</strong> Every {formData.frequency} minutes</p>
                <p style={{ color: theme.colors.textSecondary, margin: '8px 0' }}><strong style={{ color: theme.colors.textPrimary }}>Total Trips:</strong> {generatedTrips.length}</p>
              </div>

              <h4 style={{ color: theme.colors.textPrimary, margin: '0 0 16px 0' }}>Generated Trips</h4>
              {generatedTrips.length === 0 ? (
                <p style={{ color: theme.colors.textSecondary, textAlign: 'center', padding: theme.spacing.lg }}>
                  No trips generated. Please check your configuration.
                </p>
              ) : (
                generatedTrips.map(trip => (
                  <TripItem key={trip.tripNumber}>
                    <TripInfo>
                      <TripTime>{trip.scheduledStartTime} - {trip.scheduledEndTime}</TripTime>
                      <TripNumber>Trip #{trip.tripNumber}</TripNumber>
                    </TripInfo>
                    <DeleteButton onClick={() => handleDeleteTrip(trip.tripNumber)} title="Delete trip">
                      <FiTrash2 size={16} />
                    </DeleteButton>
                  </TripItem>
                ))
              )}
            </PreviewCard>
          </StepContent>
        );

      default:
        return null;
    }
  };

  return (
    <PageContainer className="fade-in">
      <PageHeader>
        <PageTitle>Create Duty Schedule</PageTitle>
        <HeaderActions>
          <Button variant="secondary" onClick={handleCancel}>
            <FiArrowLeft style={{ marginRight: '8px' }} />
            Cancel
          </Button>
        </HeaderActions>
      </PageHeader>

      <StepContainer>
        <StepIndicator>
          {steps.map((step, index) => {
            const IconComponent = step.icon;
            const isActive = currentStep === step.number;
            const isCompleted = currentStep > step.number;

            return (
              <Step key={step.number} active={isActive} completed={isCompleted}>
                <StepNumber active={isActive} completed={isCompleted}>
                  {isCompleted ? <FiCheck size={14} /> : step.number}
                </StepNumber>
                <IconComponent size={16} />
                {step.title}
              </Step>
            );
          })}
        </StepIndicator>

        <FormCard>
          {renderStepContent()}

          <div style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            marginTop: theme.spacing.xl,
            paddingTop: theme.spacing.lg,
            borderTop: `1px solid ${theme.colors.border}`
          }}>
            <Button 
              variant="secondary" 
              onClick={handlePrevious}
              disabled={currentStep === 1}
            >
              <FiArrowLeft style={{ marginRight: '8px' }} />
              Previous
            </Button>

            {currentStep < 4 ? (
              <Button variant="primary" onClick={handleNext}>
                Next
                <FiArrowRight style={{ marginLeft: '8px' }} />
              </Button>
            ) : (
              <Button 
                variant="primary" 
                onClick={handleSubmit}
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Creating...' : 'Create Duty Schedule'}
              </Button>
            )}
          </div>
        </FormCard>
      </StepContainer>
    </PageContainer>
  );
};

export default CreateDuty;
