import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { FiUser, FiSave, FiArrowLeft } from 'react-icons/fi';
import { theme } from '../../styles/theme';
import { Card, Button, Input, Select } from '../../styles/GlobalStyles';
import { Driver } from '../../types';

const CreateDriverContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
  max-width: 800px;
  margin: 0 auto;
`;

const PageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
`;

const BackButton = styled(Button)`
  padding: ${theme.spacing.sm};
`;

const PageTitle = styled.h1`
  font-size: ${theme.typography.fontSize['3xl']};
  font-weight: ${theme.typography.fontWeight.bold};
  color: ${theme.colors.textPrimary};
  margin: 0;
`;

const FormCard = styled(Card)`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
`;

const FormSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
`;

const SectionTitle = styled.h3`
  font-size: ${theme.typography.fontSize.lg};
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textPrimary};
  margin: 0;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${theme.spacing.lg};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.sm};
`;

const Label = styled.label`
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
`;

const RequiredIndicator = styled.span`
  color: ${theme.colors.error};
`;

const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
`;

const CreateDriver: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    employeeId: '',
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    address: '',
    dateOfBirth: '',
    licenseNumber: '',
    licenseClass: 'CDL-A',
    licenseExpiry: '',
    status: 'active' as Driver['status']
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.employeeId || !formData.firstName || !formData.lastName || !formData.email || 
        !formData.phone || !formData.address || !formData.dateOfBirth || !formData.licenseNumber || 
        !formData.licenseExpiry) {
      alert('Please fill in all required fields.');
      return;
    }

    setIsSubmitting(true);

    try {
      const newDriver: Omit<Driver, 'id' | 'createdAt' | 'updatedAt'> = {
        employeeId: formData.employeeId,
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        phone: formData.phone,
        address: formData.address,
        dateOfBirth: new Date(formData.dateOfBirth),
        licenseNumber: formData.licenseNumber,
        licenseClass: formData.licenseClass,
        licenseExpiry: new Date(formData.licenseExpiry),
        status: formData.status,
        rating: 0,
        totalTrips: 0,
        totalDistance: 0,
        joinDate: new Date()
      };

      console.log('Creating driver:', newDriver);

      // Simulate delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      navigate('/drivers');
    } catch (error) {
      console.error('Error creating driver:', error);
      alert('Error creating driver. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    navigate('/drivers');
  };

  return (
    <CreateDriverContainer className="fade-in">
      <PageHeader>
        <BackButton variant="secondary" onClick={handleCancel}>
          <FiArrowLeft />
        </BackButton>
        <PageTitle>Add New Driver</PageTitle>
      </PageHeader>

      <form onSubmit={handleSubmit}>
        <FormCard>
          <FormSection>
            <SectionTitle>
              <FiUser />
              Personal Information
            </SectionTitle>

            <FormRow>
              <FormGroup>
                <Label>
                  Employee ID <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="text"
                  placeholder="e.g., EMP001"
                  value={formData.employeeId}
                  onChange={(e) => handleInputChange('employeeId', e.target.value)}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Status</Label>
                <Select
                  value={formData.status}
                  onChange={(e) => handleInputChange('status', e.target.value)}
                >
                  <option value="active">Active</option>
                  <option value="inactive">Inactive</option>
                  <option value="suspended">Suspended</option>
                </Select>
              </FormGroup>
            </FormRow>

            <FormRow>
              <FormGroup>
                <Label>
                  First Name <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="text"
                  placeholder="Enter first name"
                  value={formData.firstName}
                  onChange={(e) => handleInputChange('firstName', e.target.value)}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>
                  Last Name <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="text"
                  placeholder="Enter last name"
                  value={formData.lastName}
                  onChange={(e) => handleInputChange('lastName', e.target.value)}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormRow>
              <FormGroup>
                <Label>
                  Email <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="email"
                  placeholder="driver@fleet.com"
                  value={formData.email}
                  onChange={(e) => handleInputChange('email', e.target.value)}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>
                  Phone <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="tel"
                  placeholder="+1-555-0123"
                  value={formData.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  required
                />
              </FormGroup>
            </FormRow>

            <FormGroup>
              <Label>
                Address <RequiredIndicator>*</RequiredIndicator>
              </Label>
              <Input
                type="text"
                placeholder="Full address"
                value={formData.address}
                onChange={(e) => handleInputChange('address', e.target.value)}
                required
              />
            </FormGroup>

            <FormRow>
              <FormGroup>
                <Label>
                  Date of Birth <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="date"
                  value={formData.dateOfBirth}
                  onChange={(e) => handleInputChange('dateOfBirth', e.target.value)}
                  required
                />
              </FormGroup>
              <div></div> {/* Empty div for grid alignment */}
            </FormRow>
          </FormSection>

          <FormSection>
            <SectionTitle>
              License Information
            </SectionTitle>

            <FormRow>
              <FormGroup>
                <Label>
                  License Number <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="text"
                  placeholder="e.g., DL123456789"
                  value={formData.licenseNumber}
                  onChange={(e) => handleInputChange('licenseNumber', e.target.value)}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>
                  License Class <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Select
                  value={formData.licenseClass}
                  onChange={(e) => handleInputChange('licenseClass', e.target.value)}
                >
                  <option value="CDL-A">CDL-A (Commercial Driver's License Class A)</option>
                  <option value="CDL-B">CDL-B (Commercial Driver's License Class B)</option>
                  <option value="CDL-C">CDL-C (Commercial Driver's License Class C)</option>
                  <option value="Regular">Regular Driver's License</option>
                </Select>
              </FormGroup>
            </FormRow>

            <FormRow>
              <FormGroup>
                <Label>
                  License Expiry <RequiredIndicator>*</RequiredIndicator>
                </Label>
                <Input
                  type="date"
                  value={formData.licenseExpiry}
                  onChange={(e) => handleInputChange('licenseExpiry', e.target.value)}
                  required
                />
              </FormGroup>
              <div></div> {/* Empty div for grid alignment */}
            </FormRow>
          </FormSection>

          <ActionButtons>
            <Button 
              type="button" 
              variant="secondary" 
              onClick={handleCancel}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              variant="primary"
              disabled={isSubmitting}
            >
              <FiSave />
              {isSubmitting ? 'Adding...' : 'Add Driver'}
            </Button>
          </ActionButtons>
        </FormCard>
      </form>
    </CreateDriverContainer>
  );
};

export default CreateDriver;
