import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { FiUser, FiKey, FiArrowRight, FiEye, FiEyeOff } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  PageContainer,
  FormCard,
  FormGroup,
  Label,
  Input,
  ErrorMessage
} from '../../components/Common/CommonStyles';
import { dataService } from '../../services/dataService';
import { useAuth } from '../../contexts/AuthContext';

interface LoginFormData {
  username: string;
  password: string;
}

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [formData, setFormData] = useState<LoginFormData>({
    username: '',
    otp: '',
    password: '',
    email: ''
  });
  const [errors, setErrors] = useState<string[]>([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isGeneratingOtp, setIsGeneratingOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [showOtp, setShowOtp] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [loginMode, setLoginMode] = useState<LoginMode>('password');
  const [forgotPasswordSent, setForgotPasswordSent] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear errors when user starts typing
    if (errors.length > 0) {
      setErrors([]);
    }
  };

  const validateForm = (): boolean => {
    const newErrors: string[] = [];

    if (!formData.username.trim()) {
      newErrors.push('Username is required');
    }

    if (loginMode === 'password' && !formData.password) {
      newErrors.push('Password is required');
    }

    if (loginMode === 'otp' && otpSent && !formData.otp) {
      newErrors.push('OTP is required');
    }

    if (loginMode === 'forgot-password' && !formData.email) {
      newErrors.push('Email is required');
    }

    setErrors(newErrors);
    return newErrors.length === 0;
  };

  const handleGenerateOtp = async () => {
    if (!formData.username.trim()) {
      setErrors(['Username is required']);
      return;
    }

    setIsGeneratingOtp(true);
    setErrors([]);

    try {
      const response = await dataService.generateUserOtp(formData.username);
      setOtpSent(true);

      // Show OTP in alert for development/testing
      if (response.otp) {
        alert(`Your OTP is: ${response.otp}\n\nPlease enter this code to complete login.`);
      }
    } catch (error: any) {
      console.error('Error generating OTP:', error);
      setErrors([error.message || 'Failed to generate OTP. Please try again.']);
    } finally {
      setIsGeneratingOtp(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    if (loginMode === 'otp' && !otpSent) {
      await handleGenerateOtp();
      return;
    }

    if (loginMode === 'forgot-password') {
      await handleForgotPassword();
      return;
    }

    setIsSubmitting(true);

    try {
      let response;

      if (loginMode === 'otp') {
        response = await dataService.loginUserWithOtp(formData.username, formData.otp);
      } else {
        response = await dataService.loginWithPassword(formData.username, formData.password);
      }

      // Use AuthContext to handle login
      login(response.user, response.token);

      navigate('/dashboard');
    } catch (error: any) {
      console.error('Error logging in:', error);
      setErrors([error.message || 'Failed to login. Please try again.']);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleResendOtp = () => {
    setOtpSent(false);
    setFormData(prev => ({ ...prev, otp: '' }));
    handleGenerateOtp();
  };

  const handleForgotPassword = async () => {
    if (!formData.email.trim()) {
      setErrors(['Email is required']);
      return;
    }

    setIsSubmitting(true);
    setErrors([]);

    try {
      await dataService.requestPasswordReset(formData.email);
      setForgotPasswordSent(true);
    } catch (error: any) {
      console.error('Error requesting password reset:', error);
      setErrors([error.message || 'Failed to send password reset email. Please try again.']);
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      username: '',
      otp: '',
      password: '',
      email: ''
    });
    setErrors([]);
    setOtpSent(false);
    setForgotPasswordSent(false);
  };

  const switchMode = (mode: LoginMode) => {
    setLoginMode(mode);
    resetForm();
  };

  return (
    <PageContainer className="fade-in" style={{ 
      minHeight: '100vh', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      <div style={{ width: '100%', maxWidth: '400px', padding: '20px' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h1 style={{ 
            color: 'white', 
            fontSize: '2rem', 
            fontWeight: 'bold',
            marginBottom: '0.5rem'
          }}>
            Welcome to Moveo
          </h1>
          <p style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '1rem' }}>
            Fleet Management System
          </p>
        </div>

        <FormCard>
          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '1.5rem' }}>
              <h2 style={{ 
                textAlign: 'center', 
                marginBottom: '1rem',
                color: '#2c3e50',
                fontSize: '1.5rem'
              }}>
                {loginMode === 'otp' && otpSent ? 'Enter OTP' : 
                 loginMode === 'forgot-password' ? 'Reset Password' : 'Login'}
              </h2>
              <p style={{ 
                textAlign: 'center', 
                color: '#7f8c8d',
                fontSize: '0.9rem',
                marginBottom: '1.5rem'
              }}>
                {loginMode === 'otp' && otpSent 
                  ? `OTP sent to ${formData.username}. Enter the code below.`
                  : loginMode === 'otp'
                  ? 'Enter your username to receive an OTP'
                  : loginMode === 'forgot-password'
                  ? 'Enter your email to receive a password reset link'
                  : 'Enter your username and password to login'
                }
              </p>
            </div>

            {/* Login Mode Toggle */}
            <div style={{ 
              display: 'flex', 
              marginBottom: '1.5rem',
              backgroundColor: '#f8fafc',
              borderRadius: '8px',
              padding: '4px'
            }}>
              <button
                type="button"
                onClick={() => switchMode('password')}
                style={{
                  flex: 1,
                  padding: '8px 12px',
                  border: 'none',
                  borderRadius: '6px',
                  backgroundColor: loginMode === 'password' ? '#2563eb' : 'transparent',
                  color: loginMode === 'password' ? 'white' : '#64748b',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '500'
                }}
              >
                Password
              </button>
              <button
                type="button"
                onClick={() => switchMode('otp')}
                style={{
                  flex: 1,
                  padding: '8px 12px',
                  border: 'none',
                  borderRadius: '6px',
                  backgroundColor: loginMode === 'otp' ? '#2563eb' : 'transparent',
                  color: loginMode === 'otp' ? 'white' : '#64748b',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '500'
                }}
              >
                OTP
              </button>
            </div>

            {/* Username Field */}
            {loginMode !== 'forgot-password' && (
              <FormGroup>
                <Label htmlFor="username">Username *</Label>
                <div style={{ position: 'relative' }}>
                  <Input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleInputChange}
                    placeholder="Enter your username"
                    hasError={errors.length > 0}
                    disabled={loginMode === 'otp' && otpSent}
                    style={{ paddingLeft: '40px' }}
                  />
                  <FiUser 
                    size={18} 
                    style={{ 
                      position: 'absolute', 
                      left: '12px', 
                      top: '50%', 
                      transform: 'translateY(-50%)',
                      color: '#7f8c8d'
                    }} 
                  />
                </div>
              </FormGroup>
            )}

            {/* Email Field for Forgot Password */}
            {loginMode === 'forgot-password' && (
              <FormGroup>
                <Label htmlFor="email">Email *</Label>
                <div style={{ position: 'relative' }}>
                  <Input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="Enter your email"
                    hasError={errors.length > 0}
                    style={{ paddingLeft: '40px' }}
                  />
                  <FiMail 
                    size={18} 
                    style={{ 
                      position: 'absolute', 
                      left: '12px', 
                      top: '50%', 
                      transform: 'translateY(-50%)',
                      color: '#7f8c8d'
                    }} 
                  />
                </div>
              </FormGroup>
            )}

            {/* Password Field for Password Login */}
            {loginMode === 'password' && (
              <FormGroup>
                <Label htmlFor="password">Password *</Label>
                <div style={{ position: 'relative' }}>
                  <Input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder="Enter your password"
                    hasError={errors.length > 0}
                    style={{ paddingLeft: '40px', paddingRight: '40px' }}
                  />
                  <FiKey 
                    size={18} 
                    style={{ 
                      position: 'absolute', 
                      left: '12px', 
                      top: '50%', 
                      transform: 'translateY(-50%)',
                      color: '#7f8c8d'
                    }} 
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    style={{
                      position: 'absolute',
                      right: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      color: '#7f8c8d'
                    }}
                  >
                    {showPassword ? <FiEyeOff size={18} /> : <FiEye size={18} />}
                  </button>
                </div>
              </FormGroup>
            )}

            {/* OTP Field for OTP Login */}
            {loginMode === 'otp' && otpSent && (
              <FormGroup>
                <Label htmlFor="otp">OTP Code *</Label>
                <div style={{ position: 'relative' }}>
                  <Input
                    type={showOtp ? 'text' : 'password'}
                    id="otp"
                    name="otp"
                    value={formData.otp}
                    onChange={handleInputChange}
                    placeholder="Enter 6-digit OTP"
                    hasError={errors.length > 0}
                    style={{ paddingLeft: '40px', paddingRight: '40px' }}
                  />
                  <FiKey 
                    size={18} 
                    style={{ 
                      position: 'absolute', 
                      left: '12px', 
                      top: '50%', 
                      transform: 'translateY(-50%)',
                      color: '#7f8c8d'
                    }} 
                  />
                  <button
                    type="button"
                    onClick={() => setShowOtp(!showOtp)}
                    style={{
                      position: 'absolute',
                      right: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      color: '#7f8c8d'
                    }}
                  >
                    {showOtp ? <FiEyeOff size={18} /> : <FiEye size={18} />}
                  </button>
                </div>
              </FormGroup>
            )}

            {errors.length > 0 && (
              <FormGroup>
                {errors.map((error, index) => (
                  <ErrorMessage key={index}>{error}</ErrorMessage>
                ))}
              </FormGroup>
            )}

            {forgotPasswordSent && (
              <FormGroup>
                <div style={{
                  backgroundColor: '#d1fae5',
                  border: '1px solid #10b981',
                  borderRadius: '8px',
                  padding: '12px',
                  color: '#065f46',
                  fontSize: '0.9rem',
                  textAlign: 'center'
                }}>
                  Password reset link sent! Please check your email.
                </div>
              </FormGroup>
            )}

            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <Button 
                variant="primary" 
                type="submit"
                disabled={isSubmitting || isGeneratingOtp}
                style={{ width: '100%' }}
              >
                {isSubmitting ? (
                  loginMode === 'forgot-password' ? 'Sending...' : 'Logging in...'
                ) : isGeneratingOtp ? (
                  'Generating OTP...'
                ) : loginMode === 'otp' && otpSent ? (
                  <>
                    <FiArrowRight size={18} />
                    Login
                  </>
                ) : loginMode === 'otp' ? (
                  'Generate OTP'
                ) : loginMode === 'forgot-password' ? (
                  'Send Reset Link'
                ) : (
                  <>
                    <FiArrowRight size={18} />
                    Login
                  </>
                )}
              </Button>

              {/* OTP Resend Button */}
              {loginMode === 'otp' && otpSent && (
                <Button 
                  variant="secondary" 
                  type="button"
                  onClick={handleResendOtp}
                  disabled={isGeneratingOtp}
                  style={{ width: '100%' }}
                >
                  Resend OTP
                </Button>
              )}

              {/* Forgot Password Link */}
              {loginMode === 'password' && (
                <button
                  type="button"
                  onClick={() => switchMode('forgot-password')}
                  style={{
                    background: 'none',
                    border: 'none',
                    color: '#2563eb',
                    cursor: 'pointer',
                    fontSize: '0.9rem',
                    textDecoration: 'underline',
                    padding: '8px 0'
                  }}
                >
                  Forgot your password?
                </button>
              )}

              {/* Back to Login Link */}
              {loginMode === 'forgot-password' && (
                <button
                  type="button"
                  onClick={() => switchMode('password')}
                  style={{
                    background: 'none',
                    border: 'none',
                    color: '#2563eb',
                    cursor: 'pointer',
                    fontSize: '0.9rem',
                    textDecoration: 'underline',
                    padding: '8px 0'
                  }}
                >
                  Back to Login
                </button>
              )}
            </div>
          </form>
        </FormCard>
      </div>
    </PageContainer>
  );
};

export default Login;
