const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: ${theme.spacing['3xl']};
  text-align: center;
  color: ${theme.colors.textMuted};
`;

const EmptyStateIcon = styled.div`
  font-size: 48px;
  margin-bottom: ${theme.spacing.lg};
  opacity: 0.5;
`;

const Vehicles: React.FC = () => {
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [filteredVehicles, setFilteredVehicles] = useState<Vehicle[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [typeFilter, setTypeFilter] = useState('all');

  useEffect(() => {
    const loadVehicles = async () => {
      try {
        const vehiclesData = await mockAPI.getVehicles();
        setVehicles(vehiclesData);
        setFilteredVehicles(vehiclesData);
      } catch (error) {
        console.error('Error loading vehicles:', error);
      } finally {
        setLoading(false);
      }
    };

    loadVehicles();
  }, []);

  useEffect(() => {
    let filtered = vehicles;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(vehicle =>
        vehicle.plateNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
        vehicle.make.toLowerCase().includes(searchTerm.toLowerCase()) ||
        vehicle.model.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by status
    if (statusFilter !== 'all') {
      filtered = filtered.filter(vehicle => vehicle.status === statusFilter);
    }

    // Filter by type
    if (typeFilter !== 'all') {
      filtered = filtered.filter(vehicle => vehicle.type === typeFilter);
    }

    setFilteredVehicles(filtered);
  }, [vehicles, searchTerm, statusFilter, typeFilter]);

  if (loading) {
    return <div>Loading vehicles...</div>;
  }

  return (
    <VehiclesContainer className="fade-in">
      <PageHeader>
        <PageTitle>Vehicles</PageTitle>
        <HeaderActions>
          <Button variant="primary" size="md">
            <FiPlus />
            Add Vehicle
          </Button>
        </HeaderActions>
      </PageHeader>

      <FiltersCard>
        <SearchContainer>
          <SearchIcon>
            <FiSearch size={18} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search vehicles..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>

        <FilterSelect
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="all">All Status</option>
          <option value="active">Active</option>
          <option value="maintenance">Maintenance</option>
          <option value="inactive">Inactive</option>
        </FilterSelect>

        <FilterSelect
          value={typeFilter}
          onChange={(e) => setTypeFilter(e.target.value)}
        >
          <option value="all">All Types</option>
          <option value="truck">Truck</option>
          <option value="van">Van</option>
          <option value="car">Car</option>
          <option value="motorcycle">Motorcycle</option>
        </FilterSelect>
      </FiltersCard>

      {filteredVehicles.length === 0 ? (
        <EmptyState>
          <EmptyStateIcon>ðŸš›</EmptyStateIcon>
          <h3>No vehicles found</h3>
          <p>Try adjusting your search criteria or add a new vehicle.</p>
        </EmptyState>
      ) : (
        <VehiclesGrid>
          {filteredVehicles.map((vehicle) => (
            <VehicleCard key={vehicle.id}>
              <VehicleHeader>
                <VehicleInfo>
                  <VehiclePlate>{vehicle.plateNumber}</VehiclePlate>
                  <VehicleModel>{vehicle.make} {vehicle.model} ({vehicle.year})</VehicleModel>
                </VehicleInfo>
                <VehicleActions>
                  <ActionButton>
                    <FiEdit size={16} />
                  </ActionButton>
                  <ActionButton>
                    <FiTrash2 size={16} />
                  </ActionButton>
                </VehicleActions>
              </VehicleHeader>

              <StatusBadge status={vehicle.status}>
                {vehicle.status}
              </StatusBadge>

              <VehicleDetails>
                <DetailItem>
                  <DetailLabel>Type</DetailLabel>
                  <DetailValue>{vehicle.type}</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Fuel Type</DetailLabel>
                  <DetailValue>{vehicle.fuelType}</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Mileage</DetailLabel>
                  <DetailValue>{vehicle.mileage.toLocaleString()} miles</DetailValue>
                </DetailItem>
                <DetailItem>
                  <DetailLabel>Next Maintenance</DetailLabel>
                  <DetailValue>
                    {vehicle.nextMaintenance.toLocaleDateString()}
                  </DetailValue>
                </DetailItem>
              </VehicleDetails>

              {vehicle.currentLocation && (
                <LocationInfo>
                  <LocationIcon>
                    <FiMapPin size={16} />
                  </LocationIcon>
                  <LocationText>{vehicle.currentLocation.address}</LocationText>
                </LocationInfo>
              )}
            </VehicleCard>
          ))}
        </VehiclesGrid>
      )}
    </VehiclesContainer>
  );
};

export default Vehicles;
