  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.card};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
  }
`;

const StatusBadge = styled.span<{ status: string }>`
  display: inline-flex;
  align-items: center;
  padding: ${theme.spacing.xs} ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.xs};
  font-weight: ${theme.typography.fontWeight.medium};
  background: ${({ status }) => 
    status === 'in-progress' ? theme.colors.success :
    status === 'completed' ? theme.colors.info :
    theme.colors.surface};
  color: white;
`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: ${theme.spacing.lg};
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
`;

const PaginationInfo = styled.div`
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.fontSize.sm};
`;

const PaginationButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.sm};
`;

const PageButton = styled.button<{ active?: boolean; disabled?: boolean }>`
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${({ active, disabled }) => 
    disabled ? theme.colors.surface :
    active ? theme.colors.primary :
    theme.colors.card};
  color: ${({ active, disabled }) => 
    disabled ? theme.colors.textDisabled :
    active ? 'white' :
    theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
  cursor: ${({ disabled }) => disabled ? 'not-allowed' : 'pointer'};

  &:hover {
    background: ${({ active, disabled }) => 
      disabled ? theme.colors.surface :
      active ? theme.colors.primaryHover :
      theme.colors.cardHover};
  }
`;

const DutiesList: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<'trips' | 'assignment'>('trips');
  const [selectedDate, setSelectedDate] = useState(() => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  });
  const [duties, setDuties] = useState<Duty[]>([]);
  const [routes, setRoutes] = useState<Route[]>([]);
  const [filteredDuties, setFilteredDuties] = useState<Duty[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [routeFilter, setRouteFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDuties();
  }, [selectedDate]);

  useEffect(() => {
    filterDuties();
  }, [duties, searchTerm, routeFilter, statusFilter]);

  const filterDuties = () => {
    let filtered = [...duties];

    // Only show TRIP duties
    filtered = filtered.filter(duty => duty.dutyType === 'TRIP');

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(duty => {
        const routeName = duty.schedule?.name || 'Unknown Route';
        return routeName.toLowerCase().includes(searchTerm.toLowerCase());
      });
    }

    // Route filter
    if (routeFilter !== 'all') {
      filtered = filtered.filter(duty => {
        const routeName = duty.schedule?.name || 'Unknown Route';
        return routeName === routeFilter;
      });
    }

    // Status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(duty => {
        const status = getDutyStatus(duty);
        return status === statusFilter;
      });
    }

    setFilteredDuties(filtered);
    setCurrentPage(1); // Reset to first page on filter change
  };

  const loadDuties = async () => {
    try {
      setLoading(true);

      // Load duties and routes in parallel
      const [dutiesData, routesData] = await Promise.all([
        dataService.getDuties(),
        dataService.getRoutes()
      ]);

      // Filter duties by selected date
      const selectedDateObj = new Date(selectedDate);
      const filteredDuties = dutiesData.filter(duty => {
        const dutyDate = new Date(duty.date);
        return dutyDate.toDateString() === selectedDateObj.toDateString();
      });

      setDuties(filteredDuties);
      setRoutes(routesData);
    } catch (error) {
      console.error('Error loading duties:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateDuty = () => {
    navigate('/duties/create');
  };

  const getDutyStatus = (duty: Duty): 'planned' | 'in-progress' | 'completed' => {
    // For now, we'll determine status based on whether driver/vehicle are assigned
    // In a real system, this might be based on actual start/end times
    if (duty.driverId && duty.vehicleId) {
      return 'planned'; // Assigned but not started
    }
    return 'planned'; // Default to planned
  };

  const formatTime = (time: string) => {
    const date = new Date(time);
    return date.toLocaleTimeString('en-US', { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: false 
    });
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'in-progress':
        return 'In Progress';
      case 'completed':
        return 'Completed';
      default:
        return 'Planned';
    }
  };

  // Get unique routes for filter dropdown
  const uniqueRoutes = Array.from(new Set(
    duties
      .filter(duty => duty.dutyType === 'TRIP')
      .map(duty => duty.schedule?.name || 'Unknown Route')
  ));

  // Calculate pagination
  const totalPages = Math.ceil(filteredDuties.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentDuties = filteredDuties.slice(startIndex, endIndex);

  const renderTripsTab = () => (
    <>
      <FiltersContainer>
        <DateSelector>
          <FiCalendar size={20} color={theme.colors.primary} />
          <DateInput
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </DateSelector>

        <SearchContainer style={{ flex: 1, minWidth: '200px' }}>
          <SearchInput
            type="text"
            placeholder="Search duties..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>

        <FilterSelect value={routeFilter} onChange={(e) => setRouteFilter(e.target.value)}>
          <option value="all">All Routes</option>
          {uniqueRoutes.map(route => (
            <option key={route} value={route}>{route}</option>
          ))}
        </FilterSelect>

        <FilterSelect value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
          <option value="all">All Status</option>
          <option value="planned">Planned</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </FilterSelect>
      </FiltersContainer>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '40px' }}>
          <p style={{ color: theme.colors.textSecondary }}>Loading trips...</p>
        </div>
      ) : filteredDuties.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '40px' }}>
          <p style={{ color: theme.colors.textSecondary }}>
            No trip duties found for {new Date(selectedDate).toLocaleDateString()}
          </p>
        </div>
      ) : (
        <>
          <Table>
            <TableHeader>
              <TableHeaderCell>Time</TableHeaderCell>
              <TableHeaderCell>Duty #</TableHeaderCell>
              <TableHeaderCell>Schedule</TableHeaderCell>
              <TableHeaderCell>Driver</TableHeaderCell>
              <TableHeaderCell>Vehicle</TableHeaderCell>
              <TableHeaderCell>Status</TableHeaderCell>
            </TableHeader>
            <TableBody>
              {currentDuties.map((duty) => {
                const status = getDutyStatus(duty);
                return (
                  <TableRow key={duty.id}>
                    <TableCell>
                      {formatTime(duty.startTime)} - {formatTime(duty.endTime)}
                    </TableCell>
                    <TableCell>D-{duty.id}</TableCell>
                    <TableCell>{duty.schedule?.name || 'Unknown Schedule'}</TableCell>
                    <TableCell>{duty.driver?.name || '-'}</TableCell>
                    <TableCell>{duty.vehicle ? `${duty.vehicle.plateNo} (${duty.vehicle.fleetNo})` : '-'}</TableCell>
                    <TableCell>
                      <StatusBadge status={status}>
                        {getStatusLabel(status)}
                      </StatusBadge>
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>

          <PaginationContainer>
            <PaginationInfo>
              Showing {startIndex + 1} to {Math.min(endIndex, filteredDuties.length)} of {filteredDuties.length} duties
            </PaginationInfo>
            <PaginationButtons>
              <PageButton 
                disabled={currentPage === 1}
                onClick={() => setCurrentPage(currentPage - 1)}
              >
                Previous
              </PageButton>
              {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                let pageNum = currentPage;
                if (totalPages <= 5) {
                  pageNum = i + 1;
                } else if (currentPage <= 3) {
                  pageNum = i + 1;
                } else if (currentPage >= totalPages - 2) {
                  pageNum = totalPages - 4 + i;
                } else {
                  pageNum = currentPage - 2 + i;
                }
                return (
                  <PageButton
                    key={pageNum}
                    active={currentPage === pageNum}
                    onClick={() => setCurrentPage(pageNum)}
                  >
                    {pageNum}
                  </PageButton>
                );
              })}
              <PageButton 
                disabled={currentPage === totalPages}
                onClick={() => setCurrentPage(currentPage + 1)}
              >
                Next
              </PageButton>
            </PaginationButtons>
          </PaginationContainer>
        </>
      )}
    </>
  );

  const renderAssignmentTab = () => (
    <AssignmentInterface />
  );

  return (
    <PageContainer className="fade-in">
      <PageHeader>
        <PageTitle>Duties Management</PageTitle>
        <HeaderActions>
          <Button onClick={handleCreateDuty} variant="primary">
            <FiPlus style={{ marginRight: '8px' }} />
            Generate Trips
          </Button>
        </HeaderActions>
      </PageHeader>

      <TabsContainer>
        <Tab 
          active={activeTab === 'trips'} 
          onClick={() => setActiveTab('trips')}
        >
          Trips List
        </Tab>
        <Tab 
          active={activeTab === 'assignment'} 
          onClick={() => setActiveTab('assignment')}
        >
          Assignment
        </Tab>
      </TabsContainer>

      <div>
        {activeTab === 'trips' ? renderTripsTab() : renderAssignmentTab()}
      </div>
    </PageContainer>
  );
};

export default DutiesList;
