import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, Popup } from 'react-leaflet';
import L from 'leaflet';
import styled from 'styled-components';
import { theme } from '../../styles/theme';
import { Stop } from '../../types';

// Fix for default markers in React Leaflet
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const MapWrapper = styled.div<{ height?: string }>`
  width: 100%;
  height: ${({ height }) => height || '500px'};
  border: 2px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  overflow: hidden;

  .leaflet-container {
    height: 100%;
    width: 100%;
    background: ${theme.colors.surface};
  }

  .leaflet-control-container {
    .leaflet-control-zoom {
      background: ${theme.colors.card};
      border: 1px solid ${theme.colors.border};

      a {
        background: ${theme.colors.card};
        color: ${theme.colors.textPrimary};
        border-bottom: 1px solid ${theme.colors.border};

        &:hover {
          background: ${theme.colors.cardHover};
        }

        &:last-child {
          border-bottom: none;
        }
      }
    }

    .leaflet-control-attribution {
      background: rgba(0, 0, 0, 0.8);
      color: white;
      font-size: 11px;

      a {
        color: #3b82f6;
      }
    }
  }

  .leaflet-popup-content-wrapper {
    background: ${theme.colors.card};
    color: ${theme.colors.textPrimary};
    border-radius: ${theme.borderRadius.md};
    box-shadow: ${theme.shadows.lg};
  }

  .leaflet-popup-tip {
    background: ${theme.colors.card};
  }
`;

const MapInfo = styled.div`
  position: absolute;
  top: ${theme.spacing.md};
  left: ${theme.spacing.md};
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  z-index: 1000;
  pointer-events: none;
`;

const PopupContent = styled.div`
  min-width: 200px;
`;

const PopupTitle = styled.h4`
  margin: 0 0 ${theme.spacing.sm} 0;
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
`;

const PopupDetail = styled.div`
  margin: ${theme.spacing.xs} 0;
  font-size: ${theme.typography.fontSize.xs};
  color: ${theme.colors.textSecondary};
`;

interface RouteMapProps {
  stops: Stop[];
  selectedStops: Stop[];
  center?: { lat: number; lng: number };
  zoom?: number;
  showRoute?: boolean;
  height?: string;
}

const RouteMap: React.FC<RouteMapProps> = ({ 
  stops,
  selectedStops,
  center,
  zoom = 12,
  showRoute = true,
  height
}) => {
  const [mapCenter, setMapCenter] = useState<{ lat: number; lng: number }>(
    center || { lat: 40.7128, lng: -74.0060 }
  );

  useEffect(() => {
    // Auto-center map based on selected stops or all stops
    const stopsToCenter = selectedStops.length > 0 ? selectedStops : stops;

    if (stopsToCenter.length > 0) {
      const bounds = stopsToCenter.reduce(
        (acc, stop) => ({
          minLat: Math.min(acc.minLat, stop.location.lat),
          maxLat: Math.max(acc.maxLat, stop.location.lat),
          minLng: Math.min(acc.minLng, stop.location.lon),
          maxLng: Math.max(acc.maxLng, stop.location.lon)
        }),
        {
          minLat: stopsToCenter[0].location.lat,
          maxLat: stopsToCenter[0].location.lat,
          minLng: stopsToCenter[0].location.lon,
          maxLng: stopsToCenter[0].location.lon
        }
      );

      const centerLat = (bounds.minLat + bounds.maxLat) / 2;
      const centerLng = (bounds.minLng + bounds.maxLng) / 2;

      setMapCenter({ lat: centerLat, lng: centerLng });
    }
  }, [stops, selectedStops]);

  // Create route line coordinates
  const routeCoordinates: [number, number][] = selectedStops.map(stop => [
    stop.location.lat,
    stop.location.lon
  ]);

  return (
    <MapWrapper>
      <MapInfo>
        {selectedStops.length > 0 
          ? `Route with ${selectedStops.length} stops` 
          : `${stops.length} available stops`
        }
      </MapInfo>

      <MapContainer
        center={[mapCenter.lat, mapCenter.lng]}
        zoom={zoom}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Show all available stops */}
        {stops.map((stop) => {
          const isSelected = selectedStops.some(s => s.id === stop.id);
          const selectedIndex = selectedStops.findIndex(s => s.id === stop.id);

          return (
            <Marker
              key={stop.id}
              position={[stop.location.lat, stop.location.lon]}
              opacity={isSelected ? 1 : 0.6}
            >
              <Popup>
                <PopupContent>
                  <PopupTitle>{stop.name}</PopupTitle>
                  <PopupDetail><strong>Code:</strong> {stop.code}</PopupDetail>
                  {isSelected && (
                    <PopupDetail><strong>Order:</strong> #{selectedIndex + 1} in route</PopupDetail>
                  )}
                </PopupContent>
              </Popup>
            </Marker>
          );
        })}

        {/* Show route line */}
        {showRoute && selectedStops.length > 1 && (
          <Polyline
            positions={routeCoordinates}
            color={theme.colors.primary}
            weight={4}
            opacity={0.8}
            dashArray="10, 5"
          />
        )}
      </MapContainer>
    </MapWrapper>
  );
};

export default RouteMap;
