interface TripTemplateManagerProps {
  scheduleId?: string;
  templates: DutyTemplate[];
  onTemplatesChange: (templates: DutyTemplate[]) => void;
  onError?: (error: string) => void;
}

const TripTemplateManager: React.FC<TripTemplateManagerProps> = ({
  scheduleId,
  templates,
  onTemplatesChange,
  onError
}) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [formMode, setFormMode] = useState<'single' | 'bulk'>('single');
  const [error, setError] = useState<string | null>(null);
  const [viewMode, setViewMode] = useState<'table' | 'timeline'>('table');
  const [expandedBlocks, setExpandedBlocks] = useState<Set<string>>(new Set());
  const [expandedRuns, setExpandedRuns] = useState<Set<string>>(new Set());
  const [editingBlockCode, setEditingBlockCode] = useState<string | null>(null);
  const [editingRunCode, setEditingRunCode] = useState<string | null>(null);
  const [editBlockCodeValue, setEditBlockCodeValue] = useState<string>('');
  const [editRunCodeValue, setEditRunCodeValue] = useState<string>('');

  const getDutyTypeIcon = (dutyType: string) => {
    switch (dutyType) {
      case 'TRIP': return <FiTruck size={14} />;
      case 'WASHING': return <FiDroplet size={14} />;
      case 'MAINTENANCE': return <FiTruck size={14} />;
      default: return <FiClock size={14} />;
    }
  };

  const getDutyTypeLabel = (dutyType: string) => {
    switch (dutyType) {
      case 'TRIP': return 'Trip';
      case 'WASHING': return 'Washing';
      case 'MAINTENANCE': return 'Maintenance';
      default: return dutyType;
    }
  };

  const formatTime = (timeString: string) => {
    try {
      const date = new Date(timeString);
      return date.toLocaleTimeString('en-US', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: false 
      });
    } catch {
      return timeString;
    }
  };

  const calculateDuration = (startTime: string, endTime: string) => {
    try {
      const start = new Date(startTime);
      const end = new Date(endTime);
      return (end.getTime() - start.getTime()) / (1000 * 60 * 60); // hours
    } catch {
      return 0;
    }
  };

  const calculateStartHour = (timeString: string) => {
    try {
      const date = new Date(timeString);
      return date.getHours() + (date.getMinutes() / 60);
    } catch {
      return 0;
    }
  };

  const checkForOverlaps = (templates: DutyTemplate[]) => {
    const sortedTemplates = [...templates].sort((a, b) => 
      new Date(a.startTime).getTime() - new Date(b.startTime).getTime()
    );

    for (let i = 0; i < sortedTemplates.length - 1; i++) {
      const current = sortedTemplates[i];
      const next = sortedTemplates[i + 1];

      const currentEnd = new Date(current.endTime).getTime();
      const nextStart = new Date(next.startTime).getTime();

      if (currentEnd > nextStart) {
        return true;
      }
    }
    return false;
  };

  const handleDeleteTemplate = async (templateId: string) => {
    if (!scheduleId) {
      // For new schedules, just remove from local state
      const updatedTemplates = templates.filter(t => t.id !== templateId);
      onTemplatesChange(updatedTemplates);
      return;
    }

    try {
      await apiService.deleteDutyTemplate(scheduleId, templateId);
      const updatedTemplates = templates.filter(t => t.id !== templateId);
      onTemplatesChange(updatedTemplates);
    } catch (error) {
      console.error('Error deleting template:', error);
      setError('Failed to delete template');
      onError?.('Failed to delete template');
    }
  };

  const handleAddTemplate = async (templateData: Omit<DutyTemplate, 'id' | 'scheduleId' | 'createdAt' | 'updatedAt'>) => {
    if (!scheduleId) {
      // For new schedules, add to local state with temporary ID
      const newTemplate: DutyTemplate = {
        ...templateData,
        id: `temp-${Date.now()}`,
        scheduleId: 'temp',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      onTemplatesChange([...templates, newTemplate]);
      return;
    }

    try {
      const newTemplate = await apiService.createDutyTemplate(scheduleId, {
        ...templateData,
        scheduleId
      });
      onTemplatesChange([...templates, newTemplate]);
    } catch (error) {
      console.error('Error creating template:', error);
      setError('Failed to create template');
      onError?.('Failed to create template');
    }
  };

  const handleBulkAddTemplates = async (templatesData: Omit<DutyTemplate, 'id' | 'scheduleId' | 'createdAt' | 'updatedAt'>[]) => {
    if (!scheduleId) {
      // For new schedules, add to local state with temporary IDs
      const newTemplates: DutyTemplate[] = templatesData.map((templateData, index) => ({
        ...templateData,
        id: `temp-${Date.now()}-${index}`,
        scheduleId: 'temp',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      }));
      onTemplatesChange([...templates, ...newTemplates]);
      return;
    }

    try {
      // Create templates one by one for existing schedules
      const newTemplates: DutyTemplate[] = [];
      for (const templateData of templatesData) {
        const newTemplate = await apiService.createDutyTemplate(scheduleId, {
          ...templateData,
          scheduleId
        });
        newTemplates.push(newTemplate);
      }
      onTemplatesChange([...templates, ...newTemplates]);
    } catch (error) {
      console.error('Error creating bulk templates:', error);
      setError('Failed to create some templates');
      onError?.('Failed to create some templates');
    }
  };

  const hasOverlaps = checkForOverlaps(templates);

  // Group templates by vehicle block codes
  const vehicleBlocks = useMemo(() => {
    const blocks = new Map<string, DutyTemplate[]>();
    templates.forEach(template => {
      const code = template.vehicleBlockCode || 'Unassigned';
      if (!blocks.has(code)) {
        blocks.set(code, []);
      }
      blocks.get(code)!.push(template);
    });
    return blocks;
  }, [templates]);

  // Group templates by driver run codes
  const driverRuns = useMemo(() => {
    const runs = new Map<string, DutyTemplate[]>();
    templates.forEach(template => {
      const code = template.driverRunCode || 'Unassigned';
      if (!runs.has(code)) {
        runs.set(code, []);
      }
      runs.get(code)!.push(template);
    });
    return runs;
  }, [templates]);

  // Get unique codes for dropdowns
  const allBlockCodes = useMemo(() => {
    const codes = new Set<string>();
    templates.forEach(t => {
      if (t.vehicleBlockCode) codes.add(t.vehicleBlockCode);
    });
    return Array.from(codes).sort();
  }, [templates]);

  const allRunCodes = useMemo(() => {
    const codes = new Set<string>();
    templates.forEach(t => {
      if (t.driverRunCode) codes.add(t.driverRunCode);
    });
    return Array.from(codes).sort();
  }, [templates]);

  const handleUpdateTemplate = async (templateId: string, updates: Partial<DutyTemplate>) => {
    const updatedTemplates = templates.map(t => 
      t.id === templateId ? { ...t, ...updates } : t
    );

    if (!scheduleId) {
      onTemplatesChange(updatedTemplates);
      return;
    }

    try {
      const template = templates.find(t => t.id === templateId);
      if (template) {
        await apiService.updateDutyTemplate(scheduleId, templateId, {
          ...updates,
          startTime: template.startTime,
          endTime: template.endTime,
          dutyType: template.dutyType,
          scheduleId: template.scheduleId
        });
      }
      onTemplatesChange(updatedTemplates);
    } catch (error) {
      console.error('Error updating template:', error);
      setError('Failed to update template');
      onError?.('Failed to update template');
    }
  };

  const handleAssignBlockCode = (templateId: string, code: string) => {
    handleUpdateTemplate(templateId, { vehicleBlockCode: code === '' ? undefined : code });
  };

  const handleAssignRunCode = (templateId: string, code: string) => {
    handleUpdateTemplate(templateId, { driverRunCode: code === '' ? undefined : code });
  };

  const handleEditBlockCode = (code: string) => {
    setEditingBlockCode(code);
    setEditBlockCodeValue(code);
  };

  const handleSaveBlockCode = () => {
    if (editingBlockCode && editBlockCodeValue) {
      const oldCode = editingBlockCode;
      const newCode = editBlockCodeValue.trim();

      if (newCode !== oldCode) {
        // Update all templates with this block code
        templates.forEach(template => {
          if (template.vehicleBlockCode === oldCode) {
            handleUpdateTemplate(template.id, { vehicleBlockCode: newCode });
          }
        });
      }
    }
    setEditingBlockCode(null);
    setEditBlockCodeValue('');
  };

  const handleEditRunCode = (code: string) => {
    setEditingRunCode(code);
    setEditRunCodeValue(code);
  };

  const handleSaveRunCode = () => {
    if (editingRunCode && editRunCodeValue) {
      const oldCode = editingRunCode;
      const newCode = editRunCodeValue.trim();

      if (newCode !== oldCode) {
        // Update all templates with this run code
        templates.forEach(template => {
          if (template.driverRunCode === oldCode) {
            handleUpdateTemplate(template.id, { driverRunCode: newCode });
          }
        });
      }
    }
    setEditingRunCode(null);
    setEditRunCodeValue('');
  };

  const toggleBlockExpanded = (code: string) => {
    const newExpanded = new Set(expandedBlocks);
    if (newExpanded.has(code)) {
      newExpanded.delete(code);
    } else {
      newExpanded.add(code);
    }
    setExpandedBlocks(newExpanded);
  };

  const toggleRunExpanded = (code: string) => {
    const newExpanded = new Set(expandedRuns);
    if (newExpanded.has(code)) {
      newExpanded.delete(code);
    } else {
      newExpanded.add(code);
    }
    setExpandedRuns(newExpanded);
  };

  return (
    <TemplateManagerContainer>
      <Header>
        <Title>Duties</Title>
        <AddButton onClick={() => {
          setShowAddForm(true);
          setFormMode('single');
        }}>
          <FiPlus size={16} />
          Add
        </AddButton>
      </Header>

      {error && (
        <ErrorMessage>
          {error}
        </ErrorMessage>
      )}

      {templates.length > 0 && (
        <TimelineContainer>
          <TimelineHeader>
            <span>Timeline View</span>
            <span>24 Hour Schedule</span>
          </TimelineHeader>

          <TimelineGrid>
            {Array.from({ length: 24 }, (_, i) => (
              <HourLabel key={i}>
                {i % 2 === 0 ? `${i}:00` : ''}
              </HourLabel>
            ))}
          </TimelineGrid>

          {templates.map((template) => (
            <TemplateBlock
              key={template.id}
              startHour={calculateStartHour(template.startTime)}
              duration={calculateDuration(template.startTime, template.endTime)}
              dutyType={template.dutyType}
              isOverlapping={hasOverlaps}
            >
              <TemplateInfo>
                {getDutyTypeIcon(template.dutyType)}
                <span>{formatTime(template.startTime)} - {formatTime(template.endTime)}</span>
              </TemplateInfo>
              <TemplateActions>
                <ActionButton onClick={() => handleDeleteTemplate(template.id)}>
                  <FiTrash2 size={10} />
                </ActionButton>
              </TemplateActions>
            </TemplateBlock>
          ))}
        </TimelineContainer>
      )}

      {templates.length > 0 ? (
        <TemplatesList>
          {templates.map((template) => (
            <TemplateItem 
              key={template.id} 
              dutyType={template.dutyType}
              title={`${formatTime(template.startTime)} - ${formatTime(template.endTime)}`}
            >
              <TemplateTime>
                {formatTime(template.startTime)} - {formatTime(template.endTime)}
              </TemplateTime>
              <DeleteButton 
                className="delete-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  handleDeleteTemplate(template.id);
                }}
              >

              </DeleteButton>
            </TemplateItem>
          ))}
        </TemplatesList>
      ) : (
        <EmptyState>
          <FiClock size={48} color={theme.colors.textMuted} style={{ marginBottom: theme.spacing.md }} />
          <h4 style={{ color: theme.colors.textPrimary, marginBottom: theme.spacing.sm }}>
            No Duty Templates
          </h4>
          <p>Add duty templates to define when trips, washing, or maintenance should occur.</p>
        </EmptyState>
      )}

      {/* Add Form Modal with Tabs */}
      {showAddForm && (
        <ModalOverlay onClick={() => setShowAddForm(false)}>
          <ModalContent onClick={(e) => e.stopPropagation()}>
            <ModalHeader>
              <ModalTitle>Add Duty</ModalTitle>
              <CloseButton onClick={() => setShowAddForm(false)}>
                <FiX size={20} />
              </CloseButton>
            </ModalHeader>

            <TabContainer>
              <Tab active={formMode === 'single'} onClick={() => setFormMode('single')}>
                Single
              </Tab>
              <Tab active={formMode === 'bulk'} onClick={() => setFormMode('bulk')}>
                Bulk
              </Tab>
            </TabContainer>

            {formMode === 'single' ? (
              <AddTemplateForm
                embedded={true}
                onClose={() => setShowAddForm(false)}
                onSave={(template) => {
                  handleAddTemplate(template);
                  setShowAddForm(false);
                }}
                existingTemplates={templates}
              />
            ) : (
              <BulkTemplateForm
                embedded={true}
                onClose={() => setShowAddForm(false)}
                onSave={(templates) => {
                  handleBulkAddTemplates(templates);
                  setShowAddForm(false);
                }}
                existingTemplates={templates}
              />
            )}
          </ModalContent>
        </ModalOverlay>
      )}
    </TemplateManagerContainer>
  );
};

export default TripTemplateManager;
