import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiPlus, FiEdit, FiTrash2, FiSearch, FiTruck, FiUser, FiClock, FiMapPin } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import AssignmentModal from '../../components/Modals/AssignmentModal';
import { 
  PageContainer, 
  PageHeader, 
  PageTitle, 
  HeaderActions,
  FiltersCard,
  SearchContainer,
  SearchInput,
  SearchIcon,
  TableCard,
  Table,
  TableHeader,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell,
  StatusBadge,
  ActionButtons,
  ActionButton,
  EmptyState,
  EmptyStateIcon
} from '../../components/Common/CommonStyles';
import { Trip, Route, Vehicle, Driver } from '../../types';
import { dataService } from '../../services/dataService';

// Custom components for assign actions
const AssignButton = ({ 
  isAssigned, 
  onClick, 
  icon: Icon, 
  assignedText, 
  unassignedText 
}: {
  isAssigned: boolean;
  onClick: () => void;
  icon: React.ComponentType<{ size?: number }>;
  assignedText: string;
  unassignedText: string;
}) => (
  <ActionButton 
    onClick={onClick}
    style={{
      background: isAssigned ? 'rgba(16, 185, 129, 0.1)' : 'rgba(107, 114, 128, 0.1)',
      color: isAssigned ? '#10b981' : '#6b7280',
      border: `1px solid ${isAssigned ? 'rgba(16, 185, 129, 0.2)' : 'rgba(107, 114, 128, 0.2)'}`,
      padding: '6px 12px',
      borderRadius: '6px',
      fontSize: '12px',
      display: 'flex',
      alignItems: 'center',
      gap: '4px'
    }}
  >
    <Icon size={12} />
    {isAssigned ? assignedText : unassignedText}
  </ActionButton>
);

const TripsList: React.FC = () => {
  const navigate = useNavigate();
  const [trips, setTrips] = useState<Trip[]>([]);
  const [routes, setRoutes] = useState<Route[]>([]);
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [filteredTrips, setFilteredTrips] = useState<Trip[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  // Modal states
  const [vehicleModalOpen, setVehicleModalOpen] = useState(false);
  const [driverModalOpen, setDriverModalOpen] = useState(false);
  const [selectedTripId, setSelectedTripId] = useState<string | null>(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        const [tripsData, routesData, vehiclesData, driversData] = await Promise.all([
          dataService.getTrips(),
          dataService.getRoutes(),
          dataService.getVehicles(),
          dataService.getDrivers()
        ]);

        setTrips(tripsData);
        setRoutes(routesData);
        setVehicles(vehiclesData);
        setDrivers(driversData);
        setFilteredTrips(tripsData);
      } catch (error) {
        console.error('Error loading data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    let filtered = trips;

    if (searchTerm) {
      filtered = filtered.filter(trip => {
        const route = routes.find(r => r.id === trip.routeId);
        const vehicle = vehicles.find(v => v.id === trip.scheduledVehicleId);
        const driver = drivers.find(d => d.id === trip.scheduledDriverId);

        return (
          route?.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          vehicle?.plateNo.toLowerCase().includes(searchTerm.toLowerCase()) ||
          driver?.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          trip.notes?.toLowerCase().includes(searchTerm.toLowerCase())
        );
      });
    }

    setFilteredTrips(filtered);
  }, [trips, routes, vehicles, drivers, searchTerm]);

  const handleCreateTrip = () => {
    navigate('/trips/create');
  };

  const handleEditTrip = (tripId: string) => {
    navigate(`/trips/edit/${tripId}`);
  };

  const handleDeleteTrip = (tripId: string) => {
    if (window.confirm('Are you sure you want to delete this trip?')) {
      setTrips(prev => prev.filter(trip => trip.id !== tripId));
    }
  };

  const handleAssignVehicle = (tripId: string) => {
    setSelectedTripId(tripId);
    setVehicleModalOpen(true);
  };

  const handleAssignDriver = (tripId: string) => {
    setSelectedTripId(tripId);
    setDriverModalOpen(true);
  };

  const handleVehicleAssignment = (vehicle: Vehicle) => {
    if (selectedTripId) {
      setTrips(prev => prev.map(trip => 
        trip.id === selectedTripId 
          ? { ...trip, scheduledVehicleId: vehicle.id }
          : trip
      ));
    }
    setVehicleModalOpen(false);
    setSelectedTripId(null);
  };

  const handleDriverAssignment = (driver: Driver) => {
    if (selectedTripId) {
      setTrips(prev => prev.map(trip => 
        trip.id === selectedTripId 
          ? { ...trip, scheduledDriverId: driver.id }
          : trip
      ));
    }
    setDriverModalOpen(false);
    setSelectedTripId(null);
  };

  const getRouteInfo = (routeId: string) => {
    return routes.find(r => r.id === routeId);
  };

  const getVehicleInfo = (vehicleId?: string) => {
    return vehicleId ? vehicles.find(v => v.id === vehicleId) : null;
  };

  const getDriverInfo = (driverId?: string) => {
    return driverId ? drivers.find(d => d.id === driverId) : null;
  };

  const formatDateTime = (date: Date | string) => {
    const dateObj = typeof date === 'string' ? new Date(date) : date;
    return new Intl.DateTimeFormat('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(dateObj);
  };

  if (loading) {
    return <div>Loading trips...</div>;
  }

  return (
    <PageContainer className="fade-in">
      <PageHeader>
        <PageTitle>Trips</PageTitle>
        <HeaderActions>
          <Button variant="primary" onClick={handleCreateTrip}>
            <FiPlus />
            Create Trip
          </Button>
        </HeaderActions>
      </PageHeader>

      <FiltersCard>
        <SearchContainer>
          <SearchIcon>
            <FiSearch size={18} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search trips..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>
      </FiltersCard>

      {filteredTrips.length === 0 ? (
        <EmptyState>
          <EmptyStateIcon>ðŸš›</EmptyStateIcon>
          <h3>No trips found</h3>
          <p>Try adjusting your search criteria or create a new trip.</p>
          <Button variant="primary" onClick={handleCreateTrip} style={{ marginTop: '16px' }}>
            <FiPlus />
            Create Trip
          </Button>
        </EmptyState>
      ) : (
        <TableCard>
          <Table>
            <TableHeader>
              <tr>
                <TableHeaderCell>Route</TableHeaderCell>
                <TableHeaderCell>Schedule</TableHeaderCell>
                <TableHeaderCell>Vehicle</TableHeaderCell>
                <TableHeaderCell>Driver</TableHeaderCell>
                <TableHeaderCell>Status</TableHeaderCell>
                <TableHeaderCell>Actions</TableHeaderCell>
              </tr>
            </TableHeader>
            <TableBody>
              {filteredTrips.map((trip) => {
                const route = getRouteInfo(trip.routeId);
                const vehicle = getVehicleInfo(trip.scheduledVehicleId);
                const driver = getDriverInfo(trip.scheduledDriverId);

                return (
                  <TableRow key={trip.id}>
                    <TableCell>
                      {route ? (
                        <div>
                          <div style={{ fontWeight: 'medium' }}>{route.name}</div>
                          <div style={{ 
                            fontSize: '12px', 
                            color: '#6b7280',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '4px',
                            marginTop: '2px'
                          }}>
                            <FiMapPin size={10} />
                            {/* {route.startLocation.address} â†’ {route.endLocation.address} */}
                          </div>
                        </div>
                      ) : (
                        <span style={{ color: '#6b7280' }}>Route not found</span>
                      )}
                    </TableCell>
                    <TableCell>
                      <div style={{ display: 'flex', flexDirection: 'column', gap: '2px' }}>
                        <div style={{ 
                          fontSize: '12px', 
                          display: 'flex', 
                          alignItems: 'center', 
                          gap: '4px' 
                        }}>
                          <FiClock size={10} />
                          Start: {formatDateTime(trip.scheduledStartTime)}
                        </div>
                        <div style={{ 
                          fontSize: '12px', 
                          display: 'flex', 
                          alignItems: 'center', 
                          gap: '4px',
                          color: '#6b7280'
                        }}>
                          <FiClock size={10} />
                          End: {formatDateTime(trip.scheduledEndTime)}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      {vehicle ? (
                        <div>
                          <div style={{ fontWeight: 'medium' }}>{vehicle.plateNo}</div>
                          <div style={{ fontSize: '12px', color: '#6b7280' }}>
                            {vehicle.plateNo} {vehicle.fleetNo}
                          </div>
                        </div>
                      ) : (
                        <AssignButton
                          isAssigned={false}
                          onClick={() => handleAssignVehicle(trip.id)}
                          icon={FiTruck}
                          assignedText="Assigned"
                          unassignedText="Assign Vehicle"
                        />
                      )}
                    </TableCell>
                    <TableCell>
                        {driver ? (
                          <div>
                            <div style={{ fontWeight: 'medium' }}>{driver.name}</div>
                            <div style={{ fontSize: '12px', color: '#6b7280' }}>
                              QID: {driver.qid}
                            </div>
                          </div>
                      ) : (
                        <AssignButton
                          isAssigned={false}
                          onClick={() => handleAssignDriver(trip.id)}
                          icon={FiUser}
                          assignedText="Assigned"
                          unassignedText="Assign Driver"
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      <StatusBadge status={trip.status}>
                        {trip.status}
                      </StatusBadge>
                    </TableCell>
                    <TableCell>
                      <ActionButtons>
                        <ActionButton onClick={() => handleEditTrip(trip.id)}>
                          <FiEdit size={16} />
                        </ActionButton>
                        <ActionButton onClick={() => handleDeleteTrip(trip.id)}>
                          <FiTrash2 size={16} />
                        </ActionButton>
                      </ActionButtons>
                    </TableCell>
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableCard>
      )}

      {/* Vehicle Assignment Modal */}
      <AssignmentModal
        isOpen={vehicleModalOpen}
        onClose={() => {
          setVehicleModalOpen(false);
          setSelectedTripId(null);
        }}
        title="Assign Vehicle"
        items={vehicles}
        selectedItemId={selectedTripId ? getVehicleInfo(trips.find(t => t.id === selectedTripId)?.scheduledVehicleId)?.id : undefined}
        onSelect={handleVehicleAssignment}
        searchPlaceholder="Search vehicles..."
        getItemId={(vehicle) => vehicle.id}
        getItemTitle={(vehicle) => `${vehicle.plateNo} - ${vehicle.fleetNo}`}
        getItemSubtitle={(vehicle) => `${vehicle.make} ${vehicle.model} (${vehicle.year})`}
        searchFilter={(vehicle, searchTerm) =>
          vehicle.plateNo.toLowerCase().includes(searchTerm.toLowerCase()) ||
          vehicle.fleetNo.toLowerCase().includes(searchTerm.toLowerCase()) ||
          vehicle.make.toLowerCase().includes(searchTerm.toLowerCase()) ||
          vehicle.model.toLowerCase().includes(searchTerm.toLowerCase())
        }
      />

      {/* Driver Assignment Modal */}
      <AssignmentModal
        isOpen={driverModalOpen}
        onClose={() => {
          setDriverModalOpen(false);
          setSelectedTripId(null);
        }}
        title="Assign Driver"
        items={drivers}
        selectedItemId={selectedTripId ? getDriverInfo(trips.find(t => t.id === selectedTripId)?.scheduledDriverId)?.id : undefined}
        onSelect={handleDriverAssignment}
        searchPlaceholder="Search drivers..."
        getItemId={(driver) => driver.id}
        getItemTitle={(driver) => driver.name}
        getItemSubtitle={(driver) => `QID: ${driver.qid} â€¢ Phone: ${driver.phone}`}
        searchFilter={(driver, searchTerm) =>
          driver.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          driver.qid.toLowerCase().includes(searchTerm.toLowerCase()) ||
          driver.phone.toLowerCase().includes(searchTerm.toLowerCase()) ||
          driver.username.toLowerCase().includes(searchTerm.toLowerCase())
        }
      />
    </PageContainer>
  );
};

export default TripsList;
