/**
 * MultiSelectSearchDropdown Component
 * 
 * A reusable multi-select search dropdown that extends the SearchDropdown functionality
 * to support multiple selections with checkboxes.
 * 
 * @example
 * // For selecting multiple drivers
 * <MultiSelectSearchDropdown
 *   values={selectedDrivers}
 *   onChange={setSelectedDrivers}
 *   onSearch={async (term) => await driverService.searchDrivers(term)}
 *   displayValue={(driver) => `${driver.firstName} ${driver.lastName}`}
 *   renderItem={(driver) => (
 *     <div>
 *       <div className="driver-name">{driver.firstName} {driver.lastName}</div>
 *       <div className="driver-status">{driver.status}</div>
 *     </div>
 *   )}
 *   placeholder="Search for drivers..."
 * />
 */
export interface MultiSelectSearchDropdownProps<T> {
  values: T[];
  onChange: (items: T[]) => void;
  onSearch: (searchTerm: string) => Promise<T[]>;
  displayValue: (item: T) => string;
  renderItem: (item: T) => React.ReactNode;
  placeholder?: string;
  hasError?: boolean;
  disabled?: boolean;
  debounceMs?: number;
  minSearchLength?: number;
  noResultsMessage?: string;
  searchPromptMessage?: string;
  maxHeight?: string;
}

function MultiSelectSearchDropdown<T>({
  values,
  onChange,
  onSearch,
  displayValue,
  renderItem,
  placeholder = "Search...",
  hasError = false,
  disabled = false,
  debounceMs = 300,
  minSearchLength = 1,
  noResultsMessage = "No results found",
  searchPromptMessage = "Start typing to search",
  maxHeight = "200px"
}: MultiSelectSearchDropdownProps<T>) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [options, setOptions] = useState<T[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const [showSelectedItems, setShowSelectedItems] = useState(false);

  const dropdownRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Search with debouncing
  useEffect(() => {
    if (!searchTerm.trim() || searchTerm.length < minSearchLength) {
      setOptions([]);
      setHasSearched(false);
      return;
    }

    const searchTimeout = setTimeout(async () => {
      setIsLoading(true);
      try {
        const results = await onSearch(searchTerm);
        setOptions(results);
        setHasSearched(true);
      } catch (error) {
        console.error('Error searching:', error);
        setOptions([]);
      } finally {
        setIsLoading(false);
      }
    }, debounceMs);

    return () => clearTimeout(searchTimeout);
  }, [searchTerm, onSearch, debounceMs, minSearchLength]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newSearchTerm = e.target.value;
    setSearchTerm(newSearchTerm);

    if (!isOpen) {
      setIsOpen(true);
    }
  };

  const handleOptionToggle = (item: T) => {
    const isSelected = values.some(value => JSON.stringify(value) === JSON.stringify(item));

    if (isSelected) {
      // Remove item
      onChange(values.filter(value => JSON.stringify(value) !== JSON.stringify(item)));
    } else {
      // Add item
      onChange([...values, item]);
    }
  };

  const handleRemoveItem = (itemToRemove: T) => {
    onChange(values.filter(item => JSON.stringify(item) !== JSON.stringify(itemToRemove)));
  };

  const handleClear = () => {
    onChange([]);
    setSearchTerm('');
    setOptions([]);
    setHasSearched(false);
    setIsOpen(false);
  };

  const handleInputFocus = () => {
    if (!disabled) {
      setIsOpen(true);
    }
  };

  const isItemSelected = (item: T) => {
    return values.some(value => JSON.stringify(value) === JSON.stringify(item));
  };

  const currentDisplayValue = values.length > 0 
    ? `${values.length} item${values.length > 1 ? 's' : ''} selected`
    : '';

  return (
    <DropdownContainer ref={dropdownRef}>
      <DropdownInput
        ref={inputRef}
        type="text"
        value={isOpen ? searchTerm : currentDisplayValue}
        onChange={handleInputChange}
        onFocus={handleInputFocus}
        placeholder={placeholder}
        hasError={hasError}
        disabled={disabled}
      />
      <SearchIcon>
        <FiSearch size={16} />
      </SearchIcon>

      {values.length > 0 && !isOpen && (
        <>
          <SelectedItemsToggle onClick={() => setShowSelectedItems(!showSelectedItems)}>
            {showSelectedItems ? <FiEyeOff size={16} /> : <FiEye size={16} />}
          </SelectedItemsToggle>
          <SelectedCountBadge>{values.length}</SelectedCountBadge>
          <ClearButton onClick={handleClear} type="button">
            <FiX size={16} />
          </ClearButton>
        </>
      )}

      {values.length === 0 && !isOpen && (
        <DropdownButton onClick={() => setIsOpen(true)} disabled={disabled}>
          <FiChevronDown size={16} />
        </DropdownButton>
      )}

      {isOpen && (
        <DropdownList style={{ maxHeight }}>
          {isLoading ? (
            <LoadingSpinner>
              <div className="spinner" />
              Searching...
            </LoadingSpinner>
          ) : options.length > 0 ? (
            options.map((item, index) => (
              <DropdownItem
                key={index}
                onClick={() => handleOptionToggle(item)}
                selected={isItemSelected(item)}
              >
                <Checkbox
                  type="checkbox"
                  checked={isItemSelected(item)}
                  onChange={() => handleOptionToggle(item)}
                />
                {renderItem(item)}
              </DropdownItem>
            ))
          ) : hasSearched ? (
            <NoResultsMessage>
              {noResultsMessage}
            </NoResultsMessage>
          ) : (
            <NoResultsMessage>
              {searchPromptMessage}
            </NoResultsMessage>
          )}
        </DropdownList>
      )}

      {values.length > 0 && !isOpen && (
        <SelectedItemsPanel isVisible={showSelectedItems}>
          <SelectedItemsGrid>
            {values.map((item, index) => (
              <SelectedItemCard key={index}>
                <span>{displayValue(item)}</span>
                <RemoveItemButton onClick={() => handleRemoveItem(item)}>
                  <FiX size={10} />
                </RemoveItemButton>
              </SelectedItemCard>
            ))}
          </SelectedItemsGrid>
        </SelectedItemsPanel>
      )}
    </DropdownContainer>
  );
}

export default MultiSelectSearchDropdown;
