import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiPlus, FiCalendar, FiFilter } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  PageContainer, 
  PageHeader, 
  PageTitle, 
  HeaderActions,
  SearchContainer,
  SearchInput,
  Table,
  TableHeader,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell,
  ActionButtons,
  ActionButton
} from '../../components/Common/CommonStyles';
import styled from 'styled-components';
import { theme } from '../../styles/theme';
import AssignmentInterface from './AssignmentInterface';
import { Duty, Route, Driver, Vehicle } from '../../types';
import { dataService } from '../../services/dataService';

const TabsContainer = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  border-bottom: 1px solid ${theme.colors.border};
  margin-bottom: ${theme.spacing.lg};
`;

const Tab = styled.button<{ active: boolean }>`
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  background: transparent;
  border: none;
  border-bottom: 2px solid ${({ active }) => active ? theme.colors.primary : 'transparent'};
  color: ${({ active }) => active ? theme.colors.primary : theme.colors.textSecondary};
  font-weight: ${({ active }) => active ? theme.typography.fontWeight.semibold : theme.typography.fontWeight.medium};
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    color: ${theme.colors.primary};
  }
`;

const FiltersContainer = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.lg};
  flex-wrap: wrap;
  align-items: center;
`;

const DateSelector = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const DateInput = styled.input`
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.card};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
  }
`;

const FilterSelect = styled.select`
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.card};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
  }
`;

const StatusBadge = styled.span<{ status: string }>`
  display: inline-flex;
  align-items: center;
  padding: ${theme.spacing.xs} ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.xs};
  font-weight: ${theme.typography.fontWeight.medium};
  background: ${({ status }) => 
    status === 'in-progress' ? theme.colors.success :
    status === 'completed' ? theme.colors.info :
    theme.colors.surface};
  color: white;
`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: ${theme.spacing.lg};
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
`;

const PaginationInfo = styled.div`
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.fontSize.sm};
`;

const PaginationButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.sm};
`;

const PageButton = styled.button<{ active?: boolean; disabled?: boolean }>`
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${({ active, disabled }) => 
    disabled ? theme.colors.surface :
    active ? theme.colors.primary :
    theme.colors.card};
  color: ${({ active, disabled }) => 
    disabled ? theme.colors.textDisabled :
    active ? 'white' :
    theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
  cursor: ${({ disabled }) => disabled ? 'not-allowed' : 'pointer'};

  &:hover {
    background: ${({ active, disabled }) => 
      disabled ? theme.colors.surface :
      active ? theme.colors.primaryHover :
      theme.colors.cardHover};
  }
`;

const DutiesList: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<'trips' | 'assignment'>('trips');
  const [selectedDate, setSelectedDate] = useState(() => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  });
  const [duties, setDuties] = useState<Duty[]>([]);
  const [routes, setRoutes] = useState<Route[]>([]);
  const [filteredDuties, setFilteredDuties] = useState<Duty[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [routeFilter, setRouteFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(20);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDuties();
  }, [selectedDate]);

  useEffect(() => {
    filterDuties();
  }, [duties, searchTerm, routeFilter, statusFilter]);

  const filterTrips = () => {
    let filtered = [...trips];

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(trip =>
        trip.tripNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
        trip.routeName.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Route filter
    if (routeFilter !== 'all') {
      filtered = filtered.filter(trip => trip.routeName === routeFilter);
    }

    // Status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(trip => trip.status === statusFilter);
    }

    setFilteredTrips(filtered);
    setCurrentPage(1); // Reset to first page on filter change
  };

  const loadTrips = async () => {
    try {
      setLoading(true);
      // TODO: Replace with actual API call
      // const response = await apiService.getTrips({ date: selectedDate });

      // Sample data - generating more trips for testing pagination
      const routes = [
        'Route A - Downtown Loop',
        'Route B - Airport Express',
        'Route C - University Line',
        'Route D - Shopping Mall'
      ];

      const drivers = ['John Doe', 'Jane Smith', 'Mike Johnson', 'Sarah Williams', 'David Brown'];
      const vehicles = ['Bus 101', 'Bus 102', 'Bus 103', 'Bus 104', 'Bus 105', 'Bus 106'];

      const sampleTrips: Trip[] = [];

      // Generate 50 sample trips
      for (let i = 1; i <= 50; i++) {
        const hour = String(6 + Math.floor(i / 8)).padStart(2, '0');
        const minute = String((i * 15) % 60).padStart(2, '0');
        const nextHour = String(Math.min(22, 6 + Math.floor(i / 8))).padStart(2, '0');

        sampleTrips.push({
          id: String(i),
          tripNumber: `T-${String(i).padStart(3, '0')}`,
          routeName: routes[Math.floor(Math.random() * routes.length)],
          scheduledStartTime: `${hour}:${minute}`,
          scheduledEndTime: `${nextHour}:${minute}`,
          status: ['planned', 'in-progress', 'completed'][Math.floor(Math.random() * 3)] as any,
          assignedDriver: drivers[Math.floor(Math.random() * drivers.length)],
          assignedVehicle: vehicles[Math.floor(Math.random() * vehicles.length)]
        });
      }

      setTrips(sampleTrips);
    } catch (error) {
      console.error('Error loading trips:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateDuty = () => {
    navigate('/duties/create');
  };

  const formatTime = (time: string) => {
    return time;
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'in-progress':
        return 'In Progress';
      case 'completed':
        return 'Completed';
      default:
        return 'Planned';
    }
  };

  // Get unique routes for filter dropdown
  const uniqueRoutes = Array.from(new Set(trips.map(t => t.routeName)));

  // Calculate pagination
  const totalPages = Math.ceil(filteredTrips.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentTrips = filteredTrips.slice(startIndex, endIndex);

  const renderTripsTab = () => (
    <>
      <FiltersContainer>
        <DateSelector>
          <FiCalendar size={20} color={theme.colors.primary} />
          <DateInput
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </DateSelector>

        <SearchContainer style={{ flex: 1, minWidth: '200px' }}>
          <SearchInput
            type="text"
            placeholder="Search trips..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>

        <FilterSelect value={routeFilter} onChange={(e) => setRouteFilter(e.target.value)}>
          <option value="all">All Routes</option>
          {uniqueRoutes.map(route => (
            <option key={route} value={route}>{route}</option>
          ))}
        </FilterSelect>

        <FilterSelect value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
          <option value="all">All Status</option>
          <option value="planned">Planned</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </FilterSelect>
      </FiltersContainer>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '40px' }}>
          <p style={{ color: theme.colors.textSecondary }}>Loading trips...</p>
        </div>
      ) : filteredTrips.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '40px' }}>
          <p style={{ color: theme.colors.textSecondary }}>
            No trips found for {new Date(selectedDate).toLocaleDateString()}
          </p>
        </div>
      ) : (
        <>
          <Table>
            <TableHeader>
              <TableHeaderCell>Time</TableHeaderCell>
              <TableHeaderCell>Trip #</TableHeaderCell>
              <TableHeaderCell>Route</TableHeaderCell>
              <TableHeaderCell>Driver</TableHeaderCell>
              <TableHeaderCell>Vehicle</TableHeaderCell>
              <TableHeaderCell>Status</TableHeaderCell>
            </TableHeader>
            <TableBody>
              {currentTrips.map((trip) => (
                <TableRow key={trip.id}>
                  <TableCell>
                    {formatTime(trip.scheduledStartTime)} - {formatTime(trip.scheduledEndTime)}
                  </TableCell>
                  <TableCell>{trip.tripNumber}</TableCell>
                  <TableCell>{trip.routeName}</TableCell>
                  <TableCell>{trip.assignedDriver || '-'}</TableCell>
                  <TableCell>{trip.assignedVehicle || '-'}</TableCell>
                  <TableCell>
                    <StatusBadge status={trip.status}>
                      {getStatusLabel(trip.status)}
                    </StatusBadge>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>

          <PaginationContainer>
            <PaginationInfo>
              Showing {startIndex + 1} to {Math.min(endIndex, filteredTrips.length)} of {filteredTrips.length} trips
            </PaginationInfo>
            <PaginationButtons>
              <PageButton 
                disabled={currentPage === 1}
                onClick={() => setCurrentPage(currentPage - 1)}
              >
                Previous
              </PageButton>
              {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                let pageNum = currentPage;
                if (totalPages <= 5) {
                  pageNum = i + 1;
                } else if (currentPage <= 3) {
                  pageNum = i + 1;
                } else if (currentPage >= totalPages - 2) {
                  pageNum = totalPages - 4 + i;
                } else {
                  pageNum = currentPage - 2 + i;
                }
                return (
                  <PageButton
                    key={pageNum}
                    active={currentPage === pageNum}
                    onClick={() => setCurrentPage(pageNum)}
                  >
                    {pageNum}
                  </PageButton>
                );
              })}
              <PageButton 
                disabled={currentPage === totalPages}
                onClick={() => setCurrentPage(currentPage + 1)}
              >
                Next
              </PageButton>
            </PaginationButtons>
          </PaginationContainer>
        </>
      )}
    </>
  );

  const renderAssignmentTab = () => (
    <AssignmentInterface />
  );

  return (
    <PageContainer className="fade-in">
      <PageHeader>
        <PageTitle>Duties Management</PageTitle>
        <HeaderActions>
          <Button onClick={handleCreateDuty} variant="primary">
            <FiPlus style={{ marginRight: '8px' }} />
            Generate Trips
          </Button>
        </HeaderActions>
      </PageHeader>

      <TabsContainer>
        <Tab 
          active={activeTab === 'trips'} 
          onClick={() => setActiveTab('trips')}
        >
          Trips List
        </Tab>
        <Tab 
          active={activeTab === 'assignment'} 
          onClick={() => setActiveTab('assignment')}
        >
          Assignment
        </Tab>
      </TabsContainer>

      <div>
        {activeTab === 'trips' ? renderTripsTab() : renderAssignmentTab()}
      </div>
    </PageContainer>
  );
};

export default DutiesList;
