const Stops: React.FC = () => {
  const navigate = useNavigate();
  const [stops, setStops] = useState<Stop[]>([]);
  const [filteredStops, setFilteredStops] = useState<Stop[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Simulate loading stops
    const loadStops = async () => {
      try {
        await new Promise(resolve => setTimeout(resolve, 500));
        setStops(mockStops);
        setFilteredStops(mockStops);
      } catch (error) {
        console.error('Error loading stops:', error);
      } finally {
        setLoading(false);
      }
    };

    loadStops();
  }, []);

  useEffect(() => {
    let filtered = stops;

    if (searchTerm) {
      filtered = filtered.filter(stop =>
        stop.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        stop.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
        stop.description?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredStops(filtered);
  }, [stops, searchTerm]);

  const handleCreateStop = () => {
    navigate('/stops/create');
  };

  const handleEditStop = (stopId: string) => {
    navigate(`/stops/edit/${stopId}`);
  };

  const handleDeleteStop = (stopId: string) => {
    if (window.confirm('Are you sure you want to delete this stop?')) {
      setStops(prev => prev.filter(stop => stop.id !== stopId));
    }
  };

  if (loading) {
    return <div>Loading stops...</div>;
  }

  return (
    <StopsContainer className="fade-in">
      <PageHeader>
        <PageTitle>Stops</PageTitle>
        <HeaderActions>
          <Button variant="primary" onClick={handleCreateStop}>
            <FiPlus />
            Create Stop
          </Button>
        </HeaderActions>
      </PageHeader>

      <FiltersCard>
        <SearchContainer>
          <SearchIcon>
            <FiSearch size={18} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search stops..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>
      </FiltersCard>

      {filteredStops.length === 0 ? (
        <EmptyState>
          <EmptyStateIcon>üìç</EmptyStateIcon>
          <h3>No stops found</h3>
          <p>Create your first stop to get started with route planning.</p>
          <Button variant="primary" onClick={handleCreateStop} style={{ marginTop: '16px' }}>
            <FiPlus />
            Create Stop
          </Button>
        </EmptyState>
      ) : (
        <StopsGrid>
          {filteredStops.map((stop) => (
            <StopCard key={stop.id}>
              <StopHeader>
                <StopInfo>
                  <StopName>{stop.name}</StopName>
                  <StopCode>{stop.code}</StopCode>
                </StopInfo>
                <StopActions>
                  <ActionButton onClick={() => handleEditStop(stop.id)}>
                    <FiEdit size={16} />
                  </ActionButton>
                  <ActionButton onClick={() => handleDeleteStop(stop.id)}>
                    <FiTrash2 size={16} />
                  </ActionButton>
                </StopActions>
              </StopHeader>

              <StopDetails>
                <DetailRow>
                  <DetailLabel>Type</DetailLabel>
                  <TypeBadge stopType={stop.type}>
                    {stop.type}
                  </TypeBadge>
                </DetailRow>

                {stop.description && (
                  <DetailRow>
                    <DetailLabel>Description</DetailLabel>
                    <DetailValue>{stop.description}</DetailValue>
                  </DetailRow>
                )}
              </StopDetails>

              <LocationInfo>
                <LocationIcon>
                  <FiMapPin size={16} />
                </LocationIcon>
                <LocationText>
                  <Coordinates>
                    {stop.location.lat.toFixed(4)}, {stop.location.lng.toFixed(4)}
                  </Coordinates>
                  {stop.address && <Address>{stop.address}</Address>}
                </LocationText>
              </LocationInfo>
            </StopCard>
          ))}
        </StopsGrid>
      )}
    </StopsContainer>
  );
};

export default Stops;
