import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiX, FiSearch, FiCheck } from 'react-icons/fi';
import { theme } from '../../styles/theme';
import { Button } from '../../styles/GlobalStyles';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: ${theme.colors.surface};
  border-radius: ${theme.borderRadius.lg};
  width: 90%;
  max-width: 600px;
  max-height: 80vh;
  display: flex;
  flex-direction: column;
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
`;

const ModalHeader = styled.div`
  padding: ${theme.spacing.lg};
  border-bottom: 1px solid ${theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  font-size: ${theme.typography.fontSize.xl};
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textPrimary};
  margin: 0;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: ${theme.colors.textSecondary};
  cursor: pointer;
  padding: ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.md};
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${theme.colors.surfaceHover};
    color: ${theme.colors.textPrimary};
  }
`;

const ModalBody = styled.div`
  padding: ${theme.spacing.lg};
  flex: 1;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const SearchContainer = styled.div`
  position: relative;
  margin-bottom: ${theme.spacing.lg};
`;

const SearchInput = styled.input`
  width: 100%;
  padding: ${theme.spacing.md} ${theme.spacing.md} ${theme.spacing.md} 40px;
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.background};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }

  &::placeholder {
    color: ${theme.colors.textSecondary};
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  color: ${theme.colors.textSecondary};
  pointer-events: none;
`;

const ItemsList = styled.div`
  flex: 1;
  overflow-y: auto;
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.background};
`;

const ItemRow = styled.div<{ selected?: boolean }>`
  padding: ${theme.spacing.md};
  border-bottom: 1px solid ${theme.colors.border};
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: ${({ selected }) => selected ? 'rgba(59, 130, 246, 0.1)' : 'transparent'};

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    background: ${({ selected }) => selected ? 'rgba(59, 130, 246, 0.15)' : theme.colors.surfaceHover};
  }
`;

const ItemInfo = styled.div`
  flex: 1;
`;

const ItemTitle = styled.div`
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
  margin-bottom: 2px;
`;

const ItemSubtitle = styled.div`
  font-size: ${theme.typography.fontSize.xs};
  color: ${theme.colors.textSecondary};
`;

const CheckIcon = styled.div`
  color: ${theme.colors.primary};
  display: flex;
  align-items: center;
`;

const ModalFooter = styled.div`
  padding: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
`;

const EmptyState = styled.div`
  padding: ${theme.spacing.xl};
  text-align: center;
  color: ${theme.colors.textSecondary};
`;

interface AssignmentModalProps<T> {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  items: T[];
  selectedItemId?: string;
  onSelect: (item: T) => void;
  searchPlaceholder: string;
  getItemId: (item: T) => string;
  getItemTitle: (item: T) => string;
  getItemSubtitle: (item: T) => string;
  searchFilter: (item: T, searchTerm: string) => boolean;
}

function AssignmentModal<T>({
  isOpen,
  onClose,
  title,
  items,
  selectedItemId,
  onSelect,
  searchPlaceholder,
  getItemId,
  getItemTitle,
  getItemSubtitle,
  searchFilter
}: AssignmentModalProps<T>) {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredItems, setFilteredItems] = useState<T[]>(items);
  const [localSelectedId, setLocalSelectedId] = useState<string | undefined>(selectedItemId);

  useEffect(() => {
    if (searchTerm) {
      setFilteredItems(items.filter(item => searchFilter(item, searchTerm)));
    } else {
      setFilteredItems(items);
    }
  }, [items, searchTerm, searchFilter]);

  useEffect(() => {
    setLocalSelectedId(selectedItemId);
  }, [selectedItemId]);

  const handleItemClick = (item: T) => {
    setLocalSelectedId(getItemId(item));
  };

  const handleConfirm = () => {
    const selectedItem = items.find(item => getItemId(item) === localSelectedId);
    if (selectedItem) {
      onSelect(selectedItem);
    }
    onClose();
  };

  const handleCancel = () => {
    setLocalSelectedId(selectedItemId);
    setSearchTerm('');
    onClose();
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay onClick={handleCancel}>
      <ModalContent onClick={(e) => e.stopPropagation()}>
        <ModalHeader>
          <ModalTitle>{title}</ModalTitle>
          <CloseButton onClick={handleCancel}>
            <FiX size={20} />
          </CloseButton>
        </ModalHeader>

        <ModalBody>
          <SearchContainer>
            <SearchIcon>
              <FiSearch size={16} />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder={searchPlaceholder}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </SearchContainer>

          <ItemsList>
            {filteredItems.length === 0 ? (
              <EmptyState>
                {searchTerm ? 'No items match your search' : 'No items available'}
              </EmptyState>
            ) : (
              filteredItems.map((item) => {
                const itemId = getItemId(item);
                const isSelected = itemId === localSelectedId;

                return (
                  <ItemRow
                    key={itemId}
                    selected={isSelected}
                    onClick={() => handleItemClick(item)}
                  >
                    <ItemInfo>
                      <ItemTitle>{getItemTitle(item)}</ItemTitle>
                      <ItemSubtitle>{getItemSubtitle(item)}</ItemSubtitle>
                    </ItemInfo>
                    {isSelected && (
                      <CheckIcon>
                        <FiCheck size={16} />
                      </CheckIcon>
                    )}
                  </ItemRow>
                );
              })
            )}
          </ItemsList>
        </ModalBody>

        <ModalFooter>
          <Button variant="secondary" onClick={handleCancel}>
            Cancel
          </Button>
          <Button 
            variant="primary" 
            onClick={handleConfirm}
            disabled={!localSelectedId}
          >
            Assign
          </Button>
        </ModalFooter>
      </ModalContent>
    </ModalOverlay>
  );
}

export default AssignmentModal;
