// Component to fit all available stops into view
const FitAllStopsHandler: React.FC<{ stops: Stop[] }> = ({ stops }) => {
  const map = useMap();

  useEffect(() => {
    if (stops.length > 0) {
      const bounds = L.latLngBounds(
        stops.map(stop => [stop.location.lat, stop.location.lon])
      );
      map.fitBounds(bounds, { padding: [20, 20], maxZoom: 16 });
    }
  }, [map, stops]);

  return null;
};

// Component to handle map clicks for adding new stops
const MapAddingModeHandler: React.FC<{ isAddingMode: boolean; onMapClick: (lat: number, lng: number) => void }> = ({ isAddingMode, onMapClick }) => {
  const map = useMap();

  useEffect(() => {
    if (isAddingMode) {
      map.on('click', (e) => {
        onMapClick(e.latlng.lat, e.latlng.lng);
      });
    }
    return () => {
      map.off('click');
    };
  }, [isAddingMode, onMapClick, map]);

  return null;
};

interface RouteMapProps {
  stops: Stop[];
  selectedStops: Stop[];
  center?: { lat: number; lng: number };
  zoom?: number;
  showRoute?: boolean;
  height?: string;
  onStopClick?: (stop: Stop) => void;
  onFitRoute?: () => void;
  showAllStops?: boolean;
  onToggleShowAllStops?: () => void;
  searchTerm?: string;
  onSearchChange?: (term: string) => void;
  filteredStops?: Stop[];
  onAddStop?: (stop: Stop) => void;
  onRemoveStop?: (stopId: string) => void;
  isAddingMode?: boolean;
  onMapClick?: (lat: number, lng: number) => void;
  onToggleAddingMode?: () => void;
  precalculatedRouteData?: any;  // Pre-calculated route data (e.g., from editing)
}

const RouteMap: React.FC<RouteMapProps> = ({ 
  stops,
  selectedStops,
  center,
  zoom = 12,
  showRoute = true,
  height,
  onStopClick,
  onFitRoute,
  showAllStops = false,
  onToggleShowAllStops,
  searchTerm = '',
  onSearchChange,
  filteredStops = [],
  onAddStop,
  onRemoveStop,
  isAddingMode,
  onMapClick,
  onToggleAddingMode,
  precalculatedRouteData
}) => {
  // Default center - Qatar (Doha) if no center provided
  const defaultCenter = center || { lat: 25.2854, lng: 51.5310 };

  // State for route data
  const [routeData, setRouteData] = useState<RouteResponse | null>(null);
  const [isCalculatingRoute, setIsCalculatingRoute] = useState(false);
  const [isSearchDropdownOpen, setIsSearchDropdownOpen] = useState(false);

  // Calculate route when selected stops change
  useEffect(() => {
    // Create an AbortController for this effect
    const abortController = new AbortController();
    let timeoutId: NodeJS.Timeout;

    if (selectedStops.length >= 2 && showRoute) {
      // If we have precalculated route data (e.g., from editing), skip ORS calculation
      if (precalculatedRouteData?.hasSegments && precalculatedRouteData?.segments) {
        console.log('Using precalculated route data, skipping ORS API call');
        // For editing - just mark route as calculated, don't recalculate
        setIsCalculatingRoute(false);
        // Decode polyline and set routeData
        const decodedCoordinates = polyline.decode(precalculatedRouteData.segments);
        setRouteData({
          coordinates: decodedCoordinates.map(coord => ({ lat: coord[0], lng: coord[1] })),
          distance: precalculatedRouteData.distance,
          duration: precalculatedRouteData.duration
        });
        // Return to skip the rest of the calculation
        return;
      }

      setIsCalculatingRoute(true);

      const waypoints: RouteCoordinate[] = selectedStops.map(stop => ({
        lat: stop.location.lat,
        lng: stop.location.lon
      }));

      // Create a stable key for this route calculation
    //   const routeKey = waypoints.map(w => `${w.lat.toFixed(6)},${w.lng.toFixed(6)}`).join('|');

      // Add a longer delay to avoid excessive API calls during hot reload
      timeoutId = setTimeout(async () => {
        // Check if component is still mounted and request not aborted
        if (abortController.signal.aborted) {
          return;
        }

        try {
          const route = await RoutingService.calculateRoute(waypoints, abortController.signal);

          // Check again if component is still mounted
          if (!abortController.signal.aborted) {
            setRouteData(route);
            setIsCalculatingRoute(false);
          }
        } catch (error) {
          // Only handle error if not aborted
          if (!abortController.signal.aborted) {
            console.error('Failed to calculate route:', error);
            setIsCalculatingRoute(false);
            // Fallback to straight line
            setRouteData({
              coordinates: waypoints,
              distance: 0,
              duration: 0
            });
          }
        }
      }, 1000); // Increased to 1 second to reduce hot reload requests

    } else {
      setRouteData(null);
      setIsCalculatingRoute(false);
    }

    // Cleanup function
    return () => {
      abortController.abort();
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
    };
  }, [selectedStops, showRoute, precalculatedRouteData]);

  // Create route line coordinates for Leaflet Polyline
  const routeCoordinates: [number, number][] = routeData 
    ? routeData.coordinates.map(coord => [coord.lat, coord.lng])
    : selectedStops.map(stop => [stop.location.lat, stop.location.lon]);

  // Search handlers
  const handleSearchChange = (value: string) => {
    onSearchChange?.(value);
    const shouldOpen = value.length > 0;
    console.log('Search change:', value, 'Should open:', shouldOpen, 'Filtered stops:', filteredStops.length);
    setIsSearchDropdownOpen(shouldOpen);
  };

  const handleSearchFocus = () => {
    console.log('Search focus, searchTerm:', searchTerm, 'length:', searchTerm.length);
    if (searchTerm.length > 0) {
      setIsSearchDropdownOpen(true);
    }
  };

  const handleSearchBlur = () => {
    // Delay closing to allow for click on dropdown item
    setTimeout(() => {
      setIsSearchDropdownOpen(false);
    }, 200);
  };

  const handleDropdownItemClick = (stop: Stop) => {
    onAddStop?.(stop);
    onSearchChange?.('');
    setIsSearchDropdownOpen(false);
  };

  return (
    <MapWrapper>
      {onStopClick && (
        <MapInfo>
          <div style={{ fontSize: '12px', opacity: 0.8 }}>
             Click stops to add to route
          </div>
          {isCalculatingRoute && (
            <div style={{ marginTop: '4px', fontSize: '12px', opacity: 0.8, color: '#3b82f6' }}>
               Calculating road route...
            </div>
          )}
          {selectedStops.length === 1 && (
            <div style={{ marginTop: '4px', fontSize: '12px', opacity: 0.8 }}>
              Add another stop to see route
            </div>
          )}
        </MapInfo>
      )}


      {/* Map Sidebar */}
      <MapSidebar>
        <SidebarHeader>
          <SidebarTitle>
             Route Builder
          </SidebarTitle>
        </SidebarHeader>

        <SidebarContent>
          {/* Controls Section */}
          <ControlSection>
            <ControlGroup>
              {onToggleShowAllStops && (
                <ToggleButton
                  active={showAllStops}
                  onClick={onToggleShowAllStops}
                >
                   {showAllStops ? 'Hide Available Stops' : 'Show Available Stops'}
                </ToggleButton>
              )}

              {onToggleAddingMode && (
                <ToggleButton
                  active={!!isAddingMode}
                  onClick={onToggleAddingMode}
                >
                   Add Stops
                </ToggleButton>
              )}


              {/* {selectedStops.length >= 2 && onFitRoute && (
                <ActionButton onClick={onFitRoute}>
                   Fit Route to View
                </ActionButton>
              )} */}
            </ControlGroup>
          </ControlSection>

          {/* Search Section */}
          <SearchSection>
            <SearchContainer>
              <SearchInput
                type="text"
                placeholder="Search stops to add..."
                value={searchTerm}
                onChange={(e) => handleSearchChange(e.target.value)}
                onFocus={handleSearchFocus}
                onBlur={handleSearchBlur}
              />

              {/* Search Dropdown */}
              <SearchDropdown isOpen={isSearchDropdownOpen}>
                {filteredStops.length > 0 ? (
                  filteredStops.map((stop) => {
                    const isSelected = selectedStops.some(s => String(s.id) === String(stop.id));
                    return (
                      <DropdownItem
                        key={stop.id}
                        selected={isSelected}
                        onClick={() => !isSelected && handleDropdownItemClick(stop)}
                      >
                        <DropdownItemInfo>
                          <DropdownItemName>{stop.name}</DropdownItemName>
                          <DropdownItemCode>{stop.code}</DropdownItemCode>
                        </DropdownItemInfo>
                        {!isSelected && (
                          <div style={{ color: theme.colors.primary, fontSize: '16px' }}>+</div>
                        )}
                      </DropdownItem>
                    );
                  })
                ) : (
                  <NoResults>
                    No stops found for "{searchTerm}"
                  </NoResults>
                )}
              </SearchDropdown>
            </SearchContainer>
          </SearchSection>

          {/* Selected Stops Section */}
          <SelectedStopsSection>
            <SelectedStopsHeader>
              Selected Stops ({selectedStops.length})
            </SelectedStopsHeader>

            <SelectedStopsList>
              {selectedStops.length > 0 ? (
                selectedStops.map((stop, index) => (
                  <SelectedStopItem key={stop.id}>
                    <StopOrder>{index + 1}</StopOrder>
                    <StopInfo>
                      <StopName>{stop.name}</StopName>
                      <StopCode>{stop.code}</StopCode>
                    </StopInfo>
                    <RemoveButton onClick={() => onRemoveStop?.(stop.id)}>

                    </RemoveButton>
                  </SelectedStopItem>
                ))
              ) : (
                <EmptyState>
                  <div style={{ fontSize: '20px', marginBottom: '8px' }}></div>
                  <div style={{ fontSize: '12px', fontWeight: '500' }}>No stops selected</div>
                  <div style={{ fontSize: '10px', marginTop: '4px', color: theme.colors.textMuted }}>
                    Search and add stops to build your route
                  </div>
                </EmptyState>
              )}
            </SelectedStopsList>
          </SelectedStopsSection>
        </SidebarContent>
      </MapSidebar>

      <MapContainer
        center={[defaultCenter.lat, defaultCenter.lng]}
        zoom={zoom}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Handle automatic bounds fitting when stops change */}
        <MapBoundsHandler selectedStops={selectedStops} />

        {/* Handle map clicks for adding new stops */}
        {isAddingMode && onMapClick && (
          <MapAddingModeHandler 
            isAddingMode={isAddingMode} 
            onMapClick={onMapClick}
          />
        )}

        {/* Show all available stops */}
        {stops.map((stop) => {
          const isSelected = selectedStops.some(s => String(s.id) === String(stop.id));
          const selectedIndex = selectedStops.findIndex(s => String(s.id) === String(stop.id));

          // Only show non-selected stops if showAllStops is true
          if (!isSelected && !showAllStops) {
            return null;
          }

          return (
            <Marker
              key={stop.id}
              position={[stop.location.lat, stop.location.lon]}
              icon={isSelected ? selectedIcon : defaultIcon}
              eventHandlers={{
                click: () => {
                  if (onStopClick) {
                    onStopClick(stop);
                  }
                }
              }}
            >
              <Popup>
                <PopupContent>
                  <PopupTitle>{stop.name}</PopupTitle>
                  <PopupDetail><strong>Code:</strong> {stop.code}</PopupDetail>
                  {isSelected && (
                    <PopupDetail><strong>Order:</strong> #{selectedIndex + 1} in route</PopupDetail>
                  )}
                  {onStopClick && !isSelected && (
                    <PopupDetail style={{ color: theme.colors.primary, fontWeight: 'bold', marginTop: '8px' }}>
                      Click to add to route
                    </PopupDetail>
                  )}
                  {isSelected && onRemoveStop && (
                    <div style={{ marginTop: '12px' }}>
                      <button
                        onClick={(e) => {
                          e.preventDefault();
                          e.stopPropagation();
                          onRemoveStop(stop.id);
                        }}
                        style={{
                          width: '100%',
                          padding: '6px 12px',
                          backgroundColor: theme.colors.error,
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                          cursor: 'pointer',
                          fontSize: '12px',
                          fontWeight: '500',
                          transition: 'all 0.2s ease-in-out',
                        }}
                        onMouseEnter={(e) => {
                          (e.currentTarget as HTMLButtonElement).style.backgroundColor = '#dc2626';
                          (e.currentTarget as HTMLButtonElement).style.transform = 'translateY(-1px)';
                        }}
                        onMouseLeave={(e) => {
                          (e.currentTarget as HTMLButtonElement).style.backgroundColor = theme.colors.error;
                          (e.currentTarget as HTMLButtonElement).style.transform = 'translateY(0)';
                        }}
                      >
                         Remove from Route
                      </button>
                    </div>
                  )}
                </PopupContent>
              </Popup>
            </Marker>
          );
        })}

        {/* Show route line */}
        {showRoute && selectedStops.length > 1 && routeCoordinates.length > 0 && (
          <>
            {/* Glow/shadow effect layer */}
            <Polyline
              positions={routeCoordinates}
              color={theme.colors.primary}
              weight={4}
              opacity={0.2}
              dashArray={routeData ? undefined : "10, 5"}
              lineCap="round"
              lineJoin="round"
            />
            {/* Main route line */}
            <Polyline
              positions={routeCoordinates}
              color={routeData ? theme.colors.primary : theme.colors.info}
              weight={routeData ? 4 : 6}
              opacity={1}
              dashArray={routeData ? undefined : "10, 5"}
              lineCap="round"
              lineJoin="round"
            />
          </>
        )}
      </MapContainer>
    </MapWrapper>
  );
};

export default RouteMap;
