import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { 
  FiMap, 
  FiSave, 
  FiArrowLeft, 
  FiPlus, 
  FiX, 
  FiSearch,
  FiMove,
  FiMapPin
} from 'react-icons/fi';
import { theme } from '../../styles/theme';
import { Card, Button, Input, Select } from '../../styles/GlobalStyles';
import RouteMap from '../../components/Map/RouteMap';
import { Route, Stop } from '../../types';
import { mockAPI } from '../../services/mockData';

const CreateRouteContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
  max-width: 1200px;
  margin: 0 auto;
`;

const PageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
`;

const BackButton = styled(Button)`
  padding: ${theme.spacing.sm};
`;

const PageTitle = styled.h1`
  font-size: ${theme.typography.fontSize['3xl']};
  font-weight: ${theme.typography.fontWeight.bold};
  color: ${theme.colors.textPrimary};
  margin: 0;
`;

const ContentLayout = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
`;

const TopSection = styled.div`
  display: grid;
  grid-template-columns: 400px 1fr;
  gap: ${theme.spacing.xl};

  @media (max-width: ${theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
  }
`;

const BottomSection = styled.div`
  display: grid;
  grid-template-columns: 1fr 400px;
  gap: ${theme.spacing.xl};

  @media (max-width: ${theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
  }
`;

const FormCard = styled(Card)`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
  height: fit-content;
`;

const MapCard = styled(Card)`
  padding: ${theme.spacing.md};
  grid-column: 1 / -1; /* Span full width */
`;

const CentralMapCard = styled(Card)`
  padding: ${theme.spacing.md};
  height: 600px; /* Larger height for central map */
`;

const SelectedStopsCard = styled(Card)`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
  height: fit-content;
  max-height: 600px;
  overflow: hidden;
`;

const FormSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.md};
`;

const SectionTitle = styled.h3`
  font-size: ${theme.typography.fontSize.lg};
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textPrimary};
  margin: 0;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${theme.spacing.md};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.sm};
`;

const Label = styled.label`
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
`;

const RequiredIndicator = styled.span`
  color: ${theme.colors.error};
`;

const StopsSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.md};
`;

const StopSearch = styled.div`
  position: relative;
`;

const SearchInput = styled(Input)`
  padding-left: 40px;
`;

const SearchIcon = styled.div`
  position: absolute;
  left: ${theme.spacing.md};
  top: 50%;
  transform: translateY(-50%);
  color: ${theme.colors.textMuted};
`;

const AvailableStops = styled.div`
  max-height: 200px;
  overflow-y: auto;
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.surface};
`;

const StopItem = styled.div<{ selected?: boolean }>`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${theme.spacing.md};
  border-bottom: 1px solid ${theme.colors.border};
  cursor: pointer;
  transition: background 0.2s ease-in-out;

  ${({ selected }) => selected && `
    background: rgba(59, 130, 246, 0.1);
    border-color: ${theme.colors.primary};
  `}

  &:hover {
    background: ${theme.colors.surfaceHover};
  }

  &:last-child {
    border-bottom: none;
  }
`;

const StopInfo = styled.div`
  flex: 1;
`;

const StopName = styled.div`
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
`;

const StopDetails = styled.div`
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.fontSize.xs};
  margin-top: ${theme.spacing.xs};
`;

const StopBadge = styled.span<{ type: Stop['type'] }>`
  padding: 2px 6px;
  border-radius: ${theme.borderRadius.sm};
  font-size: 10px;
  font-weight: bold;
  text-transform: uppercase;

  ${({ type }) => {
    switch (type) {
      case 'depot':
        return `background: rgba(245, 158, 11, 0.2); color: #f59e0b;`;
      case 'pickup':
        return `background: rgba(16, 185, 129, 0.2); color: #10b981;`;
      case 'delivery':
        return `background: rgba(59, 130, 246, 0.2); color: #3b82f6;`;
      default:
        return `background: rgba(107, 114, 128, 0.2); color: #6b7280;`;
    }
  }}
`;

const SelectedStops = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.sm};
  flex: 1;
  overflow-y: auto;
  padding-right: ${theme.spacing.sm};

  /* Custom scrollbar */
  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: ${theme.colors.surface};
    border-radius: 3px;
  }

  &::-webkit-scrollbar-thumb {
    background: ${theme.colors.secondary};
    border-radius: 3px;
  }
`;

const SelectedStopItem = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
  padding: ${theme.spacing.md};
  background: ${theme.colors.surface};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
`;

const StopOrder = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background: ${theme.colors.primary};
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: ${theme.typography.fontWeight.bold};
  font-size: ${theme.typography.fontSize.sm};
`;

const DragHandle = styled.div`
  color: ${theme.colors.textMuted};
  cursor: grab;

  &:active {
    cursor: grabbing;
  }
`;

const RemoveButton = styled.button`
  color: ${theme.colors.error};
  padding: ${theme.spacing.xs};

  &:hover {
    background: rgba(239, 68, 68, 0.1);
    border-radius: ${theme.borderRadius.sm};
  }
`;

const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
`;

const EmptyState = styled.div`
  text-align: center;
  padding: ${theme.spacing.xl};
  color: ${theme.colors.textMuted};
`;

const CreateRoute: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });
  const [availableStops, setAvailableStops] = useState<Stop[]>([]);
  const [selectedStops, setSelectedStops] = useState<Stop[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredStops, setFilteredStops] = useState<Stop[]>([]);
  const [loading, setLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const loadStops = async () => {
      try {
        const stops = await mockAPI.getStops();
        setAvailableStops(stops);
        setFilteredStops(stops);
      } catch (error) {
        console.error('Error loading stops:', error);
      } finally {
        setLoading(false);
      }
    };

    loadStops();
  }, []);

  useEffect(() => {
    const filtered = availableStops.filter(stop =>
      stop.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      stop.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
      stop.description?.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredStops(filtered);
  }, [availableStops, searchTerm]);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleAddStop = (stop: Stop) => {
    if (!selectedStops.find(s => s.id === stop.id)) {
      setSelectedStops(prev => [...prev, stop]);
    }
  };

  const handleRemoveStop = (stopId: string) => {
    setSelectedStops(prev => prev.filter(s => s.id !== stopId));
  };

  const handleMoveStop = (fromIndex: number, toIndex: number) => {
    const newStops = [...selectedStops];
    const [movedStop] = newStops.splice(fromIndex, 1);
    newStops.splice(toIndex, 0, movedStop);
    setSelectedStops(newStops);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.code || selectedStops.length < 2) {
      alert('Please fill in all required fields and select at least 2 stops.');
      return;
    }

    setIsSubmitting(true);

    try {
      const newRoute: Omit<Route, 'id' | 'createdAt' | 'updatedAt'> = {
        name: formData.name,
        // code: formData.code,
        description: formData.description,
        startLocation: {
          lat: selectedStops[0].location.lat,
          lng: selectedStops[0].location.lng,
          address: selectedStops[0].address || 'Unknown'
        },
        endLocation: {
          lat: selectedStops[selectedStops.length - 1].location.lat,
          lng: selectedStops[selectedStops.length - 1].location.lng,
          address: selectedStops[selectedStops.length - 1].address || 'Unknown'
        },
        waypoints: selectedStops.slice(1, -1).map((stop, index) => ({
          lat: stop.location.lat,
          lng: stop.location.lng,
          address: stop.address || 'Unknown',
          order: index + 1
        })),
        stops: selectedStops.map(s => s.id),
        estimatedDistance: 0, // Would be calculated by routing service
        estimatedDuration: 0, // Would be calculated by routing service
        status: 'active',
        assignedVehicles: []
      };

      console.log('Creating route:', newRoute);

      // Simulate delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      navigate('/routes');
    } catch (error) {
      console.error('Error creating route:', error);
      alert('Error creating route. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    navigate('/routes');
  };

  if (loading) {
    return <div>Loading stops...</div>;
  }

  return (
    <CreateRouteContainer className="fade-in">
      <PageHeader>
        <BackButton variant="secondary" onClick={handleCancel}>
          <FiArrowLeft />
        </BackButton>
        <PageTitle>Create New Route</PageTitle>
      </PageHeader>

      <ContentGrid>
        <form onSubmit={handleSubmit}>
          <FormCard>
            <FormSection>
              <SectionTitle>
                <FiMap />
                Route Information
              </SectionTitle>

              <FormRow>
                <FormGroup>
                  <Label>
                    Route Name <RequiredIndicator>*</RequiredIndicator>
                  </Label>
                  <Input
                    type="text"
                    placeholder="Enter route name"
                    value={formData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    required
                  />
                </FormGroup>

                <FormGroup>
                  <Label>
                    Route Code <RequiredIndicator>*</RequiredIndicator>
                  </Label>
                  <Input
                    type="text"
                    placeholder="Enter route code (e.g., RT001)"
                    value={formData.code}
                    onChange={(e) => handleInputChange('code', e.target.value)}
                    required
                  />
                </FormGroup>
              </FormRow>

              <FormGroup>
                <Label>Description</Label>
                <Input
                  type="text"
                  placeholder="Optional description"
                  value={formData.description}
                  onChange={(e) => handleInputChange('description', e.target.value)}
                />
              </FormGroup>
            </FormSection>

            <StopsSection>
              <SectionTitle>
                <FiMapPin />
                Select Stops <RequiredIndicator>*</RequiredIndicator>
              </SectionTitle>

              <StopSearch>
                <SearchIcon>
                  <FiSearch size={18} />
                </SearchIcon>
                <SearchInput
                  type="text"
                  placeholder="Search stops..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </StopSearch>

              <AvailableStops>
                {filteredStops.map((stop) => {
                  const isSelected = selectedStops.some(s => s.id === stop.id);
                  return (
                    <StopItem
                      key={stop.id}
                      selected={isSelected}
                      onClick={() => !isSelected && handleAddStop(stop)}
                    >
                      <StopInfo>
                        <StopName>{stop.name}</StopName>
                        <StopDetails>
                          {stop.code} ‚Ä¢ <StopBadge type={stop.type}>{stop.type}</StopBadge>
                          {stop.address && ` ‚Ä¢ ${stop.address}`}
                        </StopDetails>
                      </StopInfo>
                      {!isSelected && <FiPlus color={theme.colors.primary} />}
                    </StopItem>
                  );
                })}
              </AvailableStops>

              {selectedStops.length > 0 && (
                <>
                  <Label>Selected Stops ({selectedStops.length})</Label>
                  <SelectedStops>
                    {selectedStops.map((stop, index) => (
                      <SelectedStopItem key={stop.id}>
                        <DragHandle>
                          <FiMove />
                        </DragHandle>
                        <StopOrder>{index + 1}</StopOrder>
                        <StopInfo>
                          <StopName>{stop.name}</StopName>
                          <StopDetails>
                            {stop.code} ‚Ä¢ <StopBadge type={stop.type}>{stop.type}</StopBadge>
                          </StopDetails>
                        </StopInfo>
                        <RemoveButton onClick={() => handleRemoveStop(stop.id)}>
                          <FiX />
                        </RemoveButton>
                      </SelectedStopItem>
                    ))}
                  </SelectedStops>
                </>
              )}

              {selectedStops.length === 0 && (
                <EmptyState>
                  <div style={{ fontSize: '24px', marginBottom: '8px' }}>üìç</div>
                  <div>No stops selected</div>
                  <div style={{ fontSize: '12px', marginTop: '4px' }}>
                    Select at least 2 stops to create a route
                  </div>
                </EmptyState>
              )}
            </StopsSection>

            <ActionButtons>
              <Button 
                type="button" 
                variant="secondary" 
                onClick={handleCancel}
                disabled={isSubmitting}
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                variant="primary"
                disabled={isSubmitting || selectedStops.length < 2}
              >
                <FiSave />
                {isSubmitting ? 'Creating...' : 'Create Route'}
              </Button>
            </ActionButtons>
          </FormCard>
        </form>

        <MapCard>
          <RouteMap
            stops={availableStops}
            selectedStops={selectedStops}
            showRoute={true}
          />
        </MapCard>
      </ContentGrid>
    </CreateRouteContainer>
  );
};

export default CreateRoute;
