import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiPlus, FiSearch, FiEdit, FiTrash2, FiUser } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  PageContainer,
  PageHeader,
  PageTitle,
  HeaderActions,
  SearchContainer,
  SearchInput,
  Table,
  TableHeader,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell,
  ActionButtons,
  ActionButton,
  FormGroup,
  Label,
  Input,
  ErrorMessage
} from '../../components/Common/CommonStyles';
import { dataService } from '../../services/dataService';

interface User {
  id: string;
  username: string;
  name: string;
  phone: string;
  type: string;
  roles: string[];
}

interface UsersResponse {
  data: User[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    pages: number;
  };
}

const UsersList: React.FC = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadUsers();
  }, [currentPage, searchTerm]);

  const loadUsers = async () => {
    try {
      setLoading(true);
      setError(null);

      const params = {
        page: currentPage,
        limit: 10,
        ...(searchTerm && { search: searchTerm })
      };

      const response: UsersResponse = await dataService.getUsers(params);
      console.log(response.data);
      setUsers(response.data);
      setTotalPages(response.pagination.pages);
    } catch (error: any) {
      console.error('Error loading users:', error);
      setError(error.message || 'Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reset to first page when searching
  };

  const handleCreateUser = () => {
    navigate('/users/create');
  };

  const handleEditUser = (userId: string) => {
    navigate(`/users/edit/${userId}`);
  };

  const handleDeleteUser = async (userId: string, userName: string) => {
    if (window.confirm(`Are you sure you want to delete user "${userName}"?`)) {
      try {
        // TODO: Implement delete user API
        console.log('Delete user:', userId);
        await loadUsers(); // Reload the list
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Failed to delete user');
      }
    }
  };

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  const renderPagination = () => {
    if (totalPages <= 1) return null;

    const pages = [];
    const startPage = Math.max(1, currentPage - 2);
    const endPage = Math.min(totalPages, currentPage + 2);

    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          style={{
            padding: '8px 12px',
            margin: '0 2px',
            border: i === currentPage ? '2px solid #3b82f6' : '1px solid #e5e7eb',
            backgroundColor: i === currentPage ? '#3b82f6' : 'white',
            color: i === currentPage ? 'white' : '#374151',
            borderRadius: '6px',
            cursor: 'pointer'
          }}
        >
          {i}
        </button>
      );
    }

    return (
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px', gap: '4px' }}>
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
          style={{
            padding: '8px 12px',
            border: '1px solid #e5e7eb',
            backgroundColor: 'white',
            color: currentPage === 1 ? '#9ca3af' : '#374151',
            borderRadius: '6px',
            cursor: currentPage === 1 ? 'not-allowed' : 'pointer'
          }}
        >
          Previous
        </button>
        {pages}
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
          style={{
            padding: '8px 12px',
            border: '1px solid #e5e7eb',
            backgroundColor: 'white',
            color: currentPage === totalPages ? '#9ca3af' : '#374151',
            borderRadius: '6px',
            cursor: currentPage === totalPages ? 'not-allowed' : 'pointer'
          }}
        >
          Next
        </button>
      </div>
    );
  };

  if (loading) {
    return (
      <PageContainer className="fade-in">
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          <div>Loading users...</div>
        </div>
      </PageContainer>
    );
  }

  return (
    <PageContainer className="fade-in">
      <PageHeader>
        <PageTitle>Users Management</PageTitle>
        <HeaderActions>
          <Button variant="primary" onClick={handleCreateUser}>
            <FiPlus />
            Create User
          </Button>
        </HeaderActions>
      </PageHeader>

      <div style={{ marginBottom: '1.5rem' }}>
        <SearchContainer>
          <SearchInput
            type="text"
            placeholder="Search users by name or username..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </SearchContainer>
      </div>

      {error && (
        <div style={{ 
          padding: '1rem', 
          backgroundColor: '#fef2f2', 
          border: '1px solid #fecaca', 
          borderRadius: '8px', 
          marginBottom: '1rem',
          color: '#dc2626'
        }}>
          {error}
        </div>
      )}

      <Table>
        <TableHeader>
          <TableRow>
            <TableHeaderCell width="20%">Name</TableHeaderCell>
            <TableHeaderCell width="20%">Username</TableHeaderCell>
            <TableHeaderCell width="20%">Phone</TableHeaderCell>
            {/* <TableHeaderCell width="30%">Roles</TableHeaderCell> */}
            <TableHeaderCell width="20%">Type</TableHeaderCell>
            <TableHeaderCell>Actions</TableHeaderCell>
          </TableRow>
        </TableHeader>
        <TableBody>
          {users.length === 0 ? (
            <TableRow>
              <TableCell colSpan={5} style={{ textAlign: 'center', padding: '2rem' }}>
                <div style={{ color: '#6b7280' }}>
                  <FiUser size={48} style={{ marginBottom: '1rem', opacity: 0.5 }} />
                  <div>No users found</div>
                  {searchTerm && <div style={{ fontSize: '0.875rem', marginTop: '0.5rem' }}>
                    Try adjusting your search terms
                  </div>}
                </div>
              </TableCell>
            </TableRow>
          ) : (
            users.map((user) => (
              <TableRow key={user.id}>
                <TableCell>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                    <div style={{
                      width: '40px',
                      height: '40px',
                      backgroundColor: '#3b82f6',
                      borderRadius: '50%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontWeight: '600',
                      fontSize: '0.875rem'
                    }}>
                      {user.name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2)}
                    </div>
                    <div>
                      <div style={{ fontWeight: '500', color: '#111827' }}>
                        {user.name}
                      </div>
                    </div>
                  </div>
                </TableCell>
                <TableCell>
                  <div style={{ color: '#6b7280', fontFamily: 'monospace' }}>
                    {user.username}
                  </div>
                </TableCell>
                <TableCell>
                  <div style={{ color: '#6b7280', fontFamily: 'monospace' }}>
                    {user.phone}
                  </div>
                </TableCell>
                {/* <TableCell>
                  <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
                    {user.roles.length > 0 ? (
                      user.roles.map((role, index) => (
                        <span
                          key={index}
                          style={{
                            backgroundColor: '#f3f4f6',
                            color: '#374151',
                            padding: '2px 8px',
                            borderRadius: '12px',
