import React, { useState, useEffect } from 'react';
import { FiPlus, FiEdit2, FiTrash2, FiClock, FiTruck, FiDroplet, FiLayers } from 'react-icons/fi';
import styled from 'styled-components';
import { theme } from '../../styles/theme';
import { DutyTemplate } from '../../types';
import { apiService } from '../../services/apiService';
import AddTemplateForm from './AddTemplateForm';
import BulkTemplateForm from './BulkTemplateForm';

const TemplateManagerContainer = styled.div`
  background: ${theme.colors.card};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.lg};
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: ${theme.spacing.lg};
`;

const Title = styled.h3`
  color: ${theme.colors.textPrimary};
  margin: 0;
  font-size: ${theme.typography.fontSize.lg};
  font-weight: ${theme.typography.fontWeight.semibold};
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: ${theme.spacing.sm};
`;

const AddButton = styled.button`
  background: ${theme.colors.primary};
  color: white;
  border: none;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  transition: all 0.2s ease;

  &:hover {
    background: ${theme.colors.primaryHover};
  }
`;

const BulkButton = styled.button`
  background: ${theme.colors.secondary || '#8b5cf6'};
  color: white;
  border: none;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  transition: all 0.2s ease;

  &:hover {
    background: ${theme.colors.secondaryHover || '#7c3aed'};
  }
`;

const TimelineContainer = styled.div`
  position: relative;
  min-height: 200px;
  background: ${theme.colors.surface};
  border-radius: ${theme.borderRadius.md};
  padding: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.lg};
`;

const TimelineHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: ${theme.spacing.md};
  font-size: ${theme.typography.fontSize.sm};
  color: ${theme.colors.textSecondary};
`;

const TimelineGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(24, 1fr);
  gap: 1px;
  height: 120px;
  background: ${theme.colors.border};
  border-radius: ${theme.borderRadius.sm};
  overflow: hidden;
`;

const HourLabel = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 10px;
  color: ${theme.colors.textMuted};
  background: ${theme.colors.surface};
`;

const TemplateBlock = styled.div<{ 
  startHour: number; 
  duration: number; 
  dutyType: string;
  isOverlapping?: boolean;
}>`
  position: absolute;
  top: 40px;
  left: ${props => (props.startHour / 24) * 100}%;
  width: ${props => (props.duration / 24) * 100}%;
  height: 40px;
  background: ${props => {
    if (props.isOverlapping) return theme.colors.error;
    switch (props.dutyType) {
      case 'TRIP': return theme.colors.primary;
      case 'WASHING': return '#8b5cf6';
      case 'MAINTENANCE': return '#f59e0b';
      default: return theme.colors.textMuted;
    }
  }};
  border-radius: ${theme.borderRadius.sm};
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 ${theme.spacing.sm};
  color: white;
  font-size: ${theme.typography.fontSize.xs};
  font-weight: ${theme.typography.fontWeight.medium};
  cursor: pointer;
  transition: all 0.2s ease;
  z-index: 2;

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${theme.shadows.md};
  }
`;

const TemplateInfo = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.xs};
  flex: 1;
  min-width: 0;
`;

const TemplateActions = styled.div`
  display: flex;
  gap: ${theme.spacing.xs};
  opacity: 0;
  transition: opacity 0.2s ease;

  ${TemplateBlock}:hover & {
    opacity: 1;
  }
`;

const ActionButton = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  padding: 2px;
  border-radius: 2px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 10px;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const TemplatesList = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.sm};
`;

const TemplateItem = styled.div<{ dutyType: string }>`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${theme.spacing.sm};
  background: ${theme.colors.surface};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  border-left: 4px solid ${props => {
    switch (props.dutyType) {
      case 'TRIP': return theme.colors.primary;
      case 'WASHING': return '#8b5cf6';
      case 'MAINTENANCE': return '#f59e0b';
      default: return theme.colors.textMuted;
    }
  }};
`;

const TemplateDetails = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
  flex: 1;
`;

const TemplateIcon = styled.div<{ dutyType: string }>`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background: ${props => {
    switch (props.dutyType) {
      case 'TRIP': return `${theme.colors.primary}20`;
      case 'WASHING': return '#8b5cf620';
      case 'MAINTENANCE': return '#f59e0b20';
      default: return `${theme.colors.textMuted}20`;
    }
  }};
  color: ${props => {
    switch (props.dutyType) {
      case 'TRIP': return theme.colors.primary;
      case 'WASHING': return '#8b5cf6';
      case 'MAINTENANCE': return '#f59e0b';
      default: return theme.colors.textMuted;
    }
  }};
`;

const TemplateText = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2px;
`;

const TemplateName = styled.div`
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
`;

const TemplateTime = styled.div`
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.fontSize.xs};
`;

const TemplateActionsList = styled.div`
  display: flex;
  gap: ${theme.spacing.sm};
`;

const ListActionButton = styled.button`
  background: transparent;
  border: 1px solid ${theme.colors.border};
  color: ${theme.colors.textSecondary};
  padding: ${theme.spacing.xs} ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.sm};
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.xs};
  font-size: ${theme.typography.fontSize.xs};
  transition: all 0.2s ease;

  &:hover {
    background: ${theme.colors.surfaceHover};
    border-color: ${theme.colors.textSecondary};
  }

  &.delete:hover {
    background: ${theme.colors.error}20;
    border-color: ${theme.colors.error};
    color: ${theme.colors.error};
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: ${theme.spacing['2xl']};
  color: ${theme.colors.textMuted};
`;

const ErrorMessage = styled.div`
  background: ${theme.colors.error}20;
  border: 1px solid ${theme.colors.error};
  color: ${theme.colors.error};
  padding: ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  margin-bottom: ${theme.spacing.md};
`;

interface TripTemplateManagerProps {
  scheduleId?: string;
  templates: DutyTemplate[];
  onTemplatesChange: (templates: DutyTemplate[]) => void;
  onError?: (error: string) => void;
}

const TripTemplateManager: React.FC<TripTemplateManagerProps> = ({
  scheduleId,
  templates,
  onTemplatesChange,
  onError
}) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [showBulkForm, setShowBulkForm] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<DutyTemplate | null>(null);
  const [error, setError] = useState<string | null>(null);

  const getDutyTypeIcon = (dutyType: string) => {
    switch (dutyType) {
      case 'TRIP': return <FiTruck size={14} />;
      case 'WASHING': return <FiDroplet size={14} />;
      case 'MAINTENANCE': return <FiTruck size={14} />;
      default: return <FiClock size={14} />;
    }
  };

  const getDutyTypeLabel = (dutyType: string) => {
    switch (dutyType) {
      case 'TRIP': return 'Trip';
      case 'WASHING': return 'Washing';
      case 'MAINTENANCE': return 'Maintenance';
      default: return dutyType;
    }
  };

  const formatTime = (timeString: string) => {
    try {
      const date = new Date(timeString);
      return date.toLocaleTimeString('en-US', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: false 
      });
    } catch {
      return timeString;
    }
  };

  const calculateDuration = (startTime: string, endTime: string) => {
    try {
      const start = new Date(startTime);
      const end = new Date(endTime);
      return (end.getTime() - start.getTime()) / (1000 * 60 * 60); // hours
    } catch {
      return 0;
    }
  };

  const calculateStartHour = (timeString: string) => {
    try {
      const date = new Date(timeString);
      return date.getHours() + (date.getMinutes() / 60);
    } catch {
      return 0;
    }
  };

  const checkForOverlaps = (templates: DutyTemplate[]) => {
    const sortedTemplates = [...templates].sort((a, b) => 
      new Date(a.startTime).getTime() - new Date(b.startTime).getTime()
    );

    for (let i = 0; i < sortedTemplates.length - 1; i++) {
      const current = sortedTemplates[i];
      const next = sortedTemplates[i + 1];

      const currentEnd = new Date(current.endTime).getTime();
      const nextStart = new Date(next.startTime).getTime();

      if (currentEnd > nextStart) {
        return true;
      }
    }
    return false;
  };

  const handleDeleteTemplate = async (templateId: string) => {
    if (!scheduleId) {
      // For new schedules, just remove from local state
      const updatedTemplates = templates.filter(t => t.id !== templateId);
      onTemplatesChange(updatedTemplates);
      return;
    }

    try {
      await apiService.deleteDutyTemplate(scheduleId, templateId);
      const updatedTemplates = templates.filter(t => t.id !== templateId);
      onTemplatesChange(updatedTemplates);
    } catch (error) {
      console.error('Error deleting template:', error);
      setError('Failed to delete template');
      onError?.('Failed to delete template');
    }
  };

  const handleAddTemplate = async (templateData: Omit<DutyTemplate, 'id' | 'scheduleId' | 'createdAt' | 'updatedAt'>) => {
    if (!scheduleId) {
      // For new schedules, add to local state with temporary ID
      const newTemplate: DutyTemplate = {
        ...templateData,
        id: `temp-${Date.now()}`,
        scheduleId: 'temp',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      onTemplatesChange([...templates, newTemplate]);
      return;
    }

    try {
      const newTemplate = await apiService.createDutyTemplate(scheduleId, {
        ...templateData,
        scheduleId
      });
      onTemplatesChange([...templates, newTemplate]);
    } catch (error) {
      console.error('Error creating template:', error);
      setError('Failed to create template');
      onError?.('Failed to create template');
    }
  };

  const handleBulkAddTemplates = async (templatesData: Omit<DutyTemplate, 'id' | 'scheduleId' | 'createdAt' | 'updatedAt'>[]) => {
    if (!scheduleId) {
      // For new schedules, add to local state with temporary IDs
      const newTemplates: DutyTemplate[] = templatesData.map((templateData, index) => ({
        ...templateData,
        id: `temp-${Date.now()}-${index}`,
        scheduleId: 'temp',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      }));
      onTemplatesChange([...templates, ...newTemplates]);
      return;
    }

    try {
      // Create templates one by one for existing schedules
      const newTemplates: DutyTemplate[] = [];
      for (const templateData of templatesData) {
        const newTemplate = await apiService.createDutyTemplate(scheduleId, {
          ...templateData,
          scheduleId
        });
        newTemplates.push(newTemplate);
      }
      onTemplatesChange([...templates, ...newTemplates]);
    } catch (error) {
      console.error('Error creating bulk templates:', error);
      setError('Failed to create some templates');
      onError?.('Failed to create some templates');
    }
  };

  const hasOverlaps = checkForOverlaps(templates);

  return (
    <TemplateManagerContainer>
      <Header>
        <Title>Duty Templates</Title>
        <ButtonGroup>
          <BulkButton onClick={() => setShowBulkForm(true)}>
            <FiLayers size={16} />
            Bulk Create
          </BulkButton>
          <AddButton onClick={() => setShowAddForm(true)}>
            <FiPlus size={16} />
            Add Template
          </AddButton>
        </ButtonGroup>
      </Header>

      {error && (
        <ErrorMessage>
          {error}
        </ErrorMessage>
      )}

      {hasOverlaps && (
        <ErrorMessage>
          ⚠️ Some templates have overlapping times. Please adjust the schedule.
        </ErrorMessage>
      )}

      {templates.length > 0 && (
        <TimelineContainer>
          <TimelineHeader>
            <span>Timeline View</span>
            <span>24 Hour Schedule</span>
          </TimelineHeader>

          <TimelineGrid>
            {Array.from({ length: 24 }, (_, i) => (
              <HourLabel key={i}>
                {i % 2 === 0 ? `${i}:00` : ''}
              </HourLabel>
            ))}
          </TimelineGrid>

          {templates.map((template) => (
            <TemplateBlock
              key={template.id}
              startHour={calculateStartHour(template.startTime)}
              duration={calculateDuration(template.startTime, template.endTime)}
              dutyType={template.dutyType}
              isOverlapping={hasOverlaps}
            >
              <TemplateInfo>
                {getDutyTypeIcon(template.dutyType)}
                <span>{template.name || getDutyTypeLabel(template.dutyType)}</span>
              </TemplateInfo>
              <TemplateActions>
                <ActionButton onClick={() => setEditingTemplate(template)}>
                  <FiEdit2 size={10} />
                </ActionButton>
                <ActionButton onClick={() => handleDeleteTemplate(template.id)}>
                  <FiTrash2 size={10} />
                </ActionButton>
              </TemplateActions>
            </TemplateBlock>
          ))}
        </TimelineContainer>
      )}

      {templates.length > 0 ? (
        <TemplatesList>
          {templates.map((template) => (
            <TemplateItem key={template.id} dutyType={template.dutyType}>
              <TemplateDetails>
                <TemplateIcon dutyType={template.dutyType}>
                  {getDutyTypeIcon(template.dutyType)}
                </TemplateIcon>
                <TemplateText>
                  <TemplateName>
                    {template.name || getDutyTypeLabel(template.dutyType)}
                  </TemplateName>
                  <TemplateTime>
                    {formatTime(template.startTime)} - {formatTime(template.endTime)}
                  </TemplateTime>
                </TemplateText>
              </TemplateDetails>
              <TemplateActionsList>
                <ListActionButton onClick={() => setEditingTemplate(template)}>
                  <FiEdit2 size={12} />
                  Edit
                </ListActionButton>
                <ListActionButton 
                  className="delete"
                  onClick={() => handleDeleteTemplate(template.id)}
                >
                  <FiTrash2 size={12} />
                  Delete
                </ListActionButton>
              </TemplateActionsList>
            </TemplateItem>
          ))}
        </TemplatesList>
      ) : (
        <EmptyState>
          <FiClock size={48} color={theme.colors.textMuted} style={{ marginBottom: theme.spacing.md }} />
          <h4 style={{ color: theme.colors.textPrimary, marginBottom: theme.spacing.sm }}>
            No Duty Templates
          </h4>
          <p>Add duty templates to define when trips, washing, or maintenance should occur.</p>
        </EmptyState>
      )}

      {/* Add/Edit Form Modal */}
      {showAddForm && (
        <AddTemplateForm
          onClose={() => setShowAddForm(false)}
          onSave={handleAddTemplate}
          existingTemplates={templates}
        />
      )}

      {editingTemplate && (
        <div>
          {/* TODO: Implement EditTemplateForm component */}
          <p>Edit Template Form - To be implemented</p>
          <button onClick={() => setEditingTemplate(null)}>Cancel</button>
        </div>
      )}
    </TemplateManagerContainer>
  );
};

export default TripTemplateManager;
