import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiUser, FiKey, FiArrowRight, FiEye, FiEyeOff } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  PageContainer,
  FormCard,
  FormGroup,
  Label,
  Input,
  ErrorMessage
} from '../../components/Common/CommonStyles';
import { dataService } from '../../services/dataService';
import { useAuth } from '../../contexts/AuthContext';

interface LoginFormData {
  username: string;
  otp: string;
}

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [formData, setFormData] = useState<LoginFormData>({
    username: '',
    otp: ''
  });
  const [errors, setErrors] = useState<string[]>([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isGeneratingOtp, setIsGeneratingOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [showOtp, setShowOtp] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear errors when user starts typing
    if (errors.length > 0) {
      setErrors([]);
    }
  };

  const validateForm = (): boolean => {
    const newErrors: string[] = [];

    if (!formData.username.trim()) {
      newErrors.push('Username is required');
    }

    if (!otpSent && !formData.otp.trim()) {
      newErrors.push('OTP is required');
    }

    setErrors(newErrors);
    return newErrors.length === 0;
  };

  const handleGenerateOtp = async () => {
    if (!formData.username.trim()) {
      setErrors(['Username is required']);
      return;
    }

    setIsGeneratingOtp(true);
    setErrors([]);

    try {
      await dataService.generateUserOtp(formData.username);
      setOtpSent(true);
    } catch (error: any) {
      console.error('Error generating OTP:', error);
      setErrors([error.message || 'Failed to generate OTP. Please try again.']);
    } finally {
      setIsGeneratingOtp(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    if (!otpSent) {
      await handleGenerateOtp();
      return;
    }

    setIsSubmitting(true);

    try {
      const response = await dataService.loginUserWithOtp(formData.username, formData.otp);

      // Store token and user data
      localStorage.setItem('token', response.token);
      localStorage.setItem('user', JSON.stringify(response.user));

      navigate('/dashboard');
    } catch (error: any) {
      console.error('Error logging in:', error);
      setErrors([error.message || 'Failed to login. Please try again.']);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleResendOtp = () => {
    setOtpSent(false);
    setFormData(prev => ({ ...prev, otp: '' }));
    handleGenerateOtp();
  };

  return (
    <PageContainer className="fade-in" style={{ 
      minHeight: '100vh', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    }}>
      <div style={{ width: '100%', maxWidth: '400px', padding: '20px' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h1 style={{ 
            color: 'white', 
            fontSize: '2rem', 
            fontWeight: 'bold',
            marginBottom: '0.5rem'
          }}>
            Welcome to Moveo
          </h1>
          <p style={{ color: 'rgba(255, 255, 255, 0.8)', fontSize: '1rem' }}>
            Fleet Management System
          </p>
        </div>

        <FormCard>
          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '1.5rem' }}>
              <h2 style={{ 
                textAlign: 'center', 
                marginBottom: '1rem',
                color: '#2c3e50',
                fontSize: '1.5rem'
              }}>
                {otpSent ? 'Enter OTP' : 'Login'}
              </h2>
              <p style={{ 
                textAlign: 'center', 
                color: '#7f8c8d',
                fontSize: '0.9rem',
                marginBottom: '1.5rem'
              }}>
                {otpSent 
                  ? `OTP sent to ${formData.username}. Enter the code below.`
                  : 'Enter your username to receive an OTP'
                }
              </p>
            </div>

            <FormGroup>
              <Label htmlFor="username">Username *</Label>
              <div style={{ position: 'relative' }}>
                <Input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Enter your username"
                  hasError={errors.length > 0}
                  disabled={otpSent}
                  style={{ paddingLeft: '40px' }}
                />
                <FiUser 
                  size={18} 
                  style={{ 
                    position: 'absolute', 
                    left: '12px', 
                    top: '50%', 
                    transform: 'translateY(-50%)',
                    color: '#7f8c8d'
                  }} 
                />
              </div>
            </FormGroup>

            {otpSent && (
              <FormGroup>
                <Label htmlFor="otp">OTP Code *</Label>
                <div style={{ position: 'relative' }}>
                  <Input
                    type={showOtp ? 'text' : 'password'}
                    id="otp"
                    name="otp"
                    value={formData.otp}
                    onChange={handleInputChange}
                    placeholder="Enter 6-digit OTP"
                    hasError={errors.length > 0}
                    style={{ paddingLeft: '40px', paddingRight: '40px' }}
                  />
                  <FiKey 
                    size={18} 
                    style={{ 
                      position: 'absolute', 
                      left: '12px', 
                      top: '50%', 
                      transform: 'translateY(-50%)',
                      color: '#7f8c8d'
                    }} 
                  />
                  <button
                    type="button"
                    onClick={() => setShowOtp(!showOtp)}
                    style={{
                      position: 'absolute',
                      right: '12px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      color: '#7f8c8d'
                    }}
                  >
                    {showOtp ? <FiEyeOff size={18} /> : <FiEye size={18} />}
                  </button>
                </div>
              </FormGroup>
            )}

            {errors.length > 0 && (
              <FormGroup>
                {errors.map((error, index) => (
                  <ErrorMessage key={index}>{error}</ErrorMessage>
                ))}
              </FormGroup>
            )}

            <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <Button 
                variant="primary" 
                type="submit"
                disabled={isSubmitting || isGeneratingOtp}
                style={{ width: '100%' }}
              >
                {isSubmitting ? (
                  'Logging in...'
                ) : isGeneratingOtp ? (
                  'Generating OTP...'
                ) : otpSent ? (
                  <>
                    <FiArrowRight size={18} />
                    Login
                  </>
                ) : (
                  'Generate OTP'
                )}
              </Button>

              {otpSent && (
                <Button 
                  variant="secondary" 
                  type="button"
                  onClick={handleResendOtp}
                  disabled={isGeneratingOtp}
                  style={{ width: '100%' }}
                >
                  Resend OTP
                </Button>
              )}
            </div>
          </form>
        </FormCard>
      </div>
    </PageContainer>
  );
};

export default Login;
