import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiPlus, FiSearch, FiEdit, FiTrash2, FiClock, FiMap, FiCalendar } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  PageContainer, 
  PageHeader, 
  PageTitle, 
  HeaderActions,
  SearchContainer,
  SearchInput,
  Table,
  TableHeader,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell,
  ActionButtons,
  ActionButton
} from '../../components/Common/CommonStyles';
import styled from 'styled-components';
import { theme } from '../../styles/theme';

const DutyCard = styled.div`
  background: white;
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.lg};
  margin-bottom: ${theme.spacing.md};
  transition: all 0.2s;

  &:hover {
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }
`;

const DutyHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: ${theme.spacing.md};
`;

const DutyTitle = styled.h3`
  font-size: ${theme.typography.fontSize.lg};
  font-weight: ${theme.typography.fontWeight.semibold};
  margin: 0;
`;

const DutyMeta = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: ${theme.spacing.md};
  margin-top: ${theme.spacing.md};
`;

const MetaItem = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  font-size: ${theme.typography.fontSize.sm};
  color: ${theme.colors.textSecondary};
`;

const TripCount = styled.div`
  display: inline-flex;
  align-items: center;
  gap: ${theme.spacing.xs};
  padding: ${theme.spacing.xs} ${theme.spacing.sm};
  background: ${theme.colors.primary};
  color: white;
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
`;

interface DutyConfiguration {
  id: string;
  scheduleName: string;
  scheduleId: string;
  routeName: string;
  routeId: string;
  date: string;
  startTime: string;
  endTime: string;
  frequency: number;
  numberOfTrips: number;
  createdAt: string;
}

const DutiesList: React.FC = () => {
  const navigate = useNavigate();
  const [duties, setDuties] = useState<DutyConfiguration[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // TODO: Load duties from API
    // For now, load sample data
    loadDuties();
  }, []);

  const loadDuties = async () => {
    try {
      setLoading(true);
      // TODO: Replace with actual API call
      // const response = await apiService.getDuties();

      // Sample data for now
      const sampleDuties: DutyConfiguration[] = [
        {
          id: '1',
          scheduleName: 'Weekday Service',
          scheduleId: '1',
          routeName: 'Route A',
          routeId: '1',
          date: '2024-01-15',
          startTime: '06:00',
          endTime: '22:00',
          frequency: 30,
          numberOfTrips: 32,
          createdAt: new Date().toISOString()
        }
      ];

      setDuties(sampleDuties);
    } catch (error) {
      console.error('Error loading duties:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const filteredDuties = duties.filter(duty =>
    duty.scheduleName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    duty.routeName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCreateDuty = () => {
    navigate('/duties/create');
  };

  const handleEditDuty = (dutyId: string) => {
    navigate(`/duties/edit/${dutyId}`);
  };

  const handleDeleteDuty = async (dutyId: string, scheduleName: string) => {
    if (window.confirm(`Are you sure you want to delete duties for "${scheduleName}"?`)) {
      try {
        // TODO: Implement delete API call
        setDuties(prev => prev.filter(duty => duty.id !== dutyId));
      } catch (error) {
        console.error('Error deleting duty:', error);
        alert('Failed to delete duty. Please try again.');
      }
    }
  };

  const formatTime = (time: string) => {
    return time; // Format as needed
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) {
    return <div>Loading duties...</div>;
  }

  return (
    <PageContainer className="fade-in">
      <PageHeader>
        <PageTitle>Duties Management</PageTitle>
        <HeaderActions>
          <Button onClick={handleCreateDuty} variant="primary">
            <FiPlus style={{ marginRight: '8px' }} />
            Create Duty Schedule
          </Button>
        </HeaderActions>
      </PageHeader>

      <SearchContainer>
        <SearchInput
          type="text"
          placeholder="Search duties by schedule or route..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </SearchContainer>

      {filteredDuties.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '40px' }}>
          <p style={{ color: theme.colors.textSecondary }}>
            {searchTerm ? 'No duties found matching your search.' : 'No duties found. Create a new duty schedule to get started.'}
          </p>
        </div>
      ) : (
        filteredDuties.map((duty) => (
          <DutyCard key={duty.id}>
            <DutyHeader>
              <div>
                <DutyTitle>{duty.scheduleName}</DutyTitle>
                <p style={{ margin: '4px 0 0', fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                  {duty.routeName}
                </p>
              </div>
              <div style={{ display: 'flex', gap: theme.spacing.sm }}>
                <ActionButton onClick={() => handleEditDuty(duty.id)} title="Edit">
                  <FiEdit />
                </ActionButton>
                <ActionButton onClick={() => handleDeleteDuty(duty.id, duty.scheduleName)} title="Delete">
                  <FiTrash2 />
                </ActionButton>
              </div>
            </DutyHeader>

            <DutyMeta>
              <MetaItem>
                <FiCalendar size={16} />
                <span>{formatDate(duty.date)}</span>
              </MetaItem>
              <MetaItem>
                <FiClock size={16} />
                <span>{formatTime(duty.startTime)} - {formatTime(duty.endTime)}</span>
              </MetaItem>
              <MetaItem>
                <FiMap size={16} />
                <span>Frequency: Every {duty.frequency} min</span>
              </MetaItem>
              <MetaItem>
                <TripCount>
                  {duty.numberOfTrips} Trips
                </TripCount>
              </MetaItem>
            </DutyMeta>
          </DutyCard>
        ))
      )}
    </PageContainer>
  );
};

export default DutiesList;
