import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import { theme } from '../../styles/theme';

const MapContainer = styled.div`
  width: 100%;
  height: 400px;
  background: ${theme.colors.surface};
  border: 2px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  position: relative;
  cursor: crosshair;
  overflow: hidden;
`;

const MapBackground = styled.div`
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, 
    ${theme.colors.surface} 25%, 
    transparent 25%, 
    transparent 75%, 
    ${theme.colors.surface} 75%
  ),
  linear-gradient(45deg, 
    ${theme.colors.surface} 25%, 
    transparent 25%, 
    transparent 75%, 
    ${theme.colors.surface} 75%
  );
  background-size: 20px 20px;
  background-position: 0 0, 10px 10px;
  position: relative;
`;

const MapPin = styled.div<{ x: number; y: number }>`
  position: absolute;
  left: ${({ x }) => x - 12}px;
  top: ${({ y }) => y - 24}px;
  width: 24px;
  height: 24px;
  background: ${theme.colors.error};
  border-radius: 50% 50% 50% 0;
  transform: rotate(-45deg);
  border: 2px solid white;
  box-shadow: ${theme.shadows.md};
  z-index: 10;

  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(45deg);
    width: 8px;
    height: 8px;
    background: white;
    border-radius: 50%;
  }
`;

const MapInfo = styled.div`
  position: absolute;
  top: ${theme.spacing.md};
  left: ${theme.spacing.md};
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  z-index: 20;
`;

const CoordinatesDisplay = styled.div`
  position: absolute;
  bottom: ${theme.spacing.md};
  right: ${theme.spacing.md};
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.xs};
  font-family: ${theme.typography.fontFamily.mono.join(', ')};
  z-index: 20;
`;

interface InteractiveMapProps {
  onLocationSelect: (lat: number, lng: number) => void;
  selectedLocation?: { lat: number; lng: number } | null;
}

const InteractiveMap: React.FC<InteractiveMapProps> = ({ 
  onLocationSelect, 
  selectedLocation 
}) => {
  const [mousePosition, setMousePosition] = useState<{ x: number; y: number } | null>(null);

  const handleMapClick = useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    // Convert pixel coordinates to mock lat/lng
    // This is a simple conversion for demo purposes
    const lat = 40.7128 + ((rect.height / 2 - y) / rect.height) * 0.1;
    const lng = -74.0060 + ((x - rect.width / 2) / rect.width) * 0.1;

    onLocationSelect(lat, lng);
  }, [onLocationSelect]);

  const handleMouseMove = useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    setMousePosition({ x, y });
  }, []);

  const handleMouseLeave = useCallback(() => {
    setMousePosition(null);
  }, []);

  // Convert lat/lng back to pixel coordinates for display
  const getPixelPosition = (lat: number, lng: number, containerRect: DOMRect) => {
    const x = containerRect.width / 2 + ((lng + 74.0060) / 0.1) * containerRect.width;
    const y = containerRect.height / 2 - ((lat - 40.7128) / 0.1) * containerRect.height;
    return { x, y };
  };

  return (
    <MapContainer 
      onClick={handleMapClick}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <MapBackground>
        <MapInfo>
          Click anywhere on the map to select a location
        </MapInfo>

        {selectedLocation && (
          <MapPin 
            x={200 + ((selectedLocation.lng + 74.0060) / 0.1) * 200} 
            y={200 - ((selectedLocation.lat - 40.7128) / 0.1) * 200}
          />
        )}

        {mousePosition && (
          <CoordinatesDisplay>
            Lat: {(40.7128 + ((200 - mousePosition.y) / 400) * 0.1).toFixed(6)}, 
            Lng: {(-74.0060 + ((mousePosition.x - 200) / 400) * 0.1).toFixed(6)}
          </CoordinatesDisplay>
        )}
      </MapBackground>
    </MapContainer>
  );
};

export default InteractiveMap;
