import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { FiPlus, FiEdit, FiTrash2, FiSearch, FiUser } from 'react-icons/fi';
import { theme } from '../../styles/theme';
import { Card, Button, Input, Badge } from '../../styles/GlobalStyles';
import { Driver } from '../../types';
import { mockAPI } from '../../services/mockData';

const DriversContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
`;

const PageHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: ${theme.spacing.md};
`;

const PageTitle = styled.h1`
  font-size: ${theme.typography.fontSize['3xl']};
  font-weight: ${theme.typography.fontWeight.bold};
  color: ${theme.colors.textPrimary};
  margin: 0;
`;

const HeaderActions = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  align-items: center;
`;

const FiltersCard = styled(Card)`
  display: flex;
  gap: ${theme.spacing.md};
  align-items: center;
  flex-wrap: wrap;
  padding: ${theme.spacing.lg};
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  min-width: 250px;
`;

const SearchInput = styled(Input)`
  padding-left: 40px;
`;

const SearchIcon = styled.div`
  position: absolute;
  left: ${theme.spacing.md};
  top: 50%;
  transform: translateY(-50%);
  color: ${theme.colors.textMuted};
`;

const TableCard = styled(Card)`
  padding: 0;
  overflow: hidden;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const TableHeader = styled.thead`
  background: ${theme.colors.surface};
  border-bottom: 1px solid ${theme.colors.border};
`;

const TableHeaderCell = styled.th`
  padding: ${theme.spacing.lg};
  text-align: left;
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
  border-bottom: 1px solid ${theme.colors.border};
`;

const TableBody = styled.tbody``;

const TableRow = styled.tr`
  border-bottom: 1px solid ${theme.colors.border};
  transition: background 0.2s ease-in-out;

  &:hover {
    background: ${theme.colors.surfaceHover};
  }

  &:last-child {
    border-bottom: none;
  }
`;

const TableCell = styled.td`
  padding: ${theme.spacing.lg};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
`;

const StatusBadge = styled(Badge)<{ status: 'active' | 'inactive' | 'suspended' }>`
  ${({ status }) => {
    switch (status) {
      case 'active':
        return `
          background: rgba(16, 185, 129, 0.1);
          color: ${theme.colors.success};
          border: 1px solid rgba(16, 185, 129, 0.2);
        `;
      case 'suspended':
        return `
          background: rgba(239, 68, 68, 0.1);
          color: ${theme.colors.error};
          border: 1px solid rgba(239, 68, 68, 0.2);
        `;
      default:
        return `
          background: rgba(107, 114, 128, 0.1);
          color: ${theme.colors.secondary};
          border: 1px solid rgba(107, 114, 128, 0.2);
        `;
    }
  }}
`;

const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.sm};
`;

const ActionButton = styled.button`
  padding: ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.surface};
  border: 1px solid ${theme.colors.border};
  color: ${theme.colors.textSecondary};
  transition: all 0.2s ease-in-out;

  &:hover {
    background: ${theme.colors.surfaceHover};
    color: ${theme.colors.textPrimary};
  }
`;

const RatingStars = styled.div`
  display: flex;
  align-items: center;
  gap: 2px;
`;

const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: ${theme.spacing['3xl']};
  text-align: center;
  color: ${theme.colors.textMuted};
`;

const EmptyStateIcon = styled.div`
  font-size: 48px;
  margin-bottom: ${theme.spacing.lg};
  opacity: 0.5;
`;

const DriversList: React.FC = () => {
  const navigate = useNavigate();
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [filteredDrivers, setFilteredDrivers] = useState<Driver[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const loadDrivers = async () => {
      try {
        const driversData = await mockAPI.getDrivers();
        setDrivers(driversData);
        setFilteredDrivers(driversData);
      } catch (error) {
        console.error('Error loading drivers:', error);
      } finally {
        setLoading(false);
      }
    };

    loadDrivers();
  }, []);

  useEffect(() => {
    let filtered = drivers;

    if (searchTerm) {
      filtered = filtered.filter(driver =>
        driver.employeeId.toLowerCase().includes(searchTerm.toLowerCase()) ||
        driver.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        driver.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        driver.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        driver.licenseNumber.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredDrivers(filtered);
  }, [drivers, searchTerm]);

  const handleCreateDriver = () => {
    navigate('/drivers/create');
  };

  const handleEditDriver = (driverId: string) => {
    navigate(`/drivers/edit/${driverId}`);
  };

  const handleDeleteDriver = (driverId: string) => {
    if (window.confirm('Are you sure you want to delete this driver?')) {
      setDrivers(prev => prev.filter(driver => driver.id !== driverId));
    }
  };

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<span key={i} style={{ color: theme.colors.warning }}>‚òÖ</span>);
    }

    if (hasHalfStar) {
      stars.push(<span key="half" style={{ color: theme.colors.warning }}>‚òÜ</span>);
    }

    const remainingStars = 5 - Math.ceil(rating);
    for (let i = 0; i < remainingStars; i++) {
      stars.push(<span key={`empty-${i}`} style={{ color: theme.colors.textMuted }}>‚òÜ</span>);
    }

    return stars;
  };

  if (loading) {
    return <div>Loading drivers...</div>;
  }

  return (
    <DriversContainer className="fade-in">
      <PageHeader>
        <PageTitle>Drivers</PageTitle>
        <HeaderActions>
          <Button variant="primary" onClick={handleCreateDriver}>
            <FiPlus />
            Add Driver
          </Button>
        </HeaderActions>
      </PageHeader>

      <FiltersCard>
        <SearchContainer>
          <SearchIcon>
            <FiSearch size={18} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search drivers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>
      </FiltersCard>

      {filteredDrivers.length === 0 ? (
        <EmptyState>
          <EmptyStateIcon>üë®‚Äçüíº</EmptyStateIcon>
          <h3>No drivers found</h3>
          <p>Try adjusting your search criteria or add a new driver.</p>
          <Button variant="primary" onClick={handleCreateDriver} style={{ marginTop: '16px' }}>
            <FiPlus />
            Add Driver
          </Button>
        </EmptyState>
      ) : (
        <TableCard>
          <Table>
            <TableHeader>
              <tr>
                <TableHeaderCell>Employee ID</TableHeaderCell>
                <TableHeaderCell>Name</TableHeaderCell>
                <TableHeaderCell>Contact</TableHeaderCell>
                <TableHeaderCell>License</TableHeaderCell>
                <TableHeaderCell>Status</TableHeaderCell>
                <TableHeaderCell>Rating</TableHeaderCell>
                <TableHeaderCell>Trips</TableHeaderCell>
                <TableHeaderCell>Actions</TableHeaderCell>
              </tr>
            </TableHeader>
            <TableBody>
              {filteredDrivers.map((driver) => (
                <TableRow key={driver.id}>
                  <TableCell>
                    <strong>{driver.employeeId}</strong>
                  </TableCell>
                  <TableCell>
                    <div>
                      <strong>{driver.firstName} {driver.lastName}</strong>
                      <div style={{ 
                        fontSize: theme.typography.fontSize.xs, 
                        color: theme.colors.textSecondary,
                        marginTop: '2px'
                      }}>
                        Joined {driver.joinDate.toLocaleDateString()}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <div>{driver.email}</div>
                      <div style={{ 
                        fontSize: theme.typography.fontSize.xs, 
                        color: theme.colors.textSecondary,
                        marginTop: '2px'
                      }}>
                        {driver.phone}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <strong>{driver.licenseNumber}</strong>
                      <div style={{ 
                        fontSize: theme.typography.fontSize.xs, 
                        color: theme.colors.textSecondary,
                        marginTop: '2px'
                      }}>
                        {driver.licenseClass} ‚Ä¢ Exp: {driver.licenseExpiry.toLocaleDateString()}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <StatusBadge status={driver.status}>
                      {driver.status}
                    </StatusBadge>
                  </TableCell>
                  <TableCell>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '4px' }}>
                      <RatingStars>
                        {renderStars(driver.rating)}
                      </RatingStars>
                      <span style={{ fontSize: theme.typography.fontSize.xs, color: theme.colors.textSecondary }}>
                        {driver.rating}
                      </span>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <strong>{driver.totalTrips}</strong>
                      <div style={{ 
                        fontSize: theme.typography.fontSize.xs, 
                        color: theme.colors.textSecondary,
                        marginTop: '2px'
                      }}>
                        {(driver.totalDistance / 1000).toFixed(0)}k miles
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <ActionButtons>
                      <ActionButton onClick={() => handleEditDriver(driver.id)}>
                        <FiEdit size={16} />
                      </ActionButton>
                      <ActionButton onClick={() => handleDeleteDriver(driver.id)}>
                        <FiTrash2 size={16} />
                      </ActionButton>
                    </ActionButtons>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableCard>
      )}
    </DriversContainer>
  );
};

export default DriversList;
