import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { 
  FiMap, 
  FiSave, 
  FiArrowLeft, 
  FiPlus, 
  FiX, 
  FiSearch,
  FiMove,
  FiMapPin
} from 'react-icons/fi';
import { theme } from '../../styles/theme';
import { Card, Button, Input, Select } from '../../styles/GlobalStyles';
import RouteMap from '../../components/Map/RouteMap';
import { Route, Stop } from '../../types';
import { dataService } from '../../services/dataService';

const CreateRouteContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
  max-width: 1200px;
  margin: 0 auto;
`;

const PageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
`;

const BackButton = styled(Button)`
  padding: ${theme.spacing.sm};
`;

const PageTitle = styled.h1`
  font-size: ${theme.typography.fontSize['3xl']};
  font-weight: ${theme.typography.fontWeight.bold};
  color: ${theme.colors.textPrimary};
  margin: 0;
`;

const ContentLayout = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
  height: calc(100vh - 120px);
`;

const RouteInfoCard = styled(Card)`
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: ${theme.spacing.lg};
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  margin-bottom: ${theme.spacing.md};
`;

const MapContainer = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  border-radius: ${theme.borderRadius.lg};
  overflow: hidden;
  box-shadow: ${theme.shadows.lg};
`;


const RouteSummary = styled.div`
  position: absolute;
  bottom: 80px;
  left: ${theme.spacing.lg};
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  border-radius: ${theme.borderRadius.lg};
  backdrop-filter: blur(10px);
  z-index: 1000;
  min-width: 200px;
  max-width: calc(100% - ${theme.spacing.xl} - 320px - ${theme.spacing.lg});
  max-height: calc(100% - ${theme.spacing.xl} * 2);
  overflow-y: auto;
`;

const SummaryItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: ${theme.spacing.xs} 0;
  font-size: ${theme.typography.fontSize.sm};
`;

const SummaryLabel = styled.span`
  opacity: 0.8;
`;

const SummaryValue = styled.span`
  font-weight: ${theme.typography.fontWeight.semibold};
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.sm};
`;

const Label = styled.label`
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
`;

const RequiredIndicator = styled.span`
  color: ${theme.colors.error};
`;

const SectionTitle = styled.h3`
  font-size: ${theme.typography.fontSize.lg};
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textPrimary};
  margin: 0;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;


const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
`;

const EmptyState = styled.div`
  padding: ${theme.spacing.xl};
  color: ${theme.colors.textMuted};
`;

const MapModeButton = styled.button<{ $active: boolean }>`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  background: ${({ $active }) => $active ? theme.colors.primary : theme.colors.surface};
  color: ${({ $active }) => $active ? 'white' : theme.colors.textPrimary};
  border: 2px solid ${({ $active }) => $active ? theme.colors.primary : theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  cursor: pointer;
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.semibold};
  transition: all 0.2s ease-in-out;

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${theme.shadows.md};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const Modal = styled.div<{ $isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: ${({ $isOpen }) => $isOpen ? 'flex' : 'none'};
  align-items: center;
  justify-content: center;
  z-index: 2000;
`;

const ModalContent = styled.div`
  background: ${theme.colors.card};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  max-width: 400px;
  width: 90%;
  box-shadow: ${theme.shadows.xl};
`;

const ModalTitle = styled.h2`
  margin: 0 0 ${theme.spacing.lg} 0;
  font-size: ${theme.typography.fontSize.xl};
  font-weight: ${theme.typography.fontWeight.bold};
  color: ${theme.colors.textPrimary};
`;

const ModalFormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.lg};
`;

const ModalLabel = styled.label`
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textSecondary};
`;

const ModalInput = styled.input`
  padding: ${theme.spacing.md};
  background: ${theme.colors.surface};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.base};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
    box-shadow: 0 0 0 3px ${theme.colors.primary}20;
  }
`;

const ModalCoords = styled.div`
  padding: ${theme.spacing.md};
  background: ${theme.colors.surface};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  color: ${theme.colors.textSecondary};
  margin-bottom: ${theme.spacing.lg};
`;

const ModalButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
`;

const ModalButton = styled.button<{ $variant?: 'primary' | 'secondary' }>`
  padding: ${theme.spacing.sm} ${theme.spacing.lg};
  border: none;
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.semibold};
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  background: ${({ $variant }) => $variant === 'primary' ? theme.colors.primary : theme.colors.surface};
  color: ${({ $variant }) => $variant === 'primary' ? 'white' : theme.colors.textPrimary};
  border: 1px solid ${({ $variant }) => $variant === 'primary' ? theme.colors.primary : theme.colors.border};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${theme.shadows.md};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const CreateRoute: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });
  const [availableStops, setAvailableStops] = useState<Stop[]>([]);
  const [selectedStops, setSelectedStops] = useState<Stop[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredStops, setFilteredStops] = useState<Stop[]>([]);
  const [loading, setLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showAllStops, setShowAllStops] = useState(true);
  const [isManualAddMode, setIsManualAddMode] = useState(false);
  const [manualStopCounter, setManualStopCounter] = useState(1);
  const [isManualStopModalOpen, setIsManualStopModalOpen] = useState(false);
  const [tempManualStop, setTempManualStop] = useState<Stop | null>(null);

  useEffect(() => {
    const loadStops = async () => {
      try {
        const stops = await dataService.getStops();
        setAvailableStops(stops);
        setFilteredStops(stops);
      } catch (error) {
        console.error('Error loading stops:', error);
      } finally {
        setLoading(false);
      }
    };

    loadStops();
  }, []);

  useEffect(() => {
    const filtered = availableStops.filter(stop =>
      stop.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      stop.code.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredStops(filtered);
  }, [availableStops, searchTerm]);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleAddStop = (stop: Stop) => {
    if (!selectedStops.find(s => s.id === stop.id)) {
      setSelectedStops(prev => [...prev, stop]);
    }
  };

  const handleMapStopClick = (stop: Stop) => {
    handleAddStop(stop);
  };


  const handleFitRoute = () => {
    // This will trigger the MapBoundsHandler in RouteMap to fit the route
    // We can force a re-render by updating a dummy state
    setSelectedStops(prev => [...prev]);
  };

  const handleToggleShowAllStops = () => {
    setShowAllStops(prev => !prev);
  };

  const handleRemoveStop = (stopId: string) => {
    setSelectedStops(prev => prev.filter(s => s.id !== stopId));
  };

  const handleMoveStop = (fromIndex: number, toIndex: number) => {
    const newStops = [...selectedStops];
    const [movedStop] = newStops.splice(fromIndex, 1);
    newStops.splice(toIndex, 0, movedStop);
    setSelectedStops(newStops);
  };

  const handleMapClick = (lat: number, lon: number) => {
    if (!isManualAddMode) return;

    // Create a temporary manual stop and show modal
    const tempStop: Stop = {
      id: `manual-${manualStopCounter}`,
      name: `Manual Stop ${manualStopCounter}`,
      code: `MS${manualStopCounter}`,
      location: {
        lat: parseFloat(lat.toFixed(6)),
        lon: parseFloat(lon.toFixed(6))
      }
    };

    setTempManualStop(tempStop);
    setIsManualStopModalOpen(true);
  };

  const handleManualStopModalChange = (field: string, value: string) => {
    if (!tempManualStop) return;

    setTempManualStop(prev => 
      prev ? { ...prev, [field]: value } : null
    );
  };

  const handleConfirmManualStop = () => {
    if (!tempManualStop || !tempManualStop.name || !tempManualStop.code) {
      alert('Please enter both name and code for the stop.');
      return;
    }

    // Add to selected stops
    setSelectedStops(prev => [...prev, tempManualStop]);
    setManualStopCounter(prev => prev + 1);

    // Close modal and reset
    setIsManualStopModalOpen(false);
    setTempManualStop(null);
  };

  const handleCancelManualStop = () => {
    setIsManualStopModalOpen(false);
    setTempManualStop(null);
  };

  const handleToggleManualMode = () => {
    setIsManualAddMode(prev => !prev);
    setTempManualStop(null);
    setIsManualStopModalOpen(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.code) {
      alert('Please fill in all required fields and select at least 2 stops.');
      return;
    }

    setIsSubmitting(true);

    try {
      const newRoute = {
        name: formData.name,
        // code: formData.code,
        code: formData.code,
        stops: selectedStops.map((stop, idx) => ({...stop, order: idx}))
      };

      console.log('Creating route:', newRoute);

      await dataService.createRoute(newRoute);


      navigate('/routes');
    } catch (error) {
      console.error('Error creating route:', error);
      alert('Error creating route. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    navigate('/routes');
  };

  if (loading) {
    return <div>Loading stops...</div>;
  }

  return (
    <CreateRouteContainer className="fade-in">
      <PageHeader>
        <BackButton variant="secondary" onClick={handleCancel}>
          <FiArrowLeft />
        </BackButton>
        <PageTitle>Create New Route</PageTitle>
      </PageHeader>

      <ContentLayout>
        {/* Route Information - Compact Header */}
        <RouteInfoCard>
          <FormGroup>
            <Label>
              Route Name <RequiredIndicator>*</RequiredIndicator>
            </Label>
            <Input
              type="text"
              placeholder="Enter route name"
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>
              Route Code <RequiredIndicator>*</RequiredIndicator>
            </Label>
            <Input
              type="text"
              placeholder="Enter route code (e.g., RT001)"
              value={formData.code}
              onChange={(e) => handleInputChange('code', e.target.value)}
              required
            />
          </FormGroup>
        </RouteInfoCard>

        {/* Map-First Layout */}
        <MapContainer>
          <RouteMap
            stops={availableStops}
            selectedStops={selectedStops}
            showRoute={true}
            height="100%"
            onStopClick={handleMapStopClick}
            onFitRoute={handleFitRoute}
            showAllStops={showAllStops}
            onToggleShowAllStops={handleToggleShowAllStops}
            searchTerm={searchTerm}
            onSearchChange={setSearchTerm}
            filteredStops={filteredStops}
            onAddStop={handleAddStop}
            onRemoveStop={handleRemoveStop}
            onMapClick={handleMapClick}
            isManualAddMode={isManualAddMode}
            onToggleManualMode={handleToggleManualMode}
          />

          {/* Route Summary */}
          <RouteSummary>
            <SummaryItem>
              <SummaryLabel>Stops:</SummaryLabel>
              <SummaryValue>{selectedStops.length}</SummaryValue>
            </SummaryItem>
            {selectedStops.length >= 2 && (
              <>
                <SummaryItem>
                  <SummaryLabel>Start:</SummaryLabel>
                  <SummaryValue>{selectedStops[0]?.name}</SummaryValue>
                </SummaryItem>
                <SummaryItem>
                  <SummaryLabel>End:</SummaryLabel>
                  <SummaryValue>{selectedStops[selectedStops.length - 1]?.name}</SummaryValue>
                </SummaryItem>
              </>
            )}
          </RouteSummary>
        </MapContainer>

        {/* Action Buttons */}
        <form onSubmit={handleSubmit}>
          <ActionButtons>
            <Button 
              type="button" 
              variant="secondary" 
              onClick={handleCancel}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              variant="primary"
              disabled={isSubmitting || selectedStops.length < 2}
            >
              <FiSave />
              {isSubmitting ? 'Creating...' : 'Create Route'}
            </Button>
          </ActionButtons>
        </form>
      </ContentLayout>

      {/* Manual Stop Modal */}
      <Modal $isOpen={isManualStopModalOpen}>
        <ModalContent>
          <ModalTitle>Add Manual Stop</ModalTitle>
          <ModalFormGroup>
            <ModalLabel>Name:</ModalLabel>
            <ModalInput
              type="text"
              placeholder="Enter stop name"
              value={tempManualStop?.name || ''}
              onChange={(e) => handleManualStopModalChange('name', e.target.value)}
            />
          </ModalFormGroup>
          <ModalFormGroup>
            <ModalLabel>Code:</ModalLabel>
            <ModalInput
              type="text"
              placeholder="Enter stop code"
              value={tempManualStop?.code || ''}
              onChange={(e) => handleManualStopModalChange('code', e.target.value)}
            />
          </ModalFormGroup>
          <ModalCoords>
            <p>Coordinates: Lat: {tempManualStop?.location.lat.toFixed(6)}, Lon: {tempManualStop?.location.lon.toFixed(6)}</p>
          </ModalCoords>
          <ModalButtons>
            <ModalButton variant="secondary" onClick={handleCancelManualStop}>Cancel</ModalButton>
            <ModalButton variant="primary" onClick={handleConfirmManualStop}>Add Stop</ModalButton>
          </ModalButtons>
        </ModalContent>
      </Modal>
    </CreateRouteContainer>
  );
};

export default CreateRoute;
