import React, { useState, useEffect } from 'react';
import { FiCalendar, FiMap, FiCheck, FiUsers, FiTruck, FiRefreshCw } from 'react-icons/fi';
import { Button } from '../../styles/GlobalStyles';
import { 
  SearchContainer,
  SearchInput,
  Table,
  TableHeader,
  TableHeaderCell,
  TableBody,
  TableRow,
  TableCell
} from '../../components/Common/CommonStyles';
import styled from 'styled-components';
import { theme } from '../../styles/theme';
import AssignmentModal from '../../components/Modals/AssignmentModal';
import { dataService } from '../../services/dataService';
import { Route, Duty } from '../../types';

const AssignmentContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
`;

const SelectionControls = styled.div`
  display: flex;
  gap: ${theme.spacing.lg};
  margin-bottom: ${theme.spacing.lg};
  flex-wrap: wrap;
  align-items: center;
`;

const DateSelector = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const DateInput = styled.input`
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.card};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
  }
`;

const RouteSelect = styled.select`
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  background: ${theme.colors.card};
  color: ${theme.colors.textPrimary};
  font-size: ${theme.typography.fontSize.sm};
  min-width: 200px;

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary};
  }
`;

const Checkbox = styled.input`
  width: 18px;
  height: 18px;
  accent-color: ${theme.colors.primary};
`;

const FloatingActionBar = styled.div<{ visible: boolean }>`
  position: fixed;
  bottom: ${theme.spacing.xl};
  right: ${theme.spacing.xl};
  background: ${theme.colors.card};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.lg};
  box-shadow: ${theme.shadows.lg};
  display: ${({ visible }) => visible ? 'flex' : 'none'};
  align-items: center;
  gap: ${theme.spacing.md};
  z-index: ${theme.zIndex.modal};
  transition: all 0.3s ease;
`;

const SelectionCount = styled.div`
  color: ${theme.colors.textPrimary};
  font-weight: ${theme.typography.fontWeight.medium};
`;

const AssignButton = styled(Button)`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const EmptyState = styled.div`
  text-align: center;
  padding: ${theme.spacing['3xl']};
  color: ${theme.colors.textSecondary};
`;

const LoadingState = styled.div`
  text-align: center;
  padding: ${theme.spacing.xl};
  color: ${theme.colors.textSecondary};
`;

const UnsavedChangesWarning = styled.div`
  background: ${theme.colors.warning}20;
  border: 1px solid ${theme.colors.warning};
  border-radius: ${theme.borderRadius.md};
  padding: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.lg};
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: ${theme.spacing.md};
`;

const WarningText = styled.div`
  color: ${theme.colors.warning};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.sm};
`;

const SaveButton = styled.button`
  background: ${theme.colors.primary};
  color: white;
  border: none;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};

  &:hover:not(:disabled) {
    background: ${theme.colors.primaryHover};
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const DiscardButton = styled.button`
  background: transparent;
  color: ${theme.colors.textSecondary};
  border: 1px solid ${theme.colors.border};
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${theme.colors.surfaceHover};
    border-color: ${theme.colors.textSecondary};
  }
`;

const ModifiedRow = styled.tr<{ isModified: boolean; isNewlyAssigned: boolean }>`
  background: ${props => {
    if (props.isNewlyAssigned) return `${theme.colors.success}15`;
    if (props.isModified) return `${theme.colors.warning}15`;
    return 'transparent';
  }};
  border-left: 3px solid ${props => {
    if (props.isNewlyAssigned) return theme.colors.success;
    if (props.isModified) return theme.colors.warning;
    return 'transparent';
  }};
`;

const StatusIndicator = styled.span<{ isModified: boolean; isNewlyAssigned: boolean }>`
  display: inline-flex;
  align-items: center;
  gap: ${theme.spacing.xs};

  &::before {
    content: '';
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: ${props => {
      if (props.isNewlyAssigned) return theme.colors.success;
      if (props.isModified) return theme.colors.warning;
      return 'transparent';
    }};
  }
`;

interface Trip {
  id: string;
  tripNumber: string;
  routeName: string;
  scheduledStartTime: string;
  scheduledEndTime: string;
  status: 'planned' | 'in-progress' | 'completed';
  assignedDriver?: string;
  assignedVehicle?: string;
  // Assignment tracking
  originalAssignedDriver?: string;
  originalAssignedVehicle?: string;
  isModified?: boolean;
  isNewlyAssigned?: boolean;
  // Duty-specific fields
  dutyId: string;
  driverId?: string;
  vehicleId?: string;
  driver?: any; // Will be the actual driver object from API
  vehicle?: any; // Will be the actual vehicle object from API
}

const AssignmentInterface: React.FC = () => {
  const [selectedDate, setSelectedDate] = useState(() => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  });
  const [selectedRoute, setSelectedRoute] = useState('');
  const [selectedDriver, setSelectedDriver] = useState('');
  const [selectedVehicle, setSelectedVehicle] = useState('');
  const [trips, setTrips] = useState<Trip[]>([]);
  const [filteredTrips, setFilteredTrips] = useState<Trip[]>([]);
  const [selectedTrips, setSelectedTrips] = useState<Set<string>>(new Set());
  const [loading, setLoading] = useState(false);
  const [showAssignmentModal, setShowAssignmentModal] = useState(false);

  // State management for changes
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [originalTrips, setOriginalTrips] = useState<Trip[]>([]);
  const [isSaving, setIsSaving] = useState(false);

  // Routes state
  const [routes, setRoutes] = useState<Route[]>([]);
  const [routesLoading, setRoutesLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Drivers and vehicles state for filtering
  const [drivers, setDrivers] = useState<Driver[]>([]);
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [driversLoading, setDriversLoading] = useState(true);
  const [vehiclesLoading, setVehiclesLoading] = useState(true);

  // Load routes on component mount
  useEffect(() => {
    const loadRoutes = async () => {
      try {
        setRoutesLoading(true);
        setError(null);
        const routesData = await dataService.getRoutes();
        setRoutes(routesData);
      } catch (error) {
        console.error('Error loading routes:', error);
        setError('Failed to load routes. Please try again.');
      } finally {
        setRoutesLoading(false);
      }
    };

    loadRoutes();
  }, []);

  useEffect(() => {
    if (selectedDate && selectedRoute) {
      loadTrips();
    } else {
      setTrips([]);
      setFilteredTrips([]);
    }
  }, [selectedDate, selectedRoute]);

  useEffect(() => {
    setSelectedTrips(new Set()); // Clear selection when trips change
  }, [trips]);

  const loadTrips = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load trip duties for the selected route and date
      const duties = await dataService.getDuties({
        routeId: selectedRoute,
        date: selectedDate,
        dutyType: 'TRIP'
      });

      // Convert duties to trips format
      const tripsData: Trip[] = duties.map((duty, index) => {
        const routeName = duty.tripDuties?.[0]?.route?.name || 'Unknown Route';
        const driverName = (duty.driver as any)?.user ? `${(duty.driver as any).user.name}` : undefined;
        const vehicleName = duty.vehicle ? `${(duty.vehicle as any).model?.make || ''} ${(duty.vehicle as any).plateNo || ''}`.trim() : undefined;

        return {
          id: duty.id,
          dutyId: duty.id,
          tripNumber: `T-${String(index + 1).padStart(3, '0')}`,
          routeName,
          scheduledStartTime: new Date(duty.startTime).toLocaleTimeString('en-US', { 
            hour: '2-digit', 
            minute: '2-digit',
            hour12: false 
          }),
          scheduledEndTime: new Date(duty.endTime).toLocaleTimeString('en-US', { 
            hour: '2-digit', 
            minute: '2-digit',
            hour12: false 
          }),
          status: 'planned' as const,
          assignedDriver: driverName,
          assignedVehicle: vehicleName,
          driverId: duty.driverId,
          vehicleId: duty.vehicleId,
          driver: duty.driver,
          vehicle: duty.vehicle,
          // Initialize tracking fields
          originalAssignedDriver: driverName,
          originalAssignedVehicle: vehicleName,
          isModified: false,
          isNewlyAssigned: false
        };
      });

      setTrips(tripsData);
      setFilteredTrips(tripsData);
      setOriginalTrips([...tripsData]); // Store original data
      setHasUnsavedChanges(false); // Reset change tracking
    } catch (error) {
      console.error('Error loading trips:', error);
      setError('Failed to load trip duties. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSelectTrip = (tripId: string) => {
    const newSelected = new Set(selectedTrips);
    if (newSelected.has(tripId)) {
      newSelected.delete(tripId);
    } else {
      newSelected.add(tripId);
    }
    setSelectedTrips(newSelected);
  };

  const handleSelectAll = () => {
    if (selectedTrips.size === filteredTrips.length) {
      setSelectedTrips(new Set());
    } else {
      setSelectedTrips(new Set(filteredTrips.map(trip => trip.id)));
    }
  };

  const handleAssign = () => {
    setShowAssignmentModal(true);
  };

  const handleAssignmentComplete = (assignmentData: any) => {
    console.log('Assignment data:', assignmentData);
    console.log('Selected trips:', Array.from(selectedTrips));
    console.log('Selected drivers:', assignmentData.selectedDrivers.map((d: any) => d.name));
    console.log('Selected vehicles:', assignmentData.selectedVehicles.map((v: any) => `${v.plateNo} - ${v.fleetNo}`));

    // Apply assignments to selected trips
    const updatedTrips = trips.map(trip => {
      if (selectedTrips.has(trip.id)) {
        // Simple round-robin assignment for demo
        const driverIndex = Array.from(selectedTrips).indexOf(trip.id) % assignmentData.selectedDrivers.length;
        const vehicleIndex = Array.from(selectedTrips).indexOf(trip.id) % assignmentData.selectedVehicles.length;

        const assignedDriver = assignmentData.selectedDrivers[driverIndex];
        const assignedVehicle = assignmentData.selectedVehicles[vehicleIndex];

        return {
          ...trip,
          assignedDriver: assignedDriver.name,
          assignedVehicle: `${assignedVehicle.plateNo} - ${assignedVehicle.fleetNo}`,
          driverId: assignedDriver.id,
          vehicleId: assignedVehicle.id,
          driver: assignedDriver,
          vehicle: assignedVehicle,
          isModified: true,
          isNewlyAssigned: !trip.assignedDriver && !trip.assignedVehicle
        };
      }
      return trip;
    });

    setTrips(updatedTrips);
    setFilteredTrips(updatedTrips);
    setHasUnsavedChanges(true);
    setSelectedTrips(new Set()); // Clear selection after assignment
  };

  const handleSaveChanges = async () => {
    try {
      setIsSaving(true);

      // Get modified trips
      const modifiedTrips = trips.filter(trip => trip.isModified);

      if (modifiedTrips.length === 0) {
        console.log('No changes to save');
        return;
      }

      // Prepare assignments for bulk update
      const assignments = modifiedTrips.map(trip => ({
        dutyId: trip.dutyId,
        driverId: trip.driverId,
        vehicleId: trip.vehicleId
      }));

      // Call bulk update API
      await dataService.bulkUpdateDutyAssignments({ assignments });

      // Update all trips to reflect saved state
      const updatedTrips = trips.map(trip => ({
        ...trip,
        originalAssignedDriver: trip.assignedDriver,
        originalAssignedVehicle: trip.assignedVehicle,
        isModified: false,
        isNewlyAssigned: false
      }));

      setTrips(updatedTrips);
      setFilteredTrips(updatedTrips);
      setOriginalTrips(updatedTrips);
      setHasUnsavedChanges(false);

      console.log('Changes saved successfully');
    } catch (error) {
      console.error('Error saving changes:', error);
      alert('Failed to save changes. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const handleDiscardChanges = () => {
    if (window.confirm('Are you sure you want to discard all unsaved changes?')) {
      setTrips([...originalTrips]);
      setFilteredTrips([...originalTrips]);
      setHasUnsavedChanges(false);
      setSelectedTrips(new Set());
    }
  };

  const formatTime = (time: string) => {
    return time;
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'in-progress':
        return 'In Progress';
      case 'completed':
        return 'Completed';
      default:
        return 'Planned';
    }
  };

  const isAllSelected = filteredTrips.length > 0 && selectedTrips.size === filteredTrips.length;
  const isIndeterminate = selectedTrips.size > 0 && selectedTrips.size < filteredTrips.length;


  return (
    <AssignmentContainer>
      <SelectionControls>
        <DateSelector>
          <FiCalendar size={20} color={theme.colors.primary} />
          <DateInput
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </DateSelector>

        <RouteSelect value={selectedRoute} onChange={(e) => setSelectedRoute(e.target.value)} disabled={routesLoading}>
          <option value="">{routesLoading ? 'Loading routes...' : 'Select a route...'}</option>
          {routes.map(route => (
            <option key={route.id} value={route.id}>{route.name}</option>
          ))}
        </RouteSelect>
      </SelectionControls>

      {hasUnsavedChanges && (
        <UnsavedChangesWarning>
          <WarningText>
            <FiRefreshCw size={16} />
            You have unsaved changes. Save your changes to keep them.
          </WarningText>
          <ActionButtons>
            <DiscardButton onClick={handleDiscardChanges}>
              Discard Changes
            </DiscardButton>
            <SaveButton onClick={handleSaveChanges} disabled={isSaving}>
              {isSaving ? (
                <>
                  <div style={{ width: 12, height: 12, border: '2px solid white', borderTop: '2px solid transparent', borderRadius: '50%', animation: 'spin 1s linear infinite' }} />
                  Saving...
                </>
              ) : (
                <>
                  <FiRefreshCw size={14} />
                  Save Changes
                </>
              )}
            </SaveButton>
          </ActionButtons>
        </UnsavedChangesWarning>
      )}

      {error && (
        <div style={{
          padding: theme.spacing.md,
          backgroundColor: theme.colors.error + '20',
          border: `1px solid ${theme.colors.error}`,
          borderRadius: theme.borderRadius.md,
          color: theme.colors.error,
          marginBottom: theme.spacing.lg
        }}>
          {error}
        </div>
      )}

      {loading ? (
        <LoadingState>
          <p>Loading trips...</p>
        </LoadingState>
      ) : !selectedRoute ? (
        <EmptyState>
          <FiMap size={48} color={theme.colors.textMuted} style={{ marginBottom: theme.spacing.lg }} />
          <h3 style={{ color: theme.colors.textPrimary, marginBottom: theme.spacing.sm }}>
            Select a Route
          </h3>
          <p>Please select a route to view and manage trip assignments</p>
        </EmptyState>
      ) : filteredTrips.length === 0 ? (
        <EmptyState>
          <FiMap size={48} color={theme.colors.textMuted} style={{ marginBottom: theme.spacing.lg }} />
          <h3 style={{ color: theme.colors.textPrimary, marginBottom: theme.spacing.sm }}>
            No Trips Found
          </h3>
          <p>No trips found for {routes.find(r => r.id === selectedRoute)?.name} on {new Date(selectedDate).toLocaleDateString()}</p>
        </EmptyState>
      ) : (
        <>
          <Table>
            <TableHeader>
              <TableHeaderCell>
                <Checkbox
                  type="checkbox"
                  checked={isAllSelected}
                  ref={(input) => {
                    if (input) input.indeterminate = isIndeterminate;
                  }}
                  onChange={handleSelectAll}
                />
              </TableHeaderCell>
              <TableHeaderCell>Time</TableHeaderCell>
              <TableHeaderCell>Trip #</TableHeaderCell>
              <TableHeaderCell>Route</TableHeaderCell>
              <TableHeaderCell>Driver</TableHeaderCell>
              <TableHeaderCell>Vehicle</TableHeaderCell>
              <TableHeaderCell>Status</TableHeaderCell>
            </TableHeader>
            <TableBody>
              {filteredTrips.map((trip) => (
                <ModifiedRow 
                  key={trip.id} 
                  isModified={trip.isModified || false} 
                  isNewlyAssigned={trip.isNewlyAssigned || false}
                >
                  <TableCell>
                    <Checkbox
                      type="checkbox"
                      checked={selectedTrips.has(trip.id)}
                      onChange={() => handleSelectTrip(trip.id)}
                    />
                  </TableCell>
                  <TableCell>
                    {formatTime(trip.scheduledStartTime)} - {formatTime(trip.scheduledEndTime)}
                  </TableCell>
                  <TableCell>{trip.tripNumber}</TableCell>
                  <TableCell>{trip.routeName}</TableCell>
                  <TableCell>
                    <StatusIndicator isModified={trip.isModified || false} isNewlyAssigned={trip.isNewlyAssigned || false}>
                      {trip.assignedDriver ? (
                        <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.xs }}>
                          <FiUsers size={14} color={theme.colors.success} />
                          {trip.assignedDriver}
                        </div>
                      ) : (
                        <span style={{ color: theme.colors.textMuted }}>Unassigned</span>
                      )}
                    </StatusIndicator>
                  </TableCell>
                  <TableCell>
                    <StatusIndicator isModified={trip.isModified || false} isNewlyAssigned={trip.isNewlyAssigned || false}>
                      {trip.assignedVehicle ? (
                        <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.xs }}>
                          <FiTruck size={14} color={theme.colors.success} />
                          {trip.assignedVehicle}
                        </div>
                      ) : (
                        <span style={{ color: theme.colors.textMuted }}>Unassigned</span>
                      )}
                    </StatusIndicator>
                  </TableCell>
                  <TableCell>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      fontSize: '12px',
                      fontWeight: '500',
                      background: trip.status === 'planned' ? theme.colors.surface : theme.colors.success,
                      color: 'white'
                    }}>
                      {getStatusLabel(trip.status)}
                    </span>
                  </TableCell>
                </ModifiedRow>
              ))}
            </TableBody>
          </Table>

          <FloatingActionBar visible={selectedTrips.size > 0}>
            <SelectionCount>
              {selectedTrips.size} trip{selectedTrips.size !== 1 ? 's' : ''} selected
            </SelectionCount>
            <AssignButton variant="primary" onClick={handleAssign}>
              <FiUsers />
              Assign
            </AssignButton>
          </FloatingActionBar>
        </>
      )}

      <AssignmentModal
        isOpen={showAssignmentModal}
        onClose={() => setShowAssignmentModal(false)}
        selectedTrips={Array.from(selectedTrips)}
        onAssign={handleAssignmentComplete}
      />
    </AssignmentContainer>
  );
};

export default AssignmentInterface;
