import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { 
  FiMap, 
  FiSave, 
  FiArrowLeft, 
  FiPlus, 
  FiX, 
  FiSearch,
  FiMove,
  FiMapPin
} from 'react-icons/fi';
import { theme } from '../../styles/theme';
import { Card, Button, Input, Select } from '../../styles/GlobalStyles';
import RouteMap from '../../components/Map/RouteMap';
import { Route, Stop } from '../../types';
import { dataService } from '../../services/dataService';

const CreateRouteContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xl};
  max-width: 1200px;
  margin: 0 auto;
`;

const PageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
`;

const BackButton = styled(Button)`
  padding: ${theme.spacing.sm};
`;

const PageTitle = styled.h1`
  font-size: ${theme.typography.fontSize['3xl']};
  font-weight: ${theme.typography.fontWeight.bold};
  color: ${theme.colors.textPrimary};
  margin: 0;
`;

const ContentLayout = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
  height: calc(100vh - 120px);
`;

const RouteInfoCard = styled(Card)`
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: ${theme.spacing.lg};
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  margin-bottom: ${theme.spacing.md};
`;

const MapContainer = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  border-radius: ${theme.borderRadius.lg};
  overflow: hidden;
  box-shadow: ${theme.shadows.lg};
`;

const FloatingPanel = styled.div<{ isOpen: boolean }>`
  position: absolute;
  top: ${theme.spacing.lg};
  right: ${theme.spacing.lg};
  width: 320px;
  max-height: calc(100vh - 200px);
  background: ${theme.colors.card};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.lg};
  box-shadow: ${theme.shadows.xl};
  z-index: 1000;
  transform: translateX(${({ isOpen }) => isOpen ? '0' : 'calc(100% + 20px)'});
  transition: transform 0.3s ease-in-out;
  overflow: hidden;
`;

const PanelHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  border-bottom: 1px solid ${theme.colors.border};
  background: ${theme.colors.surface};
`;

const PanelContent = styled.div`
  display: flex;
  flex-direction: column;
  height: calc(100% - 60px);
`;

const ToggleButton = styled(Button)`
  position: absolute;
  top: ${theme.spacing.lg};
  right: ${theme.spacing.lg};
  z-index: 1001;
  padding: ${theme.spacing.md};
  border-radius: 50%;
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: ${theme.shadows.lg};
`;

const RouteSummary = styled.div`
  position: absolute;
  bottom: ${theme.spacing.lg};
  left: ${theme.spacing.lg};
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  border-radius: ${theme.borderRadius.lg};
  backdrop-filter: blur(10px);
  z-index: 1000;
  min-width: 200px;
`;

const SummaryItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: ${theme.spacing.xs} 0;
  font-size: ${theme.typography.fontSize.sm};
`;

const SummaryLabel = styled.span`
  opacity: 0.8;
`;

const SummaryValue = styled.span`
  font-weight: ${theme.typography.fontWeight.semibold};
`;

const ScrollableSection = styled.div`
  flex: 1;
  overflow-y: auto;
  padding-right: ${theme.spacing.sm};

  /* Custom scrollbar */
  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: ${theme.colors.surface};
    border-radius: 3px;
  }

  &::-webkit-scrollbar-thumb {
    background: ${theme.colors.secondary};
    border-radius: 3px;
  }
`;

const FormSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.md};
`;

const SectionTitle = styled.h3`
  font-size: ${theme.typography.fontSize.lg};
  font-weight: ${theme.typography.fontWeight.semibold};
  color: ${theme.colors.textPrimary};
  margin: 0;
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
`;

const FormRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${theme.spacing.md};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.sm};
`;

const Label = styled.label`
  font-size: ${theme.typography.fontSize.sm};
  font-weight: ${theme.typography.fontWeight.medium};
  color: ${theme.colors.textPrimary};
`;

const RequiredIndicator = styled.span`
  color: ${theme.colors.error};
`;

const StopSearch = styled.div`
  position: relative;
`;

const SearchInput = styled(Input)`
  padding-left: 40px;
  width: 100%;
`;

const SearchIcon = styled.div`
  position: absolute;
  left: ${theme.spacing.md};
  top: 50%;
  transform: translateY(-50%);
  color: ${theme.colors.textMuted};
  z-index: 1;
`;

const SearchDropdown = styled.div<{ isOpen: boolean }>`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  background: ${theme.colors.card};
  border: 1px solid ${theme.colors.border};
  border-top: none;
  border-radius: 0 0 ${theme.borderRadius.md} ${theme.borderRadius.md};
  box-shadow: ${theme.shadows.lg};
  z-index: 1000;
  max-height: 200px;
  overflow-y: auto;
  transform: translateY(${({ isOpen }) => isOpen ? '0' : '-10px'});
  opacity: ${({ isOpen }) => isOpen ? '1' : '0'};
  visibility: ${({ isOpen }) => isOpen ? 'visible' : 'hidden'};
  transition: all 0.2s ease-in-out;
`;

const DropdownItem = styled.div<{ selected?: boolean }>`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  border-bottom: 1px solid ${theme.colors.border};

  ${({ selected }) => selected ? `
    background: rgba(59, 130, 246, 0.1);
    color: ${theme.colors.primary};
  ` : `
    background: ${theme.colors.surface};

    &:hover {
      background: ${theme.colors.surfaceHover};
    }
  `}

  &:last-child {
    border-bottom: none;
  }
`;

const DropdownItemInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const DropdownItemName = styled.div`
  font-weight: ${theme.typography.fontWeight.medium};
  color: inherit;
  font-size: ${theme.typography.fontSize.sm};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const DropdownItemCode = styled.div`
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.fontSize.xs};
  margin-top: ${theme.spacing.xs};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const NoResults = styled.div`
  padding: ${theme.spacing.md};
  text-align: center;
  color: ${theme.colors.textMuted};
  font-size: ${theme.typography.fontSize.sm};
`;

const StopsList = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: ${theme.spacing.sm};
`;

const StopItem = styled.div<{ selected?: boolean }>`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  margin-bottom: ${theme.spacing.xs};
  border-radius: ${theme.borderRadius.md};
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  border: 1px solid transparent;

  ${({ selected }) => selected ? `
    background: rgba(59, 130, 246, 0.1);
    border-color: ${theme.colors.primary};
    color: ${theme.colors.primary};
  ` : `
    background: ${theme.colors.surface};
    border-color: ${theme.colors.border};

    &:hover {
      background: ${theme.colors.surfaceHover};
      border-color: ${theme.colors.primary};
      transform: translateY(-1px);
      box-shadow: ${theme.shadows.sm};
    }
  `}
`;

const StopInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const StopName = styled.div`
  font-weight: ${theme.typography.fontWeight.medium};
  color: inherit;
  font-size: ${theme.typography.fontSize.sm};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const StopDetails = styled.div`
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.fontSize.xs};
  margin-top: ${theme.spacing.xs};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;


const SelectedStops = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.xs};
  flex: 1;
  overflow-y: auto;
  padding: ${theme.spacing.sm};
`;

const SelectedStopItem = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  background: ${theme.colors.surface};
  border: 1px solid ${theme.colors.border};
  border-radius: ${theme.borderRadius.md};
  transition: all 0.2s ease-in-out;

  &:hover {
    background: ${theme.colors.surfaceHover};
    transform: translateY(-1px);
    box-shadow: ${theme.shadows.sm};
  }
`;

const StopOrder = styled.div`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: ${theme.colors.primary};
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: ${theme.typography.fontWeight.bold};
  font-size: ${theme.typography.fontSize.xs};
  flex-shrink: 0;
`;

const DragHandle = styled.div`
  color: ${theme.colors.textMuted};
  cursor: grab;
  padding: ${theme.spacing.xs};

  &:active {
    cursor: grabbing;
  }

  &:hover {
    color: ${theme.colors.textPrimary};
  }
`;

const RemoveButton = styled.button`
  color: ${theme.colors.error};
  padding: ${theme.spacing.xs};
  border-radius: ${theme.borderRadius.sm};
  transition: all 0.2s ease-in-out;

  &:hover {
    background: rgba(239, 68, 68, 0.1);
    transform: scale(1.1);
  }
`;

const ActionButtons = styled.div`
  display: flex;
  gap: ${theme.spacing.md};
  justify-content: flex-end;
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid ${theme.colors.border};
`;

const EmptyState = styled.div`
  text-align: center;
  padding: ${theme.spacing.xl};
  color: ${theme.colors.textMuted};
`;

const CreateRoute: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });
  const [availableStops, setAvailableStops] = useState<Stop[]>([]);
  const [selectedStops, setSelectedStops] = useState<Stop[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredStops, setFilteredStops] = useState<Stop[]>([]);
  const [loading, setLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [isSearchDropdownOpen, setIsSearchDropdownOpen] = useState(false);

  useEffect(() => {
    const loadStops = async () => {
      try {
        const stops = await dataService.getStops();
        setAvailableStops(stops);
        setFilteredStops(stops);
      } catch (error) {
        console.error('Error loading stops:', error);
      } finally {
        setLoading(false);
      }
    };

    loadStops();
  }, []);

  useEffect(() => {
    const filtered = availableStops.filter(stop =>
      stop.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      stop.code.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredStops(filtered);
  }, [availableStops, searchTerm]);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleAddStop = (stop: Stop) => {
    if (!selectedStops.find(s => s.id === stop.id)) {
      setSelectedStops(prev => [...prev, stop]);
    }
  };

  const handleMapStopClick = (stop: Stop) => {
    handleAddStop(stop);
  };

  const handleRemoveStop = (stopId: string) => {
    setSelectedStops(prev => prev.filter(s => s.id !== stopId));
  };

  const handleMoveStop = (fromIndex: number, toIndex: number) => {
    const newStops = [...selectedStops];
    const [movedStop] = newStops.splice(fromIndex, 1);
    newStops.splice(toIndex, 0, movedStop);
    setSelectedStops(newStops);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.code) {
      alert('Please fill in all required fields and select at least 2 stops.');
      return;
    }

    setIsSubmitting(true);

    try {
      const newRoute = {
        name: formData.name,
        // code: formData.code,
        code: formData.code,
        stops: selectedStops.map((stop, idx) => ({...stop, order: idx}))
      };

      console.log('Creating route:', newRoute);

      await dataService.createRoute(newRoute);


      navigate('/routes');
    } catch (error) {
      console.error('Error creating route:', error);
      alert('Error creating route. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    navigate('/routes');
  };

  if (loading) {
    return <div>Loading stops...</div>;
  }

  return (
    <CreateRouteContainer className="fade-in">
      <PageHeader>
        <BackButton variant="secondary" onClick={handleCancel}>
          <FiArrowLeft />
        </BackButton>
        <PageTitle>Create New Route</PageTitle>
      </PageHeader>

      <ContentLayout>
        {/* Route Information - Compact Header */}
        <RouteInfoCard>
          <FormGroup>
            <Label>
              Route Name <RequiredIndicator>*</RequiredIndicator>
            </Label>
            <Input
              type="text"
              placeholder="Enter route name"
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              required
            />
          </FormGroup>

          <FormGroup>
            <Label>
              Route Code <RequiredIndicator>*</RequiredIndicator>
            </Label>
            <Input
              type="text"
              placeholder="Enter route code (e.g., RT001)"
              value={formData.code}
              onChange={(e) => handleInputChange('code', e.target.value)}
              required
            />
          </FormGroup>
        </RouteInfoCard>

        {/* Map-First Layout */}
        <MapContainer>
          <RouteMap
            stops={availableStops}
            selectedStops={selectedStops}
            showRoute={true}
            height="100%"
            onStopClick={handleMapStopClick}
          />

          {/* Floating Toggle Button */}
          <ToggleButton
            variant="primary"
            onClick={() => setIsPanelOpen(!isPanelOpen)}
          >
            <FiMapPin />
          </ToggleButton>

          {/* Floating Stops Panel */}
          <FloatingPanel isOpen={isPanelOpen}>
            <PanelHeader>
              <SectionTitle>
                <FiMapPin />
                Route Stops ({selectedStops.length})
              </SectionTitle>
              <Button
                variant="secondary"
                onClick={() => setIsPanelOpen(false)}
                style={{ padding: '4px' }}
              >
                <FiX />
              </Button>
            </PanelHeader>

            <PanelContent>
              {/* Search */}
              <div style={{ padding: `${theme.spacing.md} ${theme.spacing.lg}` }}>
                <StopSearch>
                  <SearchIcon>
                    <FiSearch size={16} />
                  </SearchIcon>
                  <SearchInput
                    type="text"
                    placeholder="Search stops..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </StopSearch>
              </div>

              {/* Available Stops */}
              <div style={{ padding: `0 ${theme.spacing.lg}`, marginBottom: theme.spacing.md }}>
                <div style={{ fontSize: theme.typography.fontSize.sm, fontWeight: theme.typography.fontWeight.medium, color: theme.colors.textSecondary, marginBottom: theme.spacing.sm }}>
                  Available Stops
                </div>
                <StopsList>
                  {filteredStops.map((stop) => {
                    const isSelected = selectedStops.some(s => s.id === stop.id);
                    return (
                      <StopItem
                        key={stop.id}
                        selected={isSelected}
                        onClick={() => !isSelected && handleAddStop(stop)}
                      >
                        <StopInfo>
                          <StopName>{stop.name}</StopName>
                          <StopDetails>{stop.code}</StopDetails>
                        </StopInfo>
                        {!isSelected && <FiPlus color={theme.colors.primary} size={16} />}
                      </StopItem>
                    );
                  })}
                </StopsList>
              </div>

              {/* Selected Stops */}
              <div style={{ padding: `0 ${theme.spacing.lg}`, flex: 1, display: 'flex', flexDirection: 'column' }}>
                <div style={{ fontSize: theme.typography.fontSize.sm, fontWeight: theme.typography.fontWeight.medium, color: theme.colors.textSecondary, marginBottom: theme.spacing.sm }}>
                  Selected Stops {selectedStops.length < 2 && <RequiredIndicator>*Min 2</RequiredIndicator>}
                </div>

                {selectedStops.length > 0 ? (
                  <SelectedStops>
                    {selectedStops.map((stop, index) => (
                      <SelectedStopItem key={stop.id}>
                        <DragHandle>
                          <FiMove size={14} />
                        </DragHandle>
                        <StopOrder>{index + 1}</StopOrder>
                        <StopInfo>
                          <StopName>{stop.name}</StopName>
                          <StopDetails>{stop.code}</StopDetails>
                        </StopInfo>
                        <RemoveButton onClick={() => handleRemoveStop(stop.id)}>
                          <FiX size={14} />
                        </RemoveButton>
                      </SelectedStopItem>
                    ))}
                  </SelectedStops>
                ) : (
                  <EmptyState>
                    <div style={{ fontSize: '20px', marginBottom: '8px' }}></div>
                    <div style={{ fontSize: '12px', fontWeight: '500' }}>No stops selected</div>
                    <div style={{ fontSize: '10px', marginTop: '4px', color: theme.colors.textMuted }}>
                      Click stops on map or in list
                    </div>
                  </EmptyState>
                )}
              </div>
            </PanelContent>
          </FloatingPanel>

          {/* Route Summary */}
          <RouteSummary>
            <SummaryItem>
              <SummaryLabel>Stops:</SummaryLabel>
              <SummaryValue>{selectedStops.length}</SummaryValue>
            </SummaryItem>
            {selectedStops.length >= 2 && (
              <>
                <SummaryItem>
                  <SummaryLabel>Start:</SummaryLabel>
                  <SummaryValue>{selectedStops[0]?.name}</SummaryValue>
                </SummaryItem>
                <SummaryItem>
                  <SummaryLabel>End:</SummaryLabel>
                  <SummaryValue>{selectedStops[selectedStops.length - 1]?.name}</SummaryValue>
                </SummaryItem>
              </>
            )}
          </RouteSummary>
        </MapContainer>

        {/* Action Buttons */}
        <form onSubmit={handleSubmit}>
          <ActionButtons>
            <Button 
              type="button" 
              variant="secondary" 
              onClick={handleCancel}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              variant="primary"
              disabled={isSubmitting || selectedStops.length < 2}
            >
              <FiSave />
              {isSubmitting ? 'Creating...' : 'Create Route'}
            </Button>
          </ActionButtons>
        </form>
      </ContentLayout>
    </CreateRouteContainer>
  );
};

export default CreateRoute;
