// Component to fit all available stops into view
const FitAllStopsHandler: React.FC<{ stops: Stop[] }> = ({ stops }) => {
  const map = useMap();

  useEffect(() => {
    if (stops.length === 0) return;

    try {
      // Create bounds from all stops - convert string coords to numbers
      const bounds = L.latLngBounds(
        stops.map(stop => [parseFloat(stop.location.lat.toString()), parseFloat(stop.location.lon.toString())])
      );

      // Fit the map to show all stops
      map.fitBounds(bounds, {
        padding: [50, 50],
        maxZoom: 16
      });
    } catch (error) {
      console.error('Error fitting stops to view:', error);
    }
  }, [map, stops.length]);

  return null;
};

// Component to handle map bounds fitting
const MapBoundsHandler: React.FC<{ selectedStops: Stop[] }> = ({ selectedStops }) => {
  const map = useMap();

  useEffect(() => {
    if (selectedStops.length > 0) {
      // Create bounds from selected stops - convert string coords to numbers
      const bounds = L.latLngBounds(
        selectedStops.map(stop => [parseFloat(stop.location.lat.toString()), parseFloat(stop.location.lon.toString())])
      );

      // Fit the map to show all selected stops with some padding
      if (selectedStops.length === 1) {
        // For single stop, just center on it with a reasonable zoom
        const lat = parseFloat(selectedStops[0].location.lat.toString());
        const lon = parseFloat(selectedStops[0].location.lon.toString());
        map.setView([lat, lon], 14);
      } else {
        // For multiple stops, fit bounds with padding
        map.fitBounds(bounds, { 
          padding: [20, 20],
          maxZoom: 16 
        });
      }
    }
  }, [map, selectedStops]);

  return null;
};

// Component to handle map clicks for manual stop addition
const MapClickHandler: React.FC<{ onMapClick?: (lat: number, lon: number) => void; isManualAddMode?: boolean }> = ({ 
  onMapClick, 
  isManualAddMode = false 
}) => {
  const map = useMap();

  useEffect(() => {
    if (!isManualAddMode || !onMapClick) return;

    const handleClick = (e: L.LeafletMouseEvent) => {
      const { lat, lng } = e.latlng;
      onMapClick(lat, lng);
    };

    map.on('click', handleClick);

    return () => {
      map.off('click', handleClick);
    };
  }, [map, onMapClick, isManualAddMode]);

  return null;
};

interface RouteMapProps {
  stops: Stop[];
  selectedStops: Stop[];
  center?: { lat: number; lng: number };
  zoom?: number;
  showRoute?: boolean;
  height?: string;
  onStopClick?: (stop: Stop) => void;
  onFitRoute?: () => void;
  showAllStops?: boolean;
  onToggleShowAllStops?: () => void;
  searchTerm?: string;
  onSearchChange?: (term: string) => void;
  filteredStops?: Stop[];
  onAddStop?: (stop: Stop) => void;
  onRemoveStop?: (stopId: string) => void;
  onMapClick?: (lat: number, lon: number) => void;
  isManualAddMode?: boolean;
  onToggleManualMode?: () => void;
  tempMarker?: { lat: number; lon: number } | null;
}

const RouteMap: React.FC<RouteMapProps> = ({ 
  stops,
  selectedStops,
  center,
  zoom = 12,
  showRoute = true,
  height,
  onStopClick,
  onFitRoute,
  showAllStops = true,
  onToggleShowAllStops,
  searchTerm = '',
  onSearchChange,
  filteredStops = [],
  onAddStop,
  onRemoveStop,
  onMapClick,
  isManualAddMode = false,
  onToggleManualMode,
  tempMarker
}) => {
  // Default center - Qatar (Doha) if no center provided
  const defaultCenter = center || { lat: 25.2854, lng: 51.5310 };

  // State for route data
  const [routeData, setRouteData] = useState<RouteResponse | null>(null);
  const [isCalculatingRoute, setIsCalculatingRoute] = useState(false);
  const [isSearchDropdownOpen, setIsSearchDropdownOpen] = useState(false);

  // Debug: Log stops and manual mode
  useEffect(() => {
    console.log('RouteMap Debug:', {
      stopsCount: stops.length,
      selectedStopsCount: selectedStops.length,
      isManualAddMode,
      showAllStops,
      defaultCenter,
      stopsArray: stops.slice(0, 3) // First 3 stops
    });
  }, [stops.length, selectedStops.length, isManualAddMode, showAllStops, defaultCenter]);

  // Calculate route when selected stops change
  useEffect(() => {
    // Create an AbortController for this effect
    const abortController = new AbortController();
    let timeoutId: NodeJS.Timeout;

    if (selectedStops.length >= 2 && showRoute) {
      setIsCalculatingRoute(true);

      const waypoints: RouteCoordinate[] = selectedStops.map(stop => ({
        lat: parseFloat(stop.location.lat.toString()),
        lng: parseFloat(stop.location.lon.toString())
      }));

      // Create a stable key for this route calculation
    //   const routeKey = waypoints.map(w => `${w.lat.toFixed(6)},${w.lng.toFixed(6)}`).join('|');

      // Add a longer delay to avoid excessive API calls during hot reload
      timeoutId = setTimeout(async () => {
        // Check if component is still mounted and request not aborted
        if (abortController.signal.aborted) {
          return;
        }

        try {
          const route = await RoutingService.calculateRoute(waypoints, abortController.signal);

          // Check again if component is still mounted
          if (!abortController.signal.aborted) {
            setRouteData(route);
            setIsCalculatingRoute(false);
          }
        } catch (error) {
          // Only handle error if not aborted
          if (!abortController.signal.aborted) {
            console.error('Failed to calculate route:', error);
            setIsCalculatingRoute(false);
            // Fallback to straight line
            setRouteData({
              coordinates: waypoints,
              distance: 0,
              duration: 0
            });
          }
        }
      }, 1000); // Increased to 1 second to reduce hot reload requests

    } else {
      setRouteData(null);
      setIsCalculatingRoute(false);
    }

    // Cleanup function
    return () => {
      abortController.abort();
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
    };
  }, [selectedStops, showRoute]);

  // Create route line coordinates for Leaflet Polyline
  const routeCoordinates: [number, number][] = routeData 
    ? routeData.coordinates.map(coord => [coord.lat, coord.lng])
    : selectedStops.map(stop => [stop.location.lat, stop.location.lon]);

  // Search handlers
  const handleSearchChange = (value: string) => {
    onSearchChange?.(value);
    const shouldOpen = value.length > 0;
    console.log('Search change:', value, 'Should open:', shouldOpen, 'Filtered stops:', filteredStops.length);
    setIsSearchDropdownOpen(shouldOpen);
  };

  const handleSearchFocus = () => {
    console.log('Search focus, searchTerm:', searchTerm, 'length:', searchTerm.length);
    if (searchTerm.length > 0) {
      setIsSearchDropdownOpen(true);
    }
  };

  const handleSearchBlur = () => {
    // Delay closing to allow for click on dropdown item
    setTimeout(() => {
      setIsSearchDropdownOpen(false);
    }, 200);
  };

  const handleDropdownItemClick = (stop: Stop) => {
    onAddStop?.(stop);
    onSearchChange?.('');
    setIsSearchDropdownOpen(false);
  };

  return (
    <MapWrapper>
      {onStopClick && (
        <MapInfo>
          <div style={{ fontSize: '12px', opacity: 0.8 }}>
            ðŸ“ Click stops to add to route
          </div>
          {isCalculatingRoute && (
            <div style={{ marginTop: '4px', fontSize: '12px', opacity: 0.8, color: '#3b82f6' }}>
              ðŸ›£ï¸ Calculating road route...
            </div>
          )}
          {selectedStops.length === 1 && (
            <div style={{ marginTop: '4px', fontSize: '12px', opacity: 0.8 }}>
              Add another stop to see route
            </div>
          )}
        </MapInfo>
      )}


      {/* Map Sidebar */}
      <MapSidebar>
        <SidebarHeader>
          <SidebarTitle>
            ðŸ—ºï¸ Route Builder
          </SidebarTitle>
        </SidebarHeader>

        <SidebarContent>
          {/* Controls Section */}
          <ControlSection>
            <ControlGroup>
              {onToggleShowAllStops && (
                <ToggleButton
                  active={showAllStops}
                  onClick={onToggleShowAllStops}
                >
                  ðŸ“ {showAllStops ? 'Hide All Stops' : 'Show All Stops'}
                </ToggleButton>
              )}

              {onToggleManualMode && (
                <ToggleButton
                  active={isManualAddMode}
                  onClick={onToggleManualMode}
                >
                  ðŸ“Œ {isManualAddMode ? 'Stop Placement Mode' : 'Click to Add Stops'}
                </ToggleButton>
              )}

              {selectedStops.length >= 2 && onFitRoute && (
                <ActionButton onClick={onFitRoute}>
                  ðŸŽ¯ Fit Route to View
                </ActionButton>
              )}
            </ControlGroup>
          </ControlSection>

          {/* Search Section */}
          <SearchSection>
            <SearchContainer>
              <SearchInput
                type="text"
                placeholder="Search stops to add..."
                value={searchTerm}
                onChange={(e) => handleSearchChange(e.target.value)}
                onFocus={handleSearchFocus}
                onBlur={handleSearchBlur}
              />

              {/* Search Dropdown */}
              <SearchDropdown isOpen={isSearchDropdownOpen}>
                {filteredStops.length > 0 ? (
                  filteredStops.map((stop) => {
                    const isSelected = selectedStops.some(s => s.id === stop.id);
                    return (
                      <DropdownItem
                        key={stop.id}
                        selected={isSelected}
                        onClick={() => !isSelected && handleDropdownItemClick(stop)}
                      >
                        <DropdownItemInfo>
                          <DropdownItemName>{stop.name}</DropdownItemName>
                          <DropdownItemCode>{stop.code}</DropdownItemCode>
                        </DropdownItemInfo>
                        {!isSelected && (
                          <div style={{ color: theme.colors.primary, fontSize: '16px' }}>+</div>
                        )}
                      </DropdownItem>
                    );
                  })
                ) : (
                  <NoResults>
                    No stops found for "{searchTerm}"
                  </NoResults>
                )}
              </SearchDropdown>
            </SearchContainer>
          </SearchSection>

          {/* Selected Stops Section */}
          <SelectedStopsSection>
            <SelectedStopsHeader>
              Selected Stops ({selectedStops.length})
            </SelectedStopsHeader>

            <SelectedStopsList>
              {selectedStops.length > 0 ? (
                selectedStops.map((stop, index) => (
                  <SelectedStopItem key={stop.id}>
                    <StopOrder>{index + 1}</StopOrder>
                    <StopInfo>
                      <StopName>{stop.name}</StopName>
                      <StopCode>{stop.code}</StopCode>
                    </StopInfo>
                    <RemoveButton onClick={() => onRemoveStop?.(stop.id)}>
                      Ã—
                    </RemoveButton>
                  </SelectedStopItem>
                ))
              ) : (
                <EmptyState>
                  <div style={{ fontSize: '20px', marginBottom: '8px' }}>ðŸ“</div>
                  <div style={{ fontSize: '12px', fontWeight: '500' }}>No stops selected</div>
                  <div style={{ fontSize: '10px', marginTop: '4px', color: theme.colors.textMuted }}>
                    Search and add stops to build your route
                  </div>
                </EmptyState>
              )}
            </SelectedStopsList>
          </SelectedStopsSection>
        </SidebarContent>
      </MapSidebar>

      <MapContainer
        center={[defaultCenter.lat, defaultCenter.lng]}
        zoom={zoom}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Handle automatic bounds fitting when stops change */}
        <MapBoundsHandler selectedStops={selectedStops} />
        {/* Handle fitting all stops into view when they load */}
        <FitAllStopsHandler stops={stops} />

        {/* Handle map clicks for manual stop addition */}
        <MapClickHandler onMapClick={onMapClick} isManualAddMode={isManualAddMode} />

        {/* Show all available stops */}
        {(() => {
          console.log('ðŸ—ºï¸ MAP RENDERING:', { stopsLength: stops.length, showAllStops });
          const markers = stops.map((stop) => {
            const isSelected = selectedStops.some(s => s.id === stop.id);
            const selectedIndex = selectedStops.findIndex(s => s.id === stop.id);

            // Only show non-selected stops if showAllStops is true
            if (!isSelected && !showAllStops) {
              console.log('â­ï¸ SKIPPING STOP:', stop.name, '(not selected & showAllStops is false)');
              return null;
            }

            const lat = parseFloat(stop.location.lat.toString());
            const lon = parseFloat(stop.location.lon.toString());

            console.log('ðŸ“ MARKER RENDER:', {
              name: stop.name,
              lat,
              lon,
              isSelected
            });

            return (
              <Marker
                key={stop.id}
                position={[lat, lon]}
                icon={isSelected ? selectedIcon : defaultIcon}
                eventHandlers={{
                  click: () => {
                    if (onStopClick) {
                      onStopClick(stop);
                    }
                  }
                }}
              >
                <Popup>
                  <PopupContent>
                    <PopupTitle>{stop.name}</PopupTitle>
                    <PopupDetail><strong>Code:</strong> {stop.code}</PopupDetail>
                    {isSelected && (
                      <PopupDetail><strong>Order:</strong> #{selectedIndex + 1} in route</PopupDetail>
                    )}
                    {onStopClick && !isSelected && (
                      <PopupDetail style={{ color: theme.colors.primary, fontWeight: 'bold', marginTop: '8px' }}>
                        Click to add to route
                      </PopupDetail>
                    )}
                  </PopupContent>
                </Popup>
              </Marker>
            );
          });
          console.log('ðŸŽ¨ TOTAL MARKERS RENDERED:', markers.filter(m => m !== null).length);
          return markers;
        })()}

        {/* Show temporary marker when adding stop manually */}
        {(() => {
          console.log('ðŸ”´ TEMP MARKER CHECK:', {
            tempMarker,
            isManualAddMode,
            shouldRender: tempMarker && isManualAddMode
          });

          if (tempMarker && isManualAddMode) {
            const lat = parseFloat(tempMarker.lat.toString());
            const lon = parseFloat(tempMarker.lon.toString());

            console.log('ðŸ”´ RENDERING TEMP MARKER at:', { lat, lon });

            return (
              <Marker
                position={[lat, lon]}
                icon={tempIcon}
              >
                <Popup>
                  <PopupContent>
                    <PopupTitle>New Stop Location</PopupTitle>
                    <PopupDetail><strong>Lat:</strong> {lat.toFixed(6)}</PopupDetail>
                    <PopupDetail><strong>Lon:</strong> {lon.toFixed(6)}</PopupDetail>
                  </PopupContent>
                </Popup>
              </Marker>
            );
          }
          return null;
        })()}

        {/* Show route line */}
        {showRoute && selectedStops.length > 1 && routeCoordinates.length > 0 && (
          <Polyline
            positions={routeCoordinates}
            color={routeData ? theme.colors.primary : theme.colors.secondary}
            weight={routeData ? 5 : 4}
            opacity={routeData ? 0.9 : 0.6}
            dashArray={routeData ? undefined : "10, 5"}
          />
        )}
      </MapContainer>
    </MapWrapper>
  );
};

export default RouteMap;
